import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Chip,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Flight as FlightIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS, getAuthHeader } from '../config';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentFlight, setCurrentFlight] = useState({
    flightCode: '',
    route: '',
    departureTime: '',
    arrivalTime: '',
    priceByClass: {
      economy: '',
      business: '',
      firstClass: '',
    },
    totalSeats: '',
    availableSeats: '',
  });

  // Lấy danh sách chuyến bay
  const fetchFlights = async () => {
    try {
      setLoading(true);
      const response = await axios.get(API_ENDPOINTS.FLIGHTS.LIST, getAuthHeader());
      // Backend trả về { flights: [], pagination: {} }
      setFlights(response.data.data?.flights || response.data.data || []);
      setError('');
    } catch (err) {
      setError('Không thể tải danh sách chuyến bay');
      console.error('Error fetching flights:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  // Mở dialog thêm mới
  const handleOpenAdd = () => {
    setEditMode(false);
    setCurrentFlight({
      flightCode: '',
      route: '',
      departureTime: '',
      arrivalTime: '',
      priceByClass: {
        economy: '',
        business: '',
        firstClass: '',
      },
      totalSeats: '',
      availableSeats: '',
    });
    setOpenDialog(true);
  };

  // Mở dialog chỉnh sửa
  const handleOpenEdit = (flight) => {
    setEditMode(true);
    setCurrentFlight({
      ...flight,
      departureTime: new Date(flight.departureTime).toISOString().slice(0, 16),
      arrivalTime: new Date(flight.arrivalTime).toISOString().slice(0, 16),
    });
    setOpenDialog(true);
  };

  // Đóng dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setError('');
    setSuccess('');
  };

  // Xử lý thay đổi input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentFlight((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Lưu chuyến bay (thêm hoặc sửa)
  const handleSave = async () => {
    try {
      if (editMode) {
        // Cập nhật
        await axios.put(
          API_ENDPOINTS.FLIGHTS.DETAIL(currentFlight._id),
          currentFlight,
          getAuthHeader()
        );
        setSuccess('Cập nhật chuyến bay thành công!');
      } else {
        // Thêm mới
        await axios.post(
          API_ENDPOINTS.FLIGHTS.LIST,
          currentFlight,
          getAuthHeader()
        );
        setSuccess('Thêm chuyến bay thành công!');
      }

      fetchFlights();
      setTimeout(() => {
        handleCloseDialog();
        setSuccess('');
      }, 1500);
    } catch (err) {
      setError(err.response?.data?.message || 'Có lỗi xảy ra');
      console.error('Error saving flight:', err);
    }
  };

  // Xóa chuyến bay
  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa chuyến bay này?')) {
      try {
        await axios.delete(
          API_ENDPOINTS.FLIGHTS.DETAIL(id),
          getAuthHeader()
        );
        setSuccess('Xóa chuyến bay thành công!');
        fetchFlights();
        setTimeout(() => setSuccess(''), 3000);
      } catch (err) {
        setError(err.response?.data?.message || 'Không thể xóa chuyến bay');
        console.error('Error deleting flight:', err);
      }
    }
  };

  // Format giá tiền
  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
    }).format(price);
  };

  // Format ngày giờ
  const formatDateTime = (dateTime) => {
    return new Date(dateTime).toLocaleString('vi-VN');
  };

  // Màu status
  const getStatusColor = (status) => {
    const colors = {
      scheduled: 'info',
      boarding: 'warning',
      departed: 'success',
      arrived: 'default',
      cancelled: 'error',
      delayed: 'warning',
    };
    return colors[status] || 'default';
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          <FlightIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
          Quản lý chuyến bay
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleOpenAdd}
        >
          Thêm chuyến bay
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Mã chuyến bay</strong></TableCell>
              <TableCell><strong>Hãng bay</strong></TableCell>
              <TableCell><strong>Điểm đi</strong></TableCell>
              <TableCell><strong>Điểm đến</strong></TableCell>
              <TableCell><strong>Khởi hành</strong></TableCell>
              <TableCell><strong>Đến</strong></TableCell>
              <TableCell><strong>Giá</strong></TableCell>
              <TableCell><strong>Ghế trống</strong></TableCell>
              <TableCell><strong>Trạng thái</strong></TableCell>
              <TableCell align="center"><strong>Thao tác</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {flights.length === 0 ? (
              <TableRow>
                <TableCell colSpan={10} align="center">
                  Chưa có chuyến bay nào
                </TableCell>
              </TableRow>
            ) : (
              flights.map((flight) => (
                <TableRow key={flight._id} hover>
                  <TableCell>{flight.flightNumber}</TableCell>
                  <TableCell>{flight.airline}</TableCell>
                  <TableCell>{flight.origin}</TableCell>
                  <TableCell>{flight.destination}</TableCell>
                  <TableCell>{formatDateTime(flight.departureTime)}</TableCell>
                  <TableCell>{formatDateTime(flight.arrivalTime)}</TableCell>
                  <TableCell>{formatPrice(flight.price)}</TableCell>
                  <TableCell>{flight.availableSeats}</TableCell>
                  <TableCell>
                    <Chip
                      label={flight.status}
                      color={getStatusColor(flight.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell align="center">
                    <IconButton
                      color="primary"
                      size="small"
                      onClick={() => handleOpenEdit(flight)}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      color="error"
                      size="small"
                      onClick={() => handleDelete(flight._id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog thêm/sửa */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          {editMode ? 'Chỉnh sửa chuyến bay' : 'Thêm chuyến bay mới'}
        </DialogTitle>
        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          )}
          <Box sx={{ display: 'grid', gap: 2, mt: 2 }}>
            <TextField
              label="Mã chuyến bay"
              name="flightNumber"
              value={currentFlight.flightNumber}
              onChange={handleInputChange}
              fullWidth
              required
            />
            <TextField
              label="Hãng bay"
              name="airline"
              value={currentFlight.airline}
              onChange={handleInputChange}
              fullWidth
              required
            />
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Điểm đi"
                name="origin"
                value={currentFlight.origin}
                onChange={handleInputChange}
                required
              />
              <TextField
                label="Điểm đến"
                name="destination"
                value={currentFlight.destination}
                onChange={handleInputChange}
                required
              />
            </Box>
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Thời gian khởi hành"
                name="departureTime"
                type="datetime-local"
                value={currentFlight.departureTime}
                onChange={handleInputChange}
                InputLabelProps={{ shrink: true }}
                required
              />
              <TextField
                label="Thời gian đến"
                name="arrivalTime"
                type="datetime-local"
                value={currentFlight.arrivalTime}
                onChange={handleInputChange}
                InputLabelProps={{ shrink: true }}
                required
              />
            </Box>
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Giá vé (VNĐ)"
                name="price"
                type="number"
                value={currentFlight.price}
                onChange={handleInputChange}
                required
              />
              <TextField
                label="Số ghế trống"
                name="availableSeats"
                type="number"
                value={currentFlight.availableSeats}
                onChange={handleInputChange}
                required
              />
            </Box>
            <TextField
              label="Trạng thái"
              name="status"
              select
              value={currentFlight.status}
              onChange={handleInputChange}
              SelectProps={{ native: true }}
              required
            >
              <option value="scheduled">Scheduled</option>
              <option value="boarding">Boarding</option>
              <option value="departed">Departed</option>
              <option value="arrived">Arrived</option>
              <option value="cancelled">Cancelled</option>
              <option value="delayed">Delayed</option>
            </TextField>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Hủy</Button>
          <Button onClick={handleSave} variant="contained">
            {editMode ? 'Cập nhật' : 'Thêm mới'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Flights;
