import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Chip,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Flight as FlightIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS, getAuthHeader } from '../config';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentFlight, setCurrentFlight] = useState({
    flightCode: '',
    route: '',
    departureTime: '',
    arrivalTime: '',
    totalSeats: '',
    firstClassPrice: '',
    businessPrice: '',
    economyPrice: '',
  });

  // Láº¥y danh sÃ¡ch chuyáº¿n bay
  const fetchFlights = async () => {
    try {
      setLoading(true);
      const response = await axios.get(API_ENDPOINTS.FLIGHTS.LIST, getAuthHeader());
      // Backend tráº£ vá» { flights: [], pagination: {} }
      setFlights(response.data.data?.flights || response.data.data || []);
      setError('');
    } catch (err) {
      console.error('Error fetching flights:', err);
      console.error('Error response:', err.response?.data);
      console.error('Error status:', err.response?.status);

      if (err.response) {
        const status = err.response.status;
        const data = err.response.data;

        if (status === 500) {
          setError(data?.message || 'Lá»—i server ná»™i bá»™ khi táº£i danh sÃ¡ch');
        } else if (status === 401) {
          setError('KhÃ´ng cÃ³ quyá»n truy cáº­p');
        } else {
          setError(`Lá»—i server (${status}): ${data?.message || 'KhÃ´ng xÃ¡c Ä‘á»‹nh'}`);
        }
      } else if (err.request) {
        setError('KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n server');
      } else {
        setError('CÃ³ lá»—i xáº£y ra khi táº£i danh sÃ¡ch chuyáº¿n bay');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  const handleOpenAdd = () => {
    setEditMode(false);
    setCurrentFlight({
      flightCode: '',
      route: '',
      departureTime: '',
      arrivalTime: '',
      totalSeats: '',
      firstClassPrice: '',
      businessPrice: '',
      economyPrice: '',
      // CÃ¡c trÆ°á»ng hiá»ƒn thá»‹ thá»i gian (ban Ä‘áº§u rá»—ng)
      departureTimeDisplay: '',
      arrivalTimeDisplay: '',
    });
    setOpenDialog(true);
  };

  // Má»Ÿ dialog chá»‰nh sá»­a
  const handleOpenEdit = (flight) => {
    setEditMode(true);
    setCurrentFlight({
      ...flight,
      departureTime: new Date(flight.departureTime).toISOString().slice(0, 16),
      arrivalTime: new Date(flight.arrivalTime).toISOString().slice(0, 16),
      firstClassPrice: flight.firstClassPrice || '',
      businessPrice: flight.businessPrice || '',
      economyPrice: flight.economyPrice || '',
      delayReason: flight.delayReason || '', // Chá»‰ cáº§n khi chá»‰nh sá»­a
      // ThÃªm thÃ´ng tin hiá»ƒn thá»‹ dá»… Ä‘á»c
      departureTimeDisplay: new Date(flight.departureTime).toLocaleString('vi-VN'),
      arrivalTimeDisplay: new Date(flight.arrivalTime).toLocaleString('vi-VN'),
    });
    setOpenDialog(true);
  };

  // ÄÃ³ng dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setError('');
    setSuccess('');
    // Reset state currentFlight khi Ä‘Ã³ng dialog
    setCurrentFlight({
      flightCode: '',
      route: '',
      departureTime: '',
      arrivalTime: '',
      totalSeats: '',
      firstClassPrice: '',
      businessPrice: '',
      economyPrice: '',
      delayReason: '', // Reset lÃ½ do delay
      // Reset cÃ¡c trÆ°á»ng hiá»ƒn thá»‹ thá»i gian
      departureTimeDisplay: '',
      arrivalTimeDisplay: '',
    });
  };

  // Xá»­ lÃ½ thay Ä‘á»•i input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentFlight((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // LÆ°u chuyáº¿n bay (thÃªm hoáº·c sá»­a)
  const handleSave = async () => {
    try {
      // Validate cÃ¡c trÆ°á»ng báº¯t buá»™c khÃ´ng Ä‘Æ°á»£c Ä‘á»ƒ trá»‘ng
      if (!currentFlight.flightCode || !currentFlight.route ||
          (!editMode && !currentFlight.totalSeats) || !currentFlight.firstClassPrice ||
          !currentFlight.businessPrice || !currentFlight.economyPrice) {
        setError('Vui lÃ²ng Ä‘iá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin báº¯t buá»™c');
        return;
      }

      // Validate thá»i gian náº¿u cÃ³ nháº­p (cho cáº£ thÃªm má»›i vÃ  chá»‰nh sá»­a)
      if ((currentFlight.departureTime || currentFlight.arrivalTime) &&
          currentFlight.departureTime && currentFlight.arrivalTime) {
        const departureTime = new Date(currentFlight.departureTime);
        const arrivalTime = new Date(currentFlight.arrivalTime);

        if (departureTime >= arrivalTime) {
          setError('Thá»i gian Ä‘áº¿n pháº£i sau thá»i gian khá»Ÿi hÃ nh');
          return;
        }
      }

      // Náº¿u Ä‘ang thÃªm má»›i vÃ  chÆ°a nháº­p thá»i gian, bÃ¡o lá»—i
      if (!editMode && (!currentFlight.departureTime || !currentFlight.arrivalTime)) {
        setError('Vui lÃ²ng nháº­p thá»i gian khá»Ÿi hÃ nh vÃ  Ä‘áº¿n cho chuyáº¿n bay má»›i');
        return;
      }

      // Validate sá»‘ gháº¿ pháº£i lÃ  sá»‘ dÆ°Æ¡ng (chá»‰ khi thÃªm má»›i)
      if (!editMode && (isNaN(currentFlight.totalSeats) || parseInt(currentFlight.totalSeats) <= 0)) {
        setError('Sá»‘ gháº¿ pháº£i lÃ  sá»‘ dÆ°Æ¡ng');
        return;
      }

      // Validate giÃ¡ vÃ© pháº£i lÃ  sá»‘ khÃ´ng Ã¢m
      if (currentFlight.firstClassPrice !== '' && (isNaN(currentFlight.firstClassPrice) || parseFloat(currentFlight.firstClassPrice) < 0)) {
        setError('GiÃ¡ háº¡ng nháº¥t pháº£i lÃ  sá»‘ khÃ´ng Ã¢m');
        return;
      }

      if (currentFlight.businessPrice !== '' && (isNaN(currentFlight.businessPrice) || parseFloat(currentFlight.businessPrice) < 0)) {
        setError('GiÃ¡ thÆ°Æ¡ng gia pháº£i lÃ  sá»‘ khÃ´ng Ã¢m');
        return;
      }

      if (currentFlight.economyPrice !== '' && (isNaN(currentFlight.economyPrice) || parseFloat(currentFlight.economyPrice) < 0)) {
        setError('GiÃ¡ phá»• thÃ´ng pháº£i lÃ  sá»‘ khÃ´ng Ã¢m');
        return;
      }

      // Validate Ä‘á»‹nh dáº¡ng mÃ£ chuyáº¿n bay (vÃ­ dá»¥: VN123, VJ456, etc.)
      const flightCodePattern = /^[A-Z]{2}\d{3,}$/;
      if (!flightCodePattern.test(currentFlight.flightCode.trim())) {
        setError('MÃ£ chuyáº¿n bay pháº£i cÃ³ Ä‘á»‹nh dáº¡ng: 2 chá»¯ cÃ¡i + sá»‘ (VD: VN123, VJ456)');
        return;
      }

      // Chuáº©n bá»‹ dá»¯ liá»‡u
      const flightData = {
        flightCode: currentFlight.flightCode.trim(),
        route: currentFlight.route.trim(),
        firstClassPrice: parseFloat(currentFlight.firstClassPrice) || 0,
        businessPrice: parseFloat(currentFlight.businessPrice) || 0,
        economyPrice: parseFloat(currentFlight.economyPrice) || 0,
      };

      // Chá»‰ thÃªm lÃ½ do delay khi Ä‘ang chá»‰nh sá»­a vÃ  cÃ³ nháº­p lÃ½ do
      if (editMode && currentFlight.delayReason && currentFlight.delayReason.trim()) {
        flightData.delayReason = currentFlight.delayReason.trim();
      }

      // Chá»‰ thÃªm thÃ´ng tin gháº¿ khi thÃªm má»›i
      if (!editMode) {
        flightData.totalSeats = Number(currentFlight.totalSeats);
        flightData.availableSeats = Number(currentFlight.totalSeats);
      }

      // Chá»‰ thÃªm thá»i gian náº¿u ngÆ°á»i dÃ¹ng Ä‘Ã£ nháº­p
      if (currentFlight.departureTime) {
