import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Chip,
  CircularProgress,
  MenuItem,
} from '@mui/material';
import {
  Edit as EditIcon,
  Delete as DeleteIcon,
  ConfirmationNumber as TicketIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS } from '../config';

const TicketsManage = () => {
  const [tickets, setTickets] = useState([]);
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentTicket, setCurrentTicket] = useState({
    flightCode: '',
    passengerName: '',
    email: '',
    phoneNumber: '',
    seatNumber: '',
    price: '',
    status: 'booked',
  });

  // Lấy danh sách vé
  const fetchTickets = async () => {
    try {
      setLoading(true);
      const response = await axios.get(API_ENDPOINTS.TICKETS.LIST);
      console.log('Tickets response:', response.data);
      setTickets(response.data.data?.tickets || response.data.data || []);
      setError('');
    } catch (err) {
      setError('Không thể tải danh sách vé');
      console.error('Error fetching tickets:', err);
    } finally {
      setLoading(false);
    }
  };

  // Lấy danh sách chuyến bay
  const fetchFlights = async () => {
    try {
      const response = await axios.get(API_ENDPOINTS.FLIGHTS.LIST);
      const flightsList = response.data.data?.flights || response.data.data || [];
      setFlights(flightsList);
    } catch (err) {
      console.error('Error fetching flights:', err);
    }
  };

  useEffect(() => {
    fetchTickets();
    fetchFlights();
  }, []);

  // Mở dialog chỉnh sửa
  const handleOpenEdit = (ticket) => {
    setEditMode(true);
    // Lấy flightCode từ flight
    const flight = flights.find(f => f._id === ticket.flightId);
    setCurrentTicket({
      ...ticket,
      flightCode: flight?.flightCode || '',
    });
    setOpenDialog(true);
  };

  // Đóng dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setError('');
    setSuccess('');
  };

  // Xử lý thay đổi input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentTicket((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Lưu vé (chỉ sửa)
  const handleSave = async () => {
    try {
      // Validate dữ liệu
      if (!currentTicket.flightCode || !currentTicket.passengerName ||
          !currentTicket.email || !currentTicket.phoneNumber ||
          !currentTicket.seatNumber || !currentTicket.price) {
        setError('Vui lòng điền đầy đủ thông tin bắt buộc');
        return;
      }

      // Chuẩn bị dữ liệu
      const ticketData = {
        flightCode: currentTicket.flightCode,
        passengerName: currentTicket.passengerName.trim(),
        email: currentTicket.email.trim(),
        phoneNumber: currentTicket.phoneNumber.trim(),
        seatNumber: currentTicket.seatNumber.trim(),
        price: Number(currentTicket.price),
        status: currentTicket.status,
      };

      console.log('Sending ticket data:', ticketData);

      // Chỉ cập nhật (không có thêm mới)
      await axios.put(
        API_ENDPOINTS.TICKETS.DETAIL(currentTicket._id),
        ticketData
      );
      setSuccess('Cập nhật vé thành công!');

      fetchTickets();
      fetchFlights(); // Refresh để cập nhật số chỗ
      setTimeout(() => {
        handleCloseDialog();
        setSuccess('');
      }, 1500);
    } catch (err) {
      const errorMsg = err.response?.data?.message || err.message || 'Có lỗi xảy ra';
      setError(errorMsg);
      console.error('Error saving ticket:', err.response?.data || err);
    }
  };

  // Xóa vé
  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa vé này? Số chỗ sẽ được hoàn trả.')) {
      try {
        await axios.delete(
          API_ENDPOINTS.TICKETS.DETAIL(id)
        );
        setSuccess('Xóa vé thành công!');
        fetchTickets();
        fetchFlights(); // Refresh để cập nhật số chỗ
        setTimeout(() => setSuccess(''), 3000);
      } catch (err) {
        setError(err.response?.data?.message || 'Không thể xóa vé');
        console.error('Error deleting ticket:', err);
      }
    }
  };

  // Format giá tiền
  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
    }).format(price);
  };

  // Format ngày giờ
  const formatDateTime = (dateTime) => {
    return new Date(dateTime).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Màu trạng thái thanh toán
  const getPaymentStatusColor = (paymentStatus) => {
    const colors = {
      pending: 'warning',
      paid: 'success',
      refunded: 'info',
      failed: 'error',
    };
    return colors[paymentStatus] || 'default';
  };

  // Xác định loại ghế từ giá vé
  const getSeatClassFromPrice = (price, flight) => {
    if (!flight || !price) return 'Không xác định';

    const tolerance = 1000; // Dung sai để so sánh giá

    if (Math.abs(price - flight.economyPrice) <= tolerance) {
      return 'Economy';
    } else if (Math.abs(price - flight.businessPrice) <= tolerance) {
      return 'Business';
    } else if (Math.abs(price - flight.firstClassPrice) <= tolerance) {
      return 'First Class';
    }

    return 'Không xác định';
  };

  // Lấy màu cho loại ghế
  const getSeatClassColor = (seatClass) => {
    const colors = {
      'Economy': 'success',
      'Business': 'warning',
      'First Class': 'error',
    };
    return colors[seatClass] || 'default';
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="flex-start" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          <TicketIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
          Quản lý vé
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Mã vé</strong></TableCell>
              <TableCell><strong>Hành khách</strong></TableCell>
              <TableCell><strong>Email</strong></TableCell>
              <TableCell><strong>SĐT</strong></TableCell>
              <TableCell><strong>Số ghế</strong></TableCell>
              <TableCell><strong>Loại ghế</strong></TableCell>
              <TableCell><strong>Thông tin ghế</strong></TableCell>
              <TableCell><strong>Giá</strong></TableCell>
              <TableCell><strong>Ngày đặt</strong></TableCell>
              <TableCell><strong>Thanh toán</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tickets.length === 0 ? (
              <TableRow>
                <TableCell colSpan={11} align="center">
                  Chưa có vé nào
                </TableCell>
              </TableRow>
            ) : (
              tickets.map((ticket) => (
                <TableRow key={ticket._id} hover>
                  <TableCell>{ticket.ticketCode}</TableCell>
                  <TableCell>{ticket.passengerName}</TableCell>
                  <TableCell>{ticket.email}</TableCell>
                  <TableCell>{ticket.phoneNumber}</TableCell>
                  <TableCell>
                    <Typography variant="body2">{ticket.seatNumber}</Typography>
                    {(() => {
                      const seatClass = getSeatClassFromPrice(ticket.price, ticket.flightId);
                      return seatClass !== 'Không xác định' ? (
                        <Chip
                          label={seatClass}
                          color={getSeatClassColor(seatClass)}
                          size="small"
                          variant="outlined"
                          sx={{ mt: 0.5 }}
                        />
                      ) : null;
                    })()}
                    {ticket.flightId?.totalSeats && ticket.flightId?.availableSeats !== undefined && (
                      <Typography variant="caption" color="textSecondary" display="block">
                        ({ticket.flightId.totalSeats - ticket.flightId.availableSeats}/{ticket.flightId.totalSeats} ghế đã đặt)
                      </Typography>
                    )}
                  </TableCell>
                  <TableCell>
                    {ticket.flightId?.totalSeats && ticket.flightId?.availableSeats !== undefined ? (
                      <Typography variant="body2">
                        Đã đặt: {ticket.flightId.totalSeats - ticket.flightId.availableSeats}/{ticket.flightId.totalSeats}
                      </Typography>
                    ) : (
                      <Typography variant="body2" color="textSecondary">
                        Không có thông tin
                      </Typography>
                    )}
                  </TableCell>
                  <TableCell>{formatPrice(ticket.price)}</TableCell>
                  <TableCell>{formatDateTime(ticket.bookingDate)}</TableCell>
                  <TableCell>
                    <Chip
                      label={getPaymentStatusLabel(ticket.paymentStatus)}
                      color={getPaymentStatusColor(ticket.paymentStatus)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell align="center">
                    <IconButton
                      color="primary"
                      size="small"
                      onClick={() => handleOpenEdit(ticket)}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      color="error"
                      size="small"
                      onClick={() => handleDelete(ticket._id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog chỉnh sửa */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          Chỉnh sửa vé
        </DialogTitle>
        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          )}
          <Box sx={{ display: 'grid', gap: 2, mt: 2 }}>
            <TextField
              label="Chuyến bay"
              name="flightCode"
              select
              value={currentTicket.flightCode}
              onChange={handleInputChange}
              fullWidth
              required
              disabled={editMode}
              helperText={editMode ? "Không thể đổi chuyến bay khi sửa vé" : "Chọn chuyến bay"}
            >
              {flights.map((flight) => (
                <MenuItem key={flight._id} value={flight.flightCode}>
                  {flight.flightCode} - {flight.route} (Đã đặt: {flight.totalSeats - flight.availableSeats}/{flight.totalSeats})
                </MenuItem>
              ))}
            </TextField>
            <TextField
              label="Tên hành khách"
              name="passengerName"
              value={currentTicket.passengerName}
              onChange={handleInputChange}
              fullWidth
              required
            />
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Email"
                name="email"
                type="email"
                value={currentTicket.email}
                onChange={handleInputChange}
                required
              />
              <TextField
                label="Số điện thoại"
                name="phoneNumber"
                value={currentTicket.phoneNumber}
                onChange={handleInputChange}
                required
              />
            </Box>
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Số ghế"
                name="seatNumber"
                value={currentTicket.seatNumber}
                onChange={handleInputChange}
                required
                placeholder="VD: 15A"
              />
              <TextField
                label="Giá vé (VNĐ)"
                name="price"
                type="number"
                value={currentTicket.price}
                onChange={handleInputChange}
                required
              />
            </Box>
            <TextField
              label="Trạng thái"
              name="status"
              select
              value={currentTicket.status}
              onChange={handleInputChange}
              required
            >
              <MenuItem value="booked">Đã đặt</MenuItem>
              <MenuItem value="checked-in">Đã check-in</MenuItem>
              <MenuItem value="cancelled">Đã hủy</MenuItem>
            </TextField>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Hủy</Button>
          <Button onClick={handleSave} variant="contained">
            Cập nhật
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TicketsManage;
