import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Chip,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Flight as FlightIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS, getAuthHeader } from '../config';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentFlight, setCurrentFlight] = useState({
    flightNumber: '',
    route: '',
    departureTime: '',
    arrivalTime: '',
    totalSeats: '',
    firstClassPrice: '',
    businessPrice: '',
    economyPrice: '',
  });

  // Lấy danh sách chuyến bay
  const fetchFlights = async () => {
    try {
      setLoading(true);
      setError('');

      console.log('Fetching flights from:', API_ENDPOINTS.FLIGHTS.LIST);

      // Thêm timeout để tránh request bị treo
      const response = await Promise.race([
        axios.get(API_ENDPOINTS.FLIGHTS.LIST, getAuthHeader()),
        new Promise((_, reject) =>
          setTimeout(() => reject(new Error('Request timeout')), 10000)
        )
      ]);

      console.log('Flights response status:', response.status);
      console.log('Flights response data:', response.data);

      // Backend trả về { statusCode, message, data: { flights: [], pagination: {} } }
      if (response.data && response.data.data) {
        const flightsData = response.data.data.flights || response.data.data || [];
        console.log('Parsed flights data:', flightsData);
        setFlights(flightsData);
      } else {
        console.warn('Unexpected response format:', response.data);
        setFlights([]);
      }
    } catch (err) {
      console.error('Error fetching flights:', err);

      // Kiểm tra loại lỗi
      if (err.message === 'Request timeout') {
        setError('Yêu cầu bị timeout. Vui lòng thử lại.');
      } else if (err.code === 'ECONNREFUSED') {
        setError('Không thể kết nối đến server. Vui lòng kiểm tra backend có chạy không.');
      } else if (err.response) {
        // Server trả về lỗi
        const status = err.response.status;
        const data = err.response.data;

        if (status === 404) {
          setError('API endpoint không tồn tại');
        } else if (status === 401) {
          setError('Không có quyền truy cập');
        } else if (status === 500) {
          setError('Lỗi server nội bộ');
        } else if (data && data.message) {
          setError(data.message);
        } else {
          setError(`Lỗi server: ${status}`);
        }
      } else if (err.request) {
        // Request được gửi nhưng không nhận được response
        setError('Không nhận được phản hồi từ server');
      } else {
        // Lỗi khác
        setError('Có lỗi xảy ra khi tải danh sách chuyến bay');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  // Cleanup effect để tránh memory leaks
  useEffect(() => {
    return () => {
      // Cleanup any pending requests if needed
    };
  }, []);

  // Mở dialog thêm mới
  const handleOpenAdd = () => {
    setEditMode(false);
    setCurrentFlight({
      flightNumber: '',
      route: '',
      departureTime: '',
      arrivalTime: '',
      totalSeats: '',
      firstClassPrice: '',
      businessPrice: '',
      economyPrice: '',
    });
    setOpenDialog(true);
  };

  // Mở dialog chỉnh sửa
  const handleOpenEdit = (flight) => {
    setEditMode(true);
    setCurrentFlight({
      ...flight,
      departureTime: new Date(flight.departureTime).toISOString().slice(0, 16),
      arrivalTime: new Date(flight.arrivalTime).toISOString().slice(0, 16),
      firstClassPrice: flight.firstClassPrice || '',
      businessPrice: flight.businessPrice || '',
      economyPrice: flight.economyPrice || '',
    });
    setOpenDialog(true);
  };

  // Đóng dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setError('');
    setSuccess('');
  };

  // Xử lý thay đổi input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentFlight((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Lưu chuyến bay (thêm hoặc sửa)
  const handleSave = async () => {
    try {
      setError('');
      setSuccess('');

      // Validate dữ liệu
      const errors = [];

      if (!currentFlight.flightNumber || currentFlight.flightNumber.trim() === '') {
        errors.push('Mã chuyến bay');
      }

      if (!currentFlight.route || currentFlight.route.trim() === '') {
        errors.push('Tuyến đường');
      }

      if (!currentFlight.departureTime) {
        errors.push('Thời gian khởi hành');
      }

      if (!currentFlight.arrivalTime) {
        errors.push('Thời gian đến');
      }

      if (!currentFlight.totalSeats || currentFlight.totalSeats === '' || isNaN(currentFlight.totalSeats) || parseInt(currentFlight.totalSeats) <= 0) {
        errors.push('Số ghế tổng cộng (phải là số dương)');
      }

      if (currentFlight.firstClassPrice === '' || currentFlight.firstClassPrice === null || currentFlight.firstClassPrice === undefined || isNaN(currentFlight.firstClassPrice) || parseFloat(currentFlight.firstClassPrice) < 0) {
        errors.push('Giá hạng nhất');
      }

      if (currentFlight.businessPrice === '' || currentFlight.businessPrice === null || currentFlight.businessPrice === undefined || isNaN(currentFlight.businessPrice) || parseFloat(currentFlight.businessPrice) < 0) {
        errors.push('Giá thương gia');
      }

      if (currentFlight.economyPrice === '' || currentFlight.economyPrice === null || currentFlight.economyPrice === undefined || isNaN(currentFlight.economyPrice) || parseFloat(currentFlight.economyPrice) < 0) {
        errors.push('Giá phổ thông');
      }

      if (errors.length > 0) {
        setError(`Vui lòng điền đầy đủ thông tin bắt buộc: ${errors.join(', ')}`);
        return;
      }

      // Validate giá vé phải là số không âm (đã bao gồm ở trên)
      if (parseFloat(currentFlight.firstClassPrice) < 0 ||
          parseFloat(currentFlight.businessPrice) < 0 ||
          parseFloat(currentFlight.economyPrice) < 0) {
        setError('Giá vé phải là số không âm');
        return;
      }

      // Chuẩn bị dữ liệu
      const flightData = {
        flightNumber: currentFlight.flightNumber.trim(),
        route: currentFlight.route.trim(),
        departureTime: new Date(currentFlight.departureTime).toISOString(),
        arrivalTime: new Date(currentFlight.arrivalTime).toISOString(),
        totalSeats: Number(currentFlight.totalSeats),
        availableSeats: Number(currentFlight.totalSeats), // Mặc định = totalSeats
        firstClassPrice: parseFloat(currentFlight.firstClassPrice) || 0,
        businessPrice: parseFloat(currentFlight.businessPrice) || 0,
        economyPrice: parseFloat(currentFlight.economyPrice) || 0,
      };

      // Validate dữ liệu cuối cùng trước khi gửi
      if (flightData.totalSeats <= 0) {
        setError('Số ghế tổng cộng phải là số dương');
        return;
      }

      if (flightData.firstClassPrice < 0 || flightData.businessPrice < 0 || flightData.economyPrice < 0) {
        setError('Giá vé phải là số không âm');
        return;
      }

      console.log('Sending flight data:', flightData);
      console.log('Edit mode:', editMode);

      let response;
      if (editMode) {
        // Cập nhật
        const url = API_ENDPOINTS.FLIGHTS.DETAIL(currentFlight._id);
        console.log('Update URL:', url);

        response = await Promise.race([
          axios.put(url, flightData, getAuthHeader()),
          new Promise((_, reject) =>
            setTimeout(() => reject(new Error('Request timeout')), 10000)
          )
        ]);
        console.log('Update response:', response.data);
        setSuccess('Cập nhật chuyến bay thành công!');
      } else {
        // Thêm mới
        console.log('Create URL:', API_ENDPOINTS.FLIGHTS.LIST);
        response = await Promise.race([
          axios.post(API_ENDPOINTS.FLIGHTS.LIST, flightData, getAuthHeader()),
          new Promise((_, reject) =>
            setTimeout(() => reject(new Error('Request timeout')), 10000)
          )
        ]);
        console.log('Create response:', response.data);
        setSuccess('Thêm chuyến bay thành công!');
      }

      fetchFlights();
      setTimeout(() => {
        handleCloseDialog();
        setSuccess('');
      }, 1500);
    } catch (err) {
      console.error('Error saving flight:', err);

      // Xử lý lỗi chi tiết
      if (err.message === 'Request timeout') {
        setError('Yêu cầu bị timeout. Vui lòng thử lại.');
      } else if (err.code === 'ECONNREFUSED') {
        setError('Không thể kết nối đến server. Vui lòng kiểm tra backend có chạy không.');
      } else if (err.response) {
        const status = err.response.status;
        const data = err.response.data;

        if (status === 400) {
          setError(data?.message || 'Dữ liệu không hợp lệ');
        } else if (status === 401) {
          setError('Không có quyền truy cập');
        } else if (status === 404) {
          setError('Chuyến bay không tồn tại');
        } else if (status === 409) {
          setError('Mã chuyến bay đã tồn tại');
        } else if (status >= 500) {
          setError('Lỗi server nội bộ');
        } else if (data?.message) {
          setError(data.message);
        } else {
          setError(`Lỗi server: ${status}`);
        }
      } else if (err.request) {
        setError('Không nhận được phản hồi từ server');
      } else {
        setError('Có lỗi xảy ra khi lưu chuyến bay');
      }
    }
  };

  // Xóa chuyến bay
  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa chuyến bay này?')) {
      try {
        setError('');
        setSuccess('');

        console.log('Deleting flight with ID:', id);

        const response = await Promise.race([
          axios.delete(
            API_ENDPOINTS.FLIGHTS.DETAIL(id),
            getAuthHeader()
          ),
          new Promise((_, reject) =>
            setTimeout(() => reject(new Error('Request timeout')), 10000)
          )
        ]);

        console.log('Delete response:', response.data);
        setSuccess('Xóa chuyến bay thành công!');
        fetchFlights();
        setTimeout(() => setSuccess(''), 3000);
      } catch (err) {
        console.error('Error deleting flight:', err);

        // Xử lý lỗi chi tiết
        if (err.message === 'Request timeout') {
          setError('Yêu cầu bị timeout. Vui lòng thử lại.');
        } else if (err.code === 'ECONNREFUSED') {
          setError('Không thể kết nối đến server. Vui lòng kiểm tra backend có chạy không.');
        } else if (err.response) {
          const status = err.response.status;
          const data = err.response.data;

          if (status === 400) {
            setError(data?.message || 'Không thể xóa chuyến bay');
          } else if (status === 401) {
            setError('Không có quyền truy cập');
          } else if (status === 404) {
            setError('Chuyến bay không tồn tại');
          } else if (status >= 500) {
            setError('Lỗi server nội bộ');
          } else if (data?.message) {
            setError(data.message);
          } else {
            setError(`Lỗi server: ${status}`);
          }
        } else if (err.request) {
          setError('Không nhận được phản hồi từ server');
        } else {
          setError('Có lỗi xảy ra khi xóa chuyến bay');
        }
      }
    }
  };


  // Format ngày giờ
  const formatDateTime = (dateTime) => {
    return new Date(dateTime).toLocaleString('vi-VN');
  };


  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          <FlightIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
          Quản lý chuyến bay
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleOpenAdd}
        >
          Thêm chuyến bay
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Mã chuyến bay</strong></TableCell>
              <TableCell><strong>Tuyến đường</strong></TableCell>
              <TableCell><strong>Khởi hành</strong></TableCell>
              <TableCell><strong>Đến</strong></TableCell>
              <TableCell><strong>Giá hạng nhất</strong></TableCell>
              <TableCell><strong>Giá thương gia</strong></TableCell>
              <TableCell><strong>Giá phổ thông</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {flights.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} align="center">
                  Chưa có chuyến bay nào
                </TableCell>
              </TableRow>
            ) : (
              flights.map((flight) => (
                <TableRow key={flight._id} hover>
                  <TableCell>{flight.flightNumber}</TableCell>
                  <TableCell>{flight.route}</TableCell>
                  <TableCell>{formatDateTime(flight.departureTime)}</TableCell>
                  <TableCell>{formatDateTime(flight.arrivalTime)}</TableCell>
                  <TableCell>{flight.firstClassPrice?.toLocaleString('vi-VN')} VNĐ</TableCell>
                  <TableCell>{flight.businessPrice?.toLocaleString('vi-VN')} VNĐ</TableCell>
                  <TableCell>{flight.economyPrice?.toLocaleString('vi-VN')} VNĐ</TableCell>
                  <TableCell align="center">
                    <IconButton
                      color="primary"
                      size="small"
                      onClick={() => handleOpenEdit(flight)}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      color="error"
                      size="small"
                      onClick={() => handleDelete(flight._id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog thêm/sửa */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          {editMode ? 'Chỉnh sửa chuyến bay' : 'Thêm chuyến bay mới'}
        </DialogTitle>
        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          )}
          <Box sx={{ display: 'grid', gap: 2, mt: 2 }}>
            <TextField
              label="Mã chuyến bay"
              name="flightNumber"
              value={currentFlight.flightNumber}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: VN123"
            />
            <TextField
              label="Tuyến đường"
              name="route"
              value={currentFlight.route}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: Hà Nội - Hồ Chí Minh"
            />
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Thời gian khởi hành"
                name="departureTime"
                type="datetime-local"
                value={currentFlight.departureTime}
                onChange={handleInputChange}
                InputLabelProps={{ shrink: true }}
                required
              />
              <TextField
                label="Thời gian đến"
                name="arrivalTime"
                type="datetime-local"
                value={currentFlight.arrivalTime}
                onChange={handleInputChange}
                InputLabelProps={{ shrink: true }}
                required
              />
            </Box>
            <TextField
              label="Giá hạng nhất (VNĐ)"
              name="firstClassPrice"
              type="number"
              value={currentFlight.firstClassPrice}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 5000000"
              inputProps={{ min: 0, step: 1000 }}
            />
            <TextField
              label="Giá thương gia (VNĐ)"
              name="businessPrice"
              type="number"
              value={currentFlight.businessPrice}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 3000000"
              inputProps={{ min: 0, step: 1000 }}
            />
            <TextField
              label="Giá phổ thông (VNĐ)"
              name="economyPrice"
              type="number"
              value={currentFlight.economyPrice}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 1500000"
              inputProps={{ min: 0, step: 1000 }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Hủy</Button>
          <Button onClick={handleSave} variant="contained">
            {editMode ? 'Cập nhật' : 'Thêm mới'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Flights;
