import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Chip,
  CircularProgress,
  MenuItem,
} from '@mui/material';
import {
  Edit as EditIcon,
  Delete as DeleteIcon,
  ConfirmationNumber as TicketIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS } from '../config';

const TicketsManage = () => {
  const [tickets, setTickets] = useState([]);
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentTicket, setCurrentTicket] = useState({
    flightCode: '',
    passengerName: '',
    email: '',
    phoneNumber: '',
    seatNumber: '',
    price: '',
    status: 'booked',
  });

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [totalItems, setTotalItems] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  // Search and filter state
  const [searchFlightCode, setSearchFlightCode] = useState('');

  // Lấy danh sách vé
  const fetchTickets = async () => {
    try {
      setLoading(true);

      // Chuẩn bị query parameters cho pagination và tìm kiếm
      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: itemsPerPage.toString(),
      });

      // Thêm tìm kiếm theo flightCode nếu có
      if (searchFlightCode.trim()) {
        params.append('flightCode', searchFlightCode.trim());
      }

      const response = await axios.get(`${API_ENDPOINTS.TICKETS.LIST}?${params}`);
      console.log('Tickets response:', response.data);

      const ticketsData = response.data.data?.tickets || response.data.data || [];
      const paginationData = response.data.data?.pagination || response.data.pagination || {};

      setTickets(ticketsData);
      setTotalItems(paginationData.totalRecord || 0);
      setTotalPages(Math.ceil((paginationData.totalRecord || 0) / itemsPerPage));
      setError('');
    } catch (err) {
      setError('Không thể tải danh sách vé');
      console.error('Error fetching tickets:', err);
    } finally {
      setLoading(false);
    }
  };

  // Lấy danh sách chuyến bay
  const fetchFlights = async () => {
    try {
      const response = await axios.get(API_ENDPOINTS.FLIGHTS.LIST);
      const flightsList = response.data.data?.flights || response.data.data || [];
      setFlights(flightsList);
    } catch (err) {
      console.error('Error fetching flights:', err);
    }
  };

  useEffect(() => {
    fetchTickets();
    fetchFlights();
  }, [currentPage, itemsPerPage, searchFlightCode]);

  // Mở dialog chỉnh sửa
  const handleOpenEdit = (ticket) => {
    setEditMode(true);
    // Lấy flightCode từ flight
    const flight = flights.find(f => f._id === ticket.flightId);
    setCurrentTicket({
      ...ticket,
      flightCode: flight?.flightCode || '',
      paymentDeadline: ticket.paymentDeadline ? new Date(ticket.paymentDeadline).toISOString().slice(0, 16) : new Date(Date.now() + 30 * 60 * 1000).toISOString().slice(0, 16),
    });
    setOpenDialog(true);
  };

  // Đóng dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setError('');
    setSuccess('');
  };

  // Xử lý thay đổi trang
  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  // Xử lý thay đổi số items per page
  const handleItemsPerPageChange = (newItemsPerPage) => {
    setItemsPerPage(newItemsPerPage);
    setCurrentPage(1); // Reset về trang đầu khi thay đổi số items
  };

  // Xử lý tìm kiếm
  const handleSearch = () => {
    setCurrentPage(1); // Reset về trang đầu khi tìm kiếm
    fetchTickets();
  };

  // Xử lý reset tìm kiếm
  const handleResetSearch = () => {
    setSearchFlightCode('');
    setCurrentPage(1);
  };

  // Xử lý thay đổi input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentTicket((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Lưu vé (chỉ sửa)
  const handleSave = async () => {
    try {
      // Validate dữ liệu
      if (!currentTicket.flightCode || !currentTicket.passengerName ||
          !currentTicket.email || !currentTicket.phoneNumber ||
          !currentTicket.seatNumber || !currentTicket.price) {
        setError('Vui lòng điền đầy đủ thông tin bắt buộc');
        return;
      }

      // Chuẩn bị dữ liệu
      const ticketData = {
        flightCode: currentTicket.flightCode,
        passengerName: currentTicket.passengerName.trim(),
        email: currentTicket.email.trim(),
        phoneNumber: currentTicket.phoneNumber.trim(),
        seatNumber: currentTicket.seatNumber.trim(),
        price: Number(currentTicket.price),
        status: currentTicket.status,
        paymentDeadline: currentTicket.paymentDeadline ? new Date(currentTicket.paymentDeadline) : new Date(Date.now() + 30 * 60 * 1000),
      };

      console.log('Sending ticket data:', ticketData);

      // Chỉ cập nhật (không có thêm mới)
      await axios.put(
        API_ENDPOINTS.TICKETS.DETAIL(currentTicket._id),
        ticketData
      );
      setSuccess('Cập nhật vé thành công!');

      fetchTickets();
      fetchFlights(); // Refresh để cập nhật số chỗ
      setTimeout(() => {
        handleCloseDialog();
        setSuccess('');
      }, 1500);
    } catch (err) {
      const errorMsg = err.response?.data?.message || err.message || 'Có lỗi xảy ra';
      setError(errorMsg);
      console.error('Error saving ticket:', err.response?.data || err);
    }
  };

  // Xóa vé
  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa vé này? Số chỗ sẽ được hoàn trả.')) {
      try {
        await axios.delete(
          API_ENDPOINTS.TICKETS.DETAIL(id)
        );
        setSuccess('Xóa vé thành công!');
        fetchTickets();
        fetchFlights(); // Refresh để cập nhật số chỗ
        setTimeout(() => setSuccess(''), 3000);
      } catch (err) {
        setError(err.response?.data?.message || 'Không thể xóa vé');
        console.error('Error deleting ticket:', err);
      }
    }
  };

  // Format giá tiền
  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
    }).format(price);
  };

  // Format ngày giờ
  const formatDateTime = (dateTime) => {
    return new Date(dateTime).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Tính thời gian còn lại đến hạn thanh toán
  const getTimeUntilDeadline = (paymentDeadline) => {
    if (!paymentDeadline) return null;

    const deadline = new Date(paymentDeadline);
    const now = new Date();
    const timeLeft = deadline.getTime() - now.getTime();

    if (timeLeft <= 0) return null;

    const hours = Math.floor(timeLeft / (1000 * 60 * 60));
    const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);

    return { hours, minutes, seconds, total: timeLeft };
  };

  // Component đếm ngược thời gian
  const CountdownTimer = ({ paymentDeadline, paymentStatus }) => {
    const [timeLeft, setTimeLeft] = useState(getTimeUntilDeadline(paymentDeadline));

    useEffect(() => {
      if (paymentStatus !== 'pending' || !paymentDeadline) {
        setTimeLeft(null);
        return;
      }

      const timer = setInterval(() => {
        const newTimeLeft = getTimeUntilDeadline(paymentDeadline);
        setTimeLeft(newTimeLeft);

        // Dừng timer nếu hết thời gian
        if (!newTimeLeft) {
          clearInterval(timer);
        }
      }, 1000);

      return () => clearInterval(timer);
    }, [paymentDeadline, paymentStatus]);

    if (paymentStatus !== 'pending' || !timeLeft) {
      return null;
    }

    const { hours, minutes, seconds } = timeLeft;

    return (
      <Box sx={{ mt: 0.5 }}>
        <Typography variant="caption" color={hours < 2 ? 'error' : 'warning'}>
          Còn: {hours.toString().padStart(2, '0')}:{minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}
        </Typography>
      </Box>
    );
  };

  // Tên trạng thái thanh toán tiếng Việt
  const getPaymentStatusLabel = (paymentStatus) => {
    const labels = {
      pending: 'Chờ thanh toán',
      paid: 'Đã thanh toán',
      refunded: 'Đã hoàn tiền',
      failed: 'Thanh toán thất bại',
    };
    return labels[paymentStatus] || paymentStatus;
  };

  // Màu trạng thái thanh toán
  const getPaymentStatusColor = (paymentStatus) => {
    const colors = {
      pending: 'warning',
      paid: 'success',
      refunded: 'info',
      failed: 'error',
    };
    return colors[paymentStatus] || 'default';
  };

  // Tên trạng thái vé tiếng Việt
  const getTicketStatusLabel = (status) => {
    const labels = {
      booked: 'Đã đặt',
      'checked-in': 'Đã check-in',
      cancelled: 'Đã hủy',
    };
    return labels[status] || status;
  };

  // Màu trạng thái vé
  const getTicketStatusColor = (status) => {
    const colors = {
      booked: 'primary',
      'checked-in': 'success',
      cancelled: 'error',
    };
    return colors[status] || 'default';
  };

  // Xác định loại vé dựa trên giá
  const getTicketType = (ticketPrice, flight, passengerCount = 1) => {
    if (!flight) return 'Không xác định';

    // flight ở đây đã được populate từ API, có đầy đủ thông tin
    const { firstClassPrice, businessPrice, economyPrice } = flight;

    // Tính giá vé đơn lẻ từ tổng giá và số lượng hành khách
    const singlePrice = ticketPrice / passengerCount;

    // So sánh với giá các hạng (với sai số nhỏ để tránh lỗi floating point)
    const priceDiff = 1000; // Sai số cho phép

    if (Math.abs(singlePrice - firstClassPrice) <= priceDiff) {
      return 'First Class';
    } else if (Math.abs(singlePrice - businessPrice) <= priceDiff) {
      return 'Business';
    } else if (Math.abs(singlePrice - economyPrice) <= priceDiff) {
      return 'Economy';
    }

    return 'Không xác định';
  };

  // Định dạng loại vé từ enum sang tiếng Anh
  const formatTicketClass = (ticketClass) => {
    const classMap = {
      economy: 'Economy',
      business: 'Business',
      first: 'First Class'
    };
    return classMap[ticketClass] || ticketClass;
  };

  // Hiển thị thông tin ghế kèm theo loại vé
  const getSeatInfoWithTypes = (ticket) => {
    if (!ticket.seatNumbers || ticket.seatNumbers.length === 0) {
      const ticketType = ticket.seatClasses && ticket.seatClasses[0] ?
        formatTicketClass(ticket.seatClasses[0]) :
        (ticket.ticketClass ? formatTicketClass(ticket.ticketClass) : 'Không xác định');
      return `${ticket.seatNumber} (${ticketType})`;
    }

    // Nhóm ghế theo loại vé từ seatClasses nếu có
    if (ticket.seatClasses && ticket.seatClasses.length > 0) {
      const seatGroups = {};

      ticket.seatNumbers.forEach((seat, index) => {
        const seatClass = ticket.seatClasses[index];
        const ticketType = seatClass ? formatTicketClass(seatClass) : 'Không xác định';

        if (!seatGroups[ticketType]) {
          seatGroups[ticketType] = [];
        }
        seatGroups[ticketType].push(seat);
      });

      // Tạo chuỗi hiển thị
      const displayParts = Object.entries(seatGroups).map(([type, seats]) => {
        return `${type}: ${seats.join(', ')}`;
      });

      return displayParts.join('; ');
    }

    // Fallback: sử dụng ticketClass chung nếu không có seatClasses
    const ticketType = ticket.ticketClass ? formatTicketClass(ticket.ticketClass) : 'Không xác định';
    return `${ticket.seatNumbers.join(', ')} (${ticketType})`;
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="flex-start" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          <TicketIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
          Quản lý đơn vé
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      {/* Search and Filter Controls */}
      <Paper sx={{ p: 2, mb: 2 }}>
        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', flexWrap: 'wrap' }}>
          <TextField
            label="Tìm kiếm theo mã chuyến bay"
            variant="outlined"
            size="small"
            value={searchFlightCode}
            onChange={(e) => setSearchFlightCode(e.target.value)}
            sx={{ minWidth: 200 }}
          />
          <Button
            variant="contained"
            onClick={handleSearch}
            disabled={loading}
          >
            Tìm kiếm
          </Button>
          <Button
            variant="outlined"
            onClick={handleResetSearch}
            disabled={loading}
          >
            Reset
          </Button>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, ml: 'auto' }}>
            <Typography variant="body2">Hiển thị:</Typography>
            <TextField
              select
              size="small"
              value={itemsPerPage}
              onChange={(e) => handleItemsPerPageChange(Number(e.target.value))}
              sx={{ minWidth: 80 }}
            >
              <MenuItem value={10}>10</MenuItem>
              <MenuItem value={25}>25</MenuItem>
              <MenuItem value={50}>50</MenuItem>
            </TextField>
            <Typography variant="body2">mục mỗi trang</Typography>
          </Box>
        </Box>
      </Paper>

      {/* Pagination Info */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="body2">
          Hiển thị {((currentPage - 1) * itemsPerPage) + 1}-{Math.min(currentPage * itemsPerPage, totalItems)} của {totalItems} kết quả
        </Typography>
        <Typography variant="body2">
          Trang {currentPage} của {totalPages}
        </Typography>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Mã vé</strong></TableCell>
              <TableCell><strong>Hành khách</strong></TableCell>
              <TableCell><strong>Email</strong></TableCell>
              <TableCell><strong>SĐT</strong></TableCell>
              <TableCell><strong>Số ghế & Loại vé</strong></TableCell>
              <TableCell><strong>Tổng ghế đã đặt</strong></TableCell>
              <TableCell><strong>Giá</strong></TableCell>
              <TableCell><strong>Ngày đặt</strong></TableCell>
              <TableCell><strong>Hạn thanh toán</strong></TableCell>
              <TableCell><strong>Thanh toán</strong></TableCell>
              <TableCell><strong>Trạng thái</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tickets.length === 0 ? (
              <TableRow>
                <TableCell colSpan={11} align="center">
                  {searchFlightCode ? `Không tìm thấy vé nào với mã chuyến bay: ${searchFlightCode}` : 'Chưa có vé nào'}
                </TableCell>
              </TableRow>
            ) : (
              tickets.map((ticket) => (
                <TableRow key={ticket._id} hover>
                  <TableCell>{ticket.ticketCode}</TableCell>
                  <TableCell>{ticket.passengerName}</TableCell>
                  <TableCell>{ticket.email}</TableCell>
                  <TableCell>{ticket.phoneNumber}</TableCell>
                  <TableCell>{getSeatInfoWithTypes(ticket)}</TableCell>
                  <TableCell>{ticket.passengerCount || 1}</TableCell>
                  <TableCell>{formatPrice(ticket.price)}</TableCell>
                  <TableCell>{formatDateTime(ticket.bookingDate)}</TableCell>
                  <TableCell>{formatDateTime(ticket.paymentDeadline)}</TableCell>
                  <TableCell>
                    <Box>
                      <Chip
                        label={getPaymentStatusLabel(ticket.paymentStatus)}
                        color={getPaymentStatusColor(ticket.paymentStatus)}
                        size="small"
                      />
                      <CountdownTimer
                        paymentDeadline={ticket.paymentDeadline}
                        paymentStatus={ticket.paymentStatus}
                      />
                    </Box>
                  </TableCell>
                  <TableCell>
                    <Chip
                      label={getTicketStatusLabel(ticket.status)}
                      color={getTicketStatusColor(ticket.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell align="center">
                    <IconButton
                      color="primary"
                      size="small"
                      onClick={() => handleOpenEdit(ticket)}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      color="error"
                      size="small"
                      onClick={() => handleDelete(ticket._id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Pagination Controls */}
      {totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3, gap: 1, flexWrap: 'wrap' }}>
          <Button
            variant="outlined"
            disabled={currentPage === 1 || loading}
            onClick={() => handlePageChange(1)}
          >
            Trang đầu
          </Button>
          <Button
            variant="outlined"
            disabled={currentPage === 1 || loading}
            onClick={() => handlePageChange(currentPage - 1)}
          >
            Trang trước
          </Button>

          {/* Page Numbers */}
          {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
            let pageNumber;

            if (totalPages <= 5) {
              // Nếu tổng số trang <= 5, hiển thị tất cả
              pageNumber = i + 1;
            } else if (currentPage <= 3) {
              // Nếu đang ở 3 trang đầu, hiển thị 1,2,3,4,5
              pageNumber = i + 1;
            } else if (currentPage >= totalPages - 2) {
              // Nếu đang ở 3 trang cuối, hiển thị total-4, total-3, total-2, total-1, total
              pageNumber = totalPages - 4 + i;
            } else {
              // Nếu ở giữa, hiển thị current-2, current-1, current, current+1, current+2
              pageNumber = currentPage - 2 + i;
            }

            return (
              <Button
                key={pageNumber}
                variant={currentPage === pageNumber ? "contained" : "outlined"}
                disabled={loading}
                onClick={() => handlePageChange(pageNumber)}
                sx={{ minWidth: 40 }}
              >
                {pageNumber}
              </Button>
            );
          })}

          <Button
            variant="outlined"
            disabled={currentPage === totalPages || loading}
            onClick={() => handlePageChange(currentPage + 1)}
          >
            Trang sau
          </Button>
          <Button
            variant="outlined"
            disabled={currentPage === totalPages || loading}
            onClick={() => handlePageChange(totalPages)}
          >
            Trang cuối
          </Button>
        </Box>
      )}

      {/* Dialog chỉnh sửa */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          Chỉnh sửa vé
        </DialogTitle>
        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          )}
          <Box sx={{ display: 'grid', gap: 2, mt: 2 }}>
            <TextField
              label="Chuyến bay"
              name="flightCode"
              select
              value={currentTicket.flightCode}
              onChange={handleInputChange}
              fullWidth
              required
              disabled={editMode}
              helperText={editMode ? "Không thể đổi chuyến bay khi sửa vé" : "Chọn chuyến bay"}
            >
              {flights.map((flight) => (
                <MenuItem key={flight._id} value={flight.flightCode}>
                  {flight.flightCode} - {flight.route} (Còn {flight.availableSeats} chỗ)
                </MenuItem>
              ))}
            </TextField>
            <TextField
              label="Tên hành khách"
              name="passengerName"
              value={currentTicket.passengerName}
              onChange={handleInputChange}
              fullWidth
              required
            />
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Email"
                name="email"
                type="email"
                value={currentTicket.email}
                onChange={handleInputChange}
                required
              />
              <TextField
                label="Số điện thoại"
                name="phoneNumber"
                value={currentTicket.phoneNumber}
                onChange={handleInputChange}
                required
              />
            </Box>
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Số ghế"
                name="seatNumber"
                value={currentTicket.seatNumber}
                onChange={handleInputChange}
                required
                placeholder="VD: 15A"
              />
              <TextField
                label="Giá vé (VNĐ)"
                name="price"
                type="number"
                value={currentTicket.price}
                onChange={handleInputChange}
                required
              />
            </Box>
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Hạn thanh toán"
                name="paymentDeadline"
                type="datetime-local"
                value={currentTicket.paymentDeadline ? new Date(currentTicket.paymentDeadline).toISOString().slice(0, 16) : ''}
                onChange={handleInputChange}
                required
              />
              <TextField
                label="Trạng thái"
                name="status"
                select
                value={currentTicket.status}
                onChange={handleInputChange}
                required
              >
                <MenuItem value="booked">Đã đặt</MenuItem>
                <MenuItem value="checked-in">Đã check-in</MenuItem>
                <MenuItem value="cancelled">Đã hủy</MenuItem>
              </TextField>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Hủy</Button>
          <Button onClick={handleSave} variant="contained">
            Cập nhật
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TicketsManage;
