import React, { useState, useEffect } from 'react';
import {
  Flight as FlightIcon,
  ConfirmationNumber as TicketIcon,
  AttachMoney as MoneyIcon,
  People as PeopleIcon,
  Refresh as RefreshIcon,
} from '@mui/material';
import axios from 'axios';
import { API_ENDPOINTS, getAuthHeader } from '../config';

const StatCard = ({ title, value, icon: Icon, color, onClick }) => (
  <Paper
    onClick={onClick}
    sx={{
      p: 3,
      display: 'flex',
      flexDirection: 'column',
      height: '100%',
      backgroundColor: (theme) =>
        theme.palette.mode === 'dark' ? theme.palette.grey[800] : theme.palette.grey[100],
      '&:hover': {
        boxShadow: 3,
        transform: 'translateY(-4px)',
        transition: 'all 0.3s ease-in-out',
      },
    }}
    elevation={2}
  >
    <Box display="flex" justifyContent="space-between" alignItems="center">
      <Box>
        <Typography variant="h6" color="textSecondary" gutterBottom>
          {title}
        </Typography>
        <Typography variant="h4" component="div">
          {value}
        </Typography>
      </Box>
      <Icon
        sx={{
          fontSize: 48,
          color: color || 'primary.main',
          opacity: 0.8,
        }}
      />
    </Box>
  </Paper>
);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalFlights: 0,
    totalTickets: 0,
    totalRevenue: 0,
    totalUsers: 0,
  });
  const [refreshing, setRefreshing] = useState(false);

  // Hàm refresh dữ liệu
  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchStats();
    setRefreshing(false);
  };

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);

        // Lấy thống kê từ các API
        const [flightsRes, ticketsRes, usersRes, statsRes] = await Promise.all([
          axios.get(API_ENDPOINTS.FLIGHTS.LIST, getAuthHeader()),
          axios.get(API_ENDPOINTS.TICKETS.LIST, getAuthHeader()),
          axios.get(API_ENDPOINTS.USERS.LIST, getAuthHeader()).catch(() => ({ data: { data: [] } })),
          axios.get(API_ENDPOINTS.DASHBOARD.STATS, getAuthHeader()).catch(() => ({ data: { data: null } })),
        ]);

        const flights = flightsRes.data.data?.flights || flightsRes.data.data || [];
        const tickets = ticketsRes.data.data?.tickets || ticketsRes.data.data || [];
        const users = usersRes.data.data?.users || usersRes.data.data || [];

        // Sử dụng dữ liệu từ dashboard stats nếu có, nếu không thì tính toán thủ công
        let statsData = statsRes.data.data;
        if (!statsData) {
          // Tính tổng doanh thu - chỉ tính vé đã thanh toán
          const revenue = tickets
            .filter(ticket => ticket.paymentStatus === 'paid')
            .reduce((sum, ticket) => sum + (ticket.price || 0), 0);

          statsData = {
            totalFlights: flights.length,
            totalTickets: tickets.length,
            totalRevenue: revenue,
            totalUsers: users.length,
          };
        }

        setStats({
          totalFlights: statsData.totalFlights || flights.length,
          totalTickets: statsData.totalTickets || tickets.length,
          totalRevenue: statsData.totalRevenue || revenue,
          totalUsers: statsData.totalUsers || users.length,
        });

        setUsers(users);
        setError('');
      } catch (err) {
        console.error('Error fetching stats:', err);
        setError('Không thể tải thống kê');
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
    }).format(value);
  };

  const statsCards = [
    { title: 'Tổng chuyến bay', value: stats.totalFlights, icon: FlightIcon, color: 'primary.main' },
    { title: 'Đơn vé', value: stats.totalTickets, icon: TicketIcon, color: 'success.main' },
    { title: 'Doanh thu', value: formatCurrency(stats.totalRevenue), icon: MoneyIcon, color: 'warning.main' },
    { title: 'Người dùng', value: stats.totalUsers, icon: PeopleIcon, color: 'error.main' },
  ];

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Tổng quan
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3} sx={{ mb: 4 }}>
        {statsCards.map((stat, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <StatCard
              title={stat.title}
              value={stat.value}
              icon={stat.icon}
              color={stat.color}
            />
          </Grid>
        ))}
      </Grid>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Thống kê đặt vé
            </Typography>
            <Box sx={{ height: 300, backgroundColor: 'grey.100', borderRadius: 1 }}>
              {/* Placeholder for chart */}
              <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                height="100%"
                color="text.secondary"
              >
                Biểu đồ thống kê sẽ hiển thị ở đây
              </Box>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Người dùng mới đăng ký
            </Typography>
            <Box>
              {users.length === 0 ? (
                <Typography variant="body2" color="textSecondary">
                  Chưa có người dùng nào
                </Typography>
              ) : (
                users.slice(0, 5).map((user, index) => (
                  <Box
                    key={user._id || index}
                    sx={{
                      p: 2,
                      mb: 1,
                      backgroundColor: 'grey.50',
                      borderRadius: 1,
                      borderLeft: '4px solid',
                      borderColor: 'primary.main',
                    }}
                  >
                    <Typography variant="subtitle2">
                      {user.fullName || user.name || user.email || 'Người dùng'}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      {user.email}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Đăng ký: {user.createdAt ? new Date(user.createdAt).toLocaleDateString('vi-VN') : 'N/A'}
                    </Typography>
                  </Box>
                ))
              )}
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
