import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Chip,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Flight as FlightIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS, getAuthHeader } from '../config';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentFlight, setCurrentFlight] = useState({
    flightNumber: '',
    route: '',
    departureTime: '',
    arrivalTime: '',
    totalSeats: '',
    firstClassPrice: '',
    businessPrice: '',
    economyPrice: '',
  });

  // Lấy danh sách chuyến bay
  const fetchFlights = async () => {
    try {
      setLoading(true);
      const response = await axios.get(API_ENDPOINTS.FLIGHTS.LIST, getAuthHeader());
      // Backend trả về { flights: [], pagination: {} }
      setFlights(response.data.data?.flights || response.data.data || []);
      setError('');
    } catch (err) {
      setError('Không thể tải danh sách chuyến bay');
      console.error('Error fetching flights:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  // Mở dialog thêm mới
  const handleOpenAdd = () => {
    setEditMode(false);
    setCurrentFlight({
      flightNumber: '',
      route: '',
      departureTime: '',
      arrivalTime: '',
      totalSeats: '',
      firstClassPrice: '',
      businessPrice: '',
      economyPrice: '',
    });
    setOpenDialog(true);
  };

  // Mở dialog chỉnh sửa
  const handleOpenEdit = (flight) => {
    setEditMode(true);
    setCurrentFlight({
      ...flight,
      departureTime: new Date(flight.departureTime).toISOString().slice(0, 16),
      arrivalTime: new Date(flight.arrivalTime).toISOString().slice(0, 16),
      firstClassPrice: flight.firstClassPrice || '',
      businessPrice: flight.businessPrice || '',
      economyPrice: flight.economyPrice || '',
    });
    setOpenDialog(true);
  };

  // Đóng dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setError('');
    setSuccess('');
  };

  // Xử lý thay đổi input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentFlight((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Lưu chuyến bay (thêm hoặc sửa)
  const handleSave = async () => {
    try {
      // Validate mã chuyến bay
      if (!currentFlight.flightNumber || currentFlight.flightNumber.trim() === '') {
        setError('Mã chuyến bay không được để trống');
        return;
      }

      // Validate định dạng mã chuyến bay (ví dụ: VN123, VJ456, etc.)
      const flightNumberPattern = /^[A-Z]{2}\d{3,}$/;
      if (!flightNumberPattern.test(currentFlight.flightNumber.trim())) {
        setError('Mã chuyến bay phải có định dạng: 2 chữ cái + số (VD: VN123, VJ456)');
        return;
      }

      // Validate tuyến đường không được trùng với chuyến bay cùng thời gian
      // (Có thể thêm logic kiểm tra này nếu cần)

      // Validate giá vé hợp lý (có thể thêm logic kiểm tra giá vé logic)
      if (parseFloat(currentFlight.firstClassPrice) > 0 &&
          parseFloat(currentFlight.businessPrice) > parseFloat(currentFlight.firstClassPrice)) {
        setError('Giá thương gia không được cao hơn giá hạng nhất');
        return;
      }

      if (parseFloat(currentFlight.businessPrice) > 0 &&
          parseFloat(currentFlight.economyPrice) > parseFloat(currentFlight.businessPrice)) {
        setError('Giá phổ thông không được cao hơn giá thương gia');
        return;
      }

      // Chuẩn bị dữ liệu
      const flightData = {
        flightNumber: currentFlight.flightNumber.trim(),
        route: currentFlight.route.trim(),
        departureTime: new Date(currentFlight.departureTime).toISOString(),
        arrivalTime: new Date(currentFlight.arrivalTime).toISOString(),
        totalSeats: Number(currentFlight.totalSeats),
        availableSeats: Number(currentFlight.totalSeats), // Mặc định = totalSeats
        firstClassPrice: parseFloat(currentFlight.firstClassPrice) || 0,
        businessPrice: parseFloat(currentFlight.businessPrice) || 0,
        economyPrice: parseFloat(currentFlight.economyPrice) || 0,
      };

      console.log('Final flight data to send:', flightData);
      console.log('Departure time type:', typeof flightData.departureTime);
      console.log('Arrival time type:', typeof flightData.arrivalTime);

      console.log('Final flight data to send:', flightData);
      console.log('Departure time type:', typeof flightData.departureTime);
      console.log('Arrival time type:', typeof flightData.arrivalTime);

      if (editMode) {
        // Cập nhật
        await axios.put(
          API_ENDPOINTS.FLIGHTS.DETAIL(currentFlight._id),
          flightData,
          getAuthHeader()
        );
        setSuccess('Cập nhật chuyến bay thành công!');
      } else {
        // Thêm mới
        await axios.post(
          API_ENDPOINTS.FLIGHTS.LIST,
          flightData,
          getAuthHeader()
        );
        setSuccess('Thêm chuyến bay thành công!');
      }

      fetchFlights();
      setTimeout(() => {
        handleCloseDialog();
        setSuccess('');
      }, 1500);
    } catch (err) {
      console.error('Error saving flight:', err);

      // Xử lý lỗi MongoDB duplicate key chi tiết hơn
      if (err.response?.data?.message) {
        const mongoError = err.response.data.message;

        // Kiểm tra lỗi duplicate key từ MongoDB
        if (mongoError.includes('E11000') && mongoError.includes('duplicate key')) {
          if (mongoError.includes('flightNumber')) {
            setError('Mã chuyến bay đã tồn tại. Vui lòng chọn mã khác.');
          } else if (mongoError.includes('flightCode')) {
            setError('Mã chuyến bay đã tồn tại. Vui lòng chọn mã khác.');
          } else {
            setError('Dữ liệu bị trùng lặp. Vui lòng kiểm tra lại thông tin.');
          }
        } else {
          setError(mongoError);
        }
      } else {
        setError(err.response?.data?.message || err.message || 'Có lỗi xảy ra khi lưu chuyến bay');
      }
    }
  };

  // Xóa chuyến bay
  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa chuyến bay này?')) {
      try {
        await axios.delete(
          API_ENDPOINTS.FLIGHTS.DETAIL(id),
          getAuthHeader()
        );
        setSuccess('Xóa chuyến bay thành công!');
        fetchFlights();
        setTimeout(() => setSuccess(''), 3000);
      } catch (err) {
        console.error('Error deleting flight:', err);

        // Xử lý lỗi chi tiết hơn
        if (err.response?.data?.message) {
          const mongoError = err.response.data.message;

          // Kiểm tra lỗi từ MongoDB
          if (mongoError.includes('E11000') || mongoError.includes('duplicate') || mongoError.includes('unique')) {
            setError('Không thể xóa chuyến bay vì dữ liệu bị ràng buộc');
          } else {
            setError(mongoError);
          }
        } else {
          setError(err.response?.data?.message || 'Không thể xóa chuyến bay');
        }
      }
    }
  };


  // Format ngày giờ
  const formatDateTime = (dateTime) => {
    return new Date(dateTime).toLocaleString('vi-VN');
  };


  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          <FlightIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
          Quản lý chuyến bay
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleOpenAdd}
        >
          Thêm chuyến bay
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Mã chuyến bay</strong></TableCell>
              <TableCell><strong>Tuyến đường</strong></TableCell>
              <TableCell><strong>Khởi hành</strong></TableCell>
              <TableCell><strong>Đến</strong></TableCell>
              <TableCell><strong>Số ghế (còn/tổng)</strong></TableCell>
              <TableCell><strong>Giá hạng nhất</strong></TableCell>
              <TableCell><strong>Giá thương gia</strong></TableCell>
              <TableCell><strong>Giá phổ thông</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {flights.length === 0 ? (
              <TableRow>
                <TableCell colSpan={8} align="center">
                  Chưa có chuyến bay nào
                </TableCell>
              </TableRow>
            ) : (
              flights.map((flight) => (
                <TableRow key={flight._id} hover>
                  <TableCell>{flight.flightNumber}</TableCell>
                  <TableCell>{flight.route}</TableCell>
                  <TableCell>{formatDateTime(flight.departureTime)}</TableCell>
                  <TableCell>{formatDateTime(flight.arrivalTime)}</TableCell>
                  <TableCell>{flight.availableSeats || 0}/{flight.totalSeats || 0} ghế</TableCell>
                  <TableCell>{flight.firstClassPrice?.toLocaleString('vi-VN')} VNĐ</TableCell>
                  <TableCell>{flight.businessPrice?.toLocaleString('vi-VN')} VNĐ</TableCell>
                  <TableCell>{flight.economyPrice?.toLocaleString('vi-VN')} VNĐ</TableCell>
                  <TableCell align="center">
                    <IconButton
                      color="primary"
                      size="small"
                      onClick={() => handleOpenEdit(flight)}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      color="error"
                      size="small"
                      onClick={() => handleDelete(flight._id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog thêm/sửa */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          {editMode ? 'Chỉnh sửa chuyến bay' : 'Thêm chuyến bay mới'}
        </DialogTitle>
        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          )}
          <Box sx={{ display: 'grid', gap: 2, mt: 2 }}>
            <TextField
              label="Mã chuyến bay"
              name="flightNumber"
              value={currentFlight.flightNumber}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: VN123"
            />
            <TextField
              label="Tuyến đường"
              name="route"
              value={currentFlight.route}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: Hà Nội - Hồ Chí Minh"
            />
            <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 2 }}>
              <TextField
                label="Thời gian khởi hành"
                name="departureTime"
                type="datetime-local"
                value={currentFlight.departureTime}
                onChange={handleInputChange}
                InputLabelProps={{ shrink: true }}
                required
              />
              <TextField
                label="Thời gian đến"
                name="arrivalTime"
                type="datetime-local"
                value={currentFlight.arrivalTime}
                onChange={handleInputChange}
                InputLabelProps={{ shrink: true }}
                required
              />
            </Box>
            <TextField
              label="Số ghế tổng cộng"
              name="totalSeats"
              type="number"
              value={currentFlight.totalSeats}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 180"
              inputProps={{ min: 1, step: 1 }}
            />
            <TextField
              label="Giá hạng nhất (VNĐ)"
              name="firstClassPrice"
              type="number"
              value={currentFlight.firstClassPrice}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 5000000"
              inputProps={{ min: 0, step: 1000 }}
            />
            <TextField
              label="Giá thương gia (VNĐ)"
              name="businessPrice"
              type="number"
              value={currentFlight.businessPrice}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 3000000"
              inputProps={{ min: 0, step: 1000 }}
            />
            <TextField
              label="Giá phổ thông (VNĐ)"
              name="economyPrice"
              type="number"
              value={currentFlight.economyPrice}
              onChange={handleInputChange}
              fullWidth
              required
              placeholder="VD: 1500000"
              inputProps={{ min: 0, step: 1000 }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Hủy</Button>
          <Button onClick={handleSave} variant="contained">
            {editMode ? 'Cập nhật' : 'Thêm mới'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Flights;
