import React, { useState, useEffect } from 'react';
import {
  Grid,
  Paper,
  Typography,
  Box,
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Chip
} from '@mui/material';
import {
  Flight as FlightIcon,
  ConfirmationNumber as TicketIcon,
  AttachMoney as MoneyIcon,
  People as PeopleIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { API_ENDPOINTS, getAuthHeader } from '../config';

const StatCard = ({ title, value, icon: Icon, color }) => (
  <Paper
    sx={{
      p: 3,
      display: 'flex',
      flexDirection: 'column',
      height: '100%',
      backgroundColor: (theme) =>
        theme.palette.mode === 'dark' ? theme.palette.grey[800] : theme.palette.grey[100],
      '&:hover': {
        boxShadow: 3,
        transform: 'translateY(-4px)',
        transition: 'all 0.3s ease-in-out',
      },
    }}
    elevation={2}
  >
    <Box display="flex" justifyContent="space-between" alignItems="center">
      <Box>
        <Typography variant="h6" color="textSecondary" gutterBottom>
          {title}
        </Typography>
        <Typography variant="h4" component="div">
          {value}
        </Typography>
      </Box>
      <Icon
        sx={{
          fontSize: 48,
          color: color || 'primary.main',
          opacity: 0.8,
        }}
      />
    </Box>
  </Paper>
);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalTickets: 0,
    totalPaidTickets: 0,
    totalRevenue: 0,
    pendingTickets: 0,
    refundedTickets: 0,
    failedTickets: 0,
  });

  // State cho danh sách vé và phân trang
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalRecords, setTotalRecords] = useState(0);
  const [pageSize, setPageSize] = useState(10);

  useEffect(() => {
    fetchStatsAndTickets();
  }, [currentPage, pageSize]);

  const fetchStatsAndTickets = async () => {
    try {
      setLoading(true);

      // Gọi API lấy danh sách vé với phân trang
      const ticketsRes = await axios.get(
        `${API_ENDPOINTS.TICKETS.LIST}?page=${currentPage}&limit=${pageSize}`,
        getAuthHeader()
      );

      // Xử lý dữ liệu trả về từ API
      const ticketsData = ticketsRes.data.data?.tickets || ticketsRes.data.data || [];
      const paginationData = ticketsRes.data.data?.pagination || {};

      // Lấy toàn bộ danh sách vé từ response
      const tickets = Array.isArray(ticketsData) ? ticketsData : [];

      // Tính thống kê từ danh sách vé
      const totalTickets = paginationData.totalRecord || tickets.length;

      // Đếm số vé đã thanh toán
      const paidTickets = tickets.filter(ticket => ticket.paymentStatus === 'paid');
      const totalPaidTickets = paidTickets.length;

      // Tính tổng doanh thu từ vé đã thanh toán
      const revenue = paidTickets.reduce((sum, ticket) => {
        const pricePerSeat = ticket.price / (ticket.passengerCount || 1);
        return sum + (pricePerSeat * (ticket.passengerCount || 1));
      }, 0);

      // Đếm số vé theo trạng thái
      const pendingTickets = tickets.filter(ticket => ticket.paymentStatus === 'pending').length;
      const refundedTickets = tickets.filter(ticket => ticket.paymentStatus === 'refunded').length;
      const failedTickets = tickets.filter(ticket => ticket.paymentStatus === 'failed').length;

      setStats({
        totalTickets: totalTickets,
        totalPaidTickets: totalPaidTickets,
        totalRevenue: revenue,
        pendingTickets: pendingTickets,
        refundedTickets: refundedTickets,
        failedTickets: failedTickets,
      });

      setTickets(tickets);
      setTotalPages(paginationData.totalPages || Math.ceil(totalTickets / pageSize));
      setTotalRecords(totalTickets);

      setError('');
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Không thể tải dữ liệu');
      setTickets([]);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
    }).format(value);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusChipColor = (status) => {
    switch (status) {
      case 'paid': return 'success';
      case 'pending': return 'warning';
      case 'refunded': return 'info';
      case 'failed': return 'error';
      default: return 'default';
    }
  };

  const getStatusLabel = (status) => {
    switch (status) {
      case 'paid': return 'Đã thanh toán';
      case 'pending': return 'Chờ thanh toán';
      case 'refunded': return 'Đã hoàn tiền';
      case 'failed': return 'Thất bại';
      default: return status;
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  const handlePageSizeChange = (newSize) => {
    setPageSize(newSize);
    setCurrentPage(1); // Reset về trang đầu khi đổi kích thước trang
  };

  const handleDeleteTicket = async (ticketId) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa vé này?')) {
      return;
    }

    try {
      await axios.delete(`${API_ENDPOINTS.TICKETS.DETAIL(ticketId)}`, getAuthHeader());
      alert('Xóa vé thành công');
      // Refresh dữ liệu sau khi xóa
      fetchStatsAndTickets();
    } catch (err) {
      console.error('Lỗi khi xóa vé:', err);
      alert('Xóa vé thất bại');
    }
  };

  const statsCards = [
    { title: 'Tổng vé', value: stats.totalTickets, icon: TicketIcon, color: 'primary.main' },
    { title: 'Đã thanh toán', value: stats.totalPaidTickets, icon: MoneyIcon, color: 'success.main' },
    { title: 'Doanh thu', value: formatCurrency(stats.totalRevenue), icon: MoneyIcon, color: 'warning.main' },
    { title: 'Chờ thanh toán', value: stats.pendingTickets, icon: TicketIcon, color: 'warning.main' },
  ];

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Tổng quan
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3} sx={{ mb: 4 }}>
        {statsCards.map((stat, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <StatCard
              title={stat.title}
              value={stat.value}
              icon={stat.icon}
              color={stat.color}
            />
          </Grid>
        ))}
      </Grid>

      {/* Danh sách vé với phân trang */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Typography variant="h6">
            Danh sách vé ({totalRecords} vé)
          </Typography>
          <FormControl variant="outlined" size="small" sx={{ minWidth: 120 }}>
            <InputLabel>Số vé/trang</InputLabel>
            <Select
              value={pageSize}
              onChange={(e) => handlePageSizeChange(e.target.value)}
              label="Số vé/trang"
            >
              <MenuItem value={5}>5</MenuItem>
              <MenuItem value={10}>10</MenuItem>
              <MenuItem value={20}>20</MenuItem>
              <MenuItem value={50}>50</MenuItem>
            </Select>
          </FormControl>
        </Box>

        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>STT</TableCell>
                <TableCell>Mã vé</TableCell>
                <TableCell>Hành khách</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Chuyến bay</TableCell>
                <TableCell>Giá vé</TableCell>
                <TableCell>Trạng thái</TableCell>
                <TableCell>Ngày đặt</TableCell>
                <TableCell>Hành động</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {tickets.map((ticket, index) => (
                <TableRow key={ticket._id} hover>
                  <TableCell>{(currentPage - 1) * pageSize + index + 1}</TableCell>
                  <TableCell>{ticket.ticketCode}</TableCell>
                  <TableCell>{ticket.passengerName}</TableCell>
                  <TableCell>{ticket.email}</TableCell>
                  <TableCell>{ticket.flightId?.flightCode || 'N/A'}</TableCell>
                  <TableCell>{formatCurrency(ticket.price)}</TableCell>
                  <TableCell>
                    <Chip
                      label={getStatusLabel(ticket.paymentStatus)}
                      color={getStatusChipColor(ticket.paymentStatus)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>{formatDate(ticket.bookingDate)}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      color="error"
                      size="small"
                      onClick={() => handleDeleteTicket(ticket._id)}
                    >
                      Xóa
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Pagination Controls */}
        {totalPages > 1 && (
          <Box display="flex" justifyContent="center" alignItems="center" mt={3} gap={2}>
            <Button
              variant="outlined"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage <= 1}
            >
              Trang trước
            </Button>

            <Typography>
              Trang {currentPage} / {totalPages} (Tổng: {totalRecords} vé)
            </Typography>

            <Button
              variant="outlined"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage >= totalPages}
            >
              Trang sau
            </Button>
          </Box>
        )}
      </Paper>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Thống kê đặt vé
            </Typography>
            <Box sx={{ height: 300, backgroundColor: 'grey.100', borderRadius: 1 }}>
              <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                height="100%"
                color="text.secondary"
              >
                Biểu đồ thống kê sẽ hiển thị ở đây
              </Box>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Thống kê trạng thái vé
            </Typography>
            <Box>
              <Box sx={{
                p: 2,
                mb: 2,
                backgroundColor: 'success.light',
                borderRadius: 1,
                color: 'success.contrastText'
              }}>
                <Typography variant="subtitle2">
                  ✅ Đã thanh toán: {stats.totalPaidTickets}
                </Typography>
              </Box>
              <Box sx={{
                p: 2,
                mb: 2,
                backgroundColor: 'warning.light',
                borderRadius: 1,
                color: 'warning.contrastText'
              }}>
                <Typography variant="subtitle2">
                  ⏳ Chờ thanh toán: {stats.pendingTickets}
                </Typography>
              </Box>
              <Box sx={{
                p: 2,
                mb: 2,
                backgroundColor: 'info.light',
                borderRadius: 1,
                color: 'info.contrastText'
              }}>
                <Typography variant="subtitle2">
                  ↩ Đã hoàn tiền: {stats.refundedTickets}
                </Typography>
              </Box>
              <Box sx={{
                p: 2,
                backgroundColor: 'error.light',
                borderRadius: 1,
                color: 'error.contrastText'
              }}>
                <Typography variant="subtitle2">
                  ❌ Thất bại: {stats.failedTickets}
                </Typography>
              </Box>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
