import React from 'react';
import './SeatMap.css';

const SeatMap = ({ seats, selectedSeats, onSeatClick, seatTypes }) => {
  // Tạo sơ đồ ghế ngồi dựa trên dữ liệu seats
  const createSeatLayout = () => {
    if (!seats || seats.length === 0) {
      return null;
    }

    // Nhóm ghế theo hàng (row)
    const seatsByRow = seats.reduce((acc, seat) => {
      if (!acc[seat.row]) {
        acc[seat.row] = [];
      }
      acc[seat.row].push(seat);
      return acc;
    }, {});

    // Sắp xếp các hàng theo số thứ tự
    const sortedRows = Object.keys(seatsByRow).sort((a, b) => parseInt(a) - parseInt(b));

    return sortedRows.map(rowNum => {
      const rowSeats = seatsByRow[rowNum].sort((a, b) => {
        // Sắp xếp theo cột: A, B, C, D, E, F
        const colOrder = ['A', 'B', 'C', 'D', 'E', 'F'];
        return colOrder.indexOf(a.column) - colOrder.indexOf(b.column);
      });

      return (
        <div key={rowNum} className="seat-row">
          <div className="row-label">{rowNum}</div>
          <div className="seats-container">
            {rowSeats.map(seat => {
              const isSelected = selectedSeats.includes(seat.seatNumber);
              const isBooked = seat.status === 'booked';
              const isBlocked = seat.status === 'blocked';

              return (
                <div
                  key={seat.seatNumber}
                  className={`seat ${getSeatClass(seat.seatType)} ${isSelected ? 'selected' : ''} ${isBooked ? 'booked' : ''} ${isBlocked ? 'blocked' : ''}`}
                  onClick={() => !isBooked && !isBlocked && onSeatClick(seat.seatNumber)}
                  title={`${seat.seatNumber} - ${seat.seatType}`}
                >
                  {seat.seatNumber}
                </div>
              );
            })}
          </div>
        </div>
      );
    });
  };

  const getSeatClass = (seatType) => {
    switch (seatType) {
      case 'first':
        return 'first-class';
      case 'business':
        return 'business-class';
      default:
        return 'economy-class';
    }
  };

  const getSeatTypeColor = (seatType) => {
    switch (seatType) {
      case 'first':
        return '#FFD700'; // Gold
      case 'business':
        return '#87CEEB'; // Sky blue
      default:
        return '#90EE90'; // Light green
    }
  };

  const legendItems = seatTypes ? Object.entries(seatTypes).map(([type, count]) => (
    <div key={type} className="legend-item">
      <div
        className={`legend-color ${getSeatClass(type)}`}
        style={{ backgroundColor: getSeatTypeColor(type) }}
      ></div>
      <span>{type.charAt(0).toUpperCase() + type.slice(1)} ({count})</span>
    </div>
  )) : null;

  return (
    <div className="seat-map-container">
      <div className="seat-map-header">
        <h3>Chọn ghế ngồi</h3>
        <div className="seat-legend">
          <div className="legend-item">
            <div className="legend-color available"></div>
            <span>Còn trống</span>
          </div>
          <div className="legend-item">
            <div className="legend-color selected"></div>
            <span>Đã chọn</span>
          </div>
          <div className="legend-item">
            <div className="legend-color booked"></div>
            <span>Đã đặt</span>
          </div>
          {legendItems}
        </div>
      </div>

      <div className="seat-map">
        <div className="airplane-layout">
          <div className="seat-grid">
            {createSeatLayout()}
          </div>
        </div>
      </div>

      {selectedSeats.length > 0 && (
        <div className="selected-seats-info">
          <h4>Ghế đã chọn:</h4>
          <div className="selected-seats-list">
            {selectedSeats.map(seatNum => (
              <span key={seatNum} className="selected-seat-badge">
                {seatNum}
              </span>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default SeatMap;
