import React, { useState, useEffect } from 'react';
import './Wallet.css';
import WalletTopup from './WalletTopup';
import SetPinModal from './SetPinModal';
import WithdrawModal from './WithdrawModal';

const Wallet = ({ user }) => {
  const [walletInfo, setWalletInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [showTopupModal, setShowTopupModal] = useState(false);
  const [showSetPinModal, setShowSetPinModal] = useState(false);
  const [showWithdrawModal, setShowWithdrawModal] = useState(false);

  useEffect(() => {
    fetchWalletInfo();
  }, [user]);

  const fetchWalletInfo = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/wallet', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setWalletInfo(data.data);
      }
    } catch (error) {
      console.error('L·ªói l·∫•y th√¥ng tin v√≠:', error);
    } finally {
      setLoading(false);
    }
  };

  const getWalletLevelColor = (level) => {
    switch (level) {
      case 'diamond': return '#E8E8E8';
      case 'gold': return '#FFD700';
      case 'silver': return '#C0C0C0';
      case 'bronze': return '#CD7F32';
      default: return '#CD7F32';
    }
  };

  const getWalletLevelIcon = (level) => {
    switch (level) {
      case 'diamond': return 'üíé';
      case 'gold': return 'ü•á';
      case 'silver': return 'ü•à';
      case 'bronze': return 'ü•â';
      default: return 'ü•â';
    }
  };

  const getWalletLevelName = (level) => {
    switch (level) {
      case 'diamond': return 'Kim c∆∞∆°ng';
      case 'gold': return 'V√†ng';
      case 'silver': return 'B·∫°c';
      case 'bronze': return 'ƒê·ªìng';
      default: return 'ƒê·ªìng';
    }
  };

  const getNextLevelThreshold = (currentLevel) => {
    switch (currentLevel) {
      case 'bronze': return 10000000; // 10M
      case 'silver': return 50000000; // 50M
      case 'gold': return 100000000; // 100M
      case 'diamond': return Infinity;
      default: return 10000000;
    }
  };

  const calculateLevelProgress = (userData) => {
    if (!userData?.totalSpentInWallet || !userData?.walletLevel) return 0;

    const spent = userData.totalSpentInWallet;
    const currentLevel = userData.walletLevel;
    const currentThreshold = getCurrentLevelMin(currentLevel);
    const nextThreshold = getNextLevelThreshold(currentLevel);

    if (currentLevel === 'diamond' || nextThreshold === Infinity) return 100;

    return Math.min((spent - currentThreshold) / (nextThreshold - currentThreshold) * 100, 100);
  };

  const getCurrentLevelMin = (level) => {
    switch (level) {
      case 'bronze': return 0;
      case 'silver': return 10000000;
      case 'gold': return 50000000;
      case 'diamond': return 100000000;
      default: return 0;
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
  };

  if (loading) {
    return (
      <div className="wallet-container">
        <div className="loading">ƒêang t·∫£i th√¥ng tin v√≠...</div>
      </div>
    );
  }

  return (
    <div className="wallet-container">
      <div className="wallet-header">
        <h2>üëõ V√≠ ƒëi·ªán t·ª≠</h2>
        <div className="wallet-actions">
          <button
            className="btn-primary"
            onClick={() => setShowTopupModal(true)}
          >
            üí∞ N·∫°p ti·ªÅn
          </button>
          <button
            className="btn-secondary"
            onClick={() => setShowWithdrawModal(true)}
          >
            üí∏ R√∫t ti·ªÅn
          </button>
        </div>
      </div>

      {/* Wallet Overview Cards */}
      <div className="wallet-overview">
        <div className="wallet-balance-card">
          <div className="balance-header">
            <h3>S·ªë d∆∞ v√≠</h3>
            <span className="wallet-status">
              {walletInfo?.isWalletActive ? '‚úÖ Ho·∫°t ƒë·ªông' : '‚ùå T·∫°m kh√≥a'}
            </span>
