import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminSeatManagement.css';

const AdminSeatManagement = () => {
  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [seatLayout, setSeatLayout] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFlights = async () => {
      const token = localStorage.getItem('accessToken');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await fetch('http://localhost:8080/api/v1/flights', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error('Không có quyền truy cập');
        }

        const data = await response.json();
        setFlights(data.data || []);
      } catch (err) {
        setError(err.message);
        if (err.message === 'Không có quyền truy cập') {
          navigate('/');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchFlights();
  }, [navigate]);

  const fetchSeatLayout = async (flightId) => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:8080/api/v1/seat-layout/flights/${flightId}/seats`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setSeatLayout(data.data);
      } else {
        setError('Không thể tải sơ đồ ghế');
      }
    } catch (error) {
      console.error('Lỗi tải sơ đồ ghế:', error);
      setError('Lỗi kết nối server');
    } finally {
      setLoading(false);
    }
  };

  const handleFlightSelect = (flight) => {
    setSelectedFlight(flight);
    fetchSeatLayout(flight._id);
  };

  const getSeatStatusColor = (status) => {
    switch (status) {
      case 'available':
        return '#10B981'; // Green for available
      case 'booked':
        return '#EF4444'; // Red for booked
      case 'blocked':
        return '#F59E0B'; // Yellow for blocked
      default:
        return '#6B7280'; // Gray for unknown
    }
  };

  const getSeatStatusText = (status) => {
    switch (status) {
      case 'available':
        return 'Trống';
      case 'booked':
        return 'Đã đặt';
      case 'blocked':
        return 'Khóa';
      default:
        return 'Không xác định';
    }
  };

  if (loading && !selectedFlight) {
    return <div className="loading">Đang tải...</div>;
  }

  if (error && !selectedFlight) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="admin-seat-management">
      <div className="admin-header">
        <h1>Quản lý ghế ngồi</h1>
        <button onClick={() => navigate('/admin')} className="back-btn">
          ← Quay lại dashboard
        </button>
      </div>

      <div className="admin-content">
        {/* Chọn chuyến bay */}
        <div className="flight-selection">
          <h3>Chọn chuyến bay</h3>
          <div className="flight-list">
            {flights.map((flight) => (
              <div
                key={flight._id}
                className={`flight-card ${selectedFlight?._id === flight._id ? 'selected' : ''}`}
                onClick={() => handleFlightSelect(flight)}
              >
                <div className="flight-info">
                  <strong>{flight.flightCode}</strong>
                  <span>{flight.route}</span>
                  <span>Ngày: {new Date(flight.departureTime).toLocaleDateString('vi-VN')}</span>
                  <span>Ghế còn: {flight.availableSeats}/{flight.totalSeats}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Hiển thị sơ đồ ghế */}
        {selectedFlight && seatLayout && (
          <div className="seat-layout-admin">
            <div className="flight-details-admin">
              <h3>Sơ đồ ghế - {selectedFlight.flightCode}</h3>
              <div className="flight-stats">
                <div className="stat-item">
                  <span className="stat-label">Tổng ghế:</span>
                  <span className="stat-value">{seatLayout.totalSeats}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Ghế trống:</span>
                  <span className="stat-value available">
                    {seatLayout.layout.reduce((total, row) =>
                      total + row.seats.filter(seat => seat.status === 'available').length, 0
                    )}
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Ghế đã đặt:</span>
                  <span className="stat-value booked">
                    {seatLayout.layout.reduce((total, row) =>
                      total + row.seats.filter(seat => seat.status === 'booked').length, 0
                    )}
                  </span>
                </div>
              </div>
            </div>

            <div className="seat-grid-admin">
              {seatLayout.layout.map((row) => (
                <div key={row.row} className="seat-row-admin">
                  <div className="row-number-admin">{row.row}</div>

                  <div className="seats-container-admin">
                    {row.seats.map((seat) => (
                      <div
                        key={seat.seatId}
                        className={`seat-admin ${seat.status}`}
                        style={{
                          backgroundColor: getSeatStatusColor(seat.status),
                          border: '1px solid #333'
                        }}
                        title={`${seat.seatId} - ${seat.seatClass} - ${getSeatStatusText(seat.status)}`}
                      >
                        <div className="seat-id-admin">{seat.seatId}</div>
                        <div className="seat-class-admin">
                          {seat.seatClass === 'first' ? 'H1' :
                           seat.seatClass === 'business' ? 'TG' : 'PT'}
                        </div>
                        <div className="seat-status-admin">
                          {seat.status === 'booked' && seat.ticketId ? 'Đặt' : getSeatStatusText(seat.status)}
                        </div>
                      </div>
                    ))}
                  </div>

                  <div className="row-number-admin">{row.row}</div>
                </div>
              ))}
            </div>

            {/* Legend */}
            <div className="seat-legend-admin">
              <div className="legend-item-admin">
                <div className="legend-color-admin available"></div>
                <span>Ghế trống</span>
              </div>
              <div className="legend-item-admin">
                <div className="legend-color-admin booked"></div>
                <span>Ghế đã đặt</span>
              </div>
              <div className="legend-item-admin">
                <div className="legend-color-admin blocked"></div>
                <span>Ghế bị khóa</span>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminSeatManagement;
