import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import BannerSlider from "./BannerSlider";
import Login from "./pages/login";
import Register from "./pages/Register";
import Profile from "./pages/Profile";
import Booking from "./pages/Booking";
import Success from "./pages/Success";
import Payment from "./pages/Payment";
import TicketDetail from "./pages/TicketDetail";
import Verification from "./pages/Verification";
import "./pages/TicketDetail.css";

// Home component
function Home({ user, setUser }) {
  const navigate = useNavigate();
  const [flights, setFlights] = useState([]);
  const [showUserMenu, setShowUserMenu] = useState(false);

  // Hàm clear kết quả tìm kiếm
  const handleClearResults = () => {
    setFlights([]);
  };
  const handleSearch = async (e) => {
    e.preventDefault();

    // Lấy dữ liệu từ form
    const formData = new FormData(e.target);
    const from = formData.get('from')?.trim().toLowerCase();
    const to = formData.get('to')?.trim().toLowerCase();

    if (!from || !to) {
      alert('Vui lòng nhập nơi đi và nơi đến!');
      return;
    }

    try {
      // Tạo query string cho tìm kiếm
      const queryParams = new URLSearchParams({
        route: `${from} - ${to}`,
        limit: '50' // Lấy nhiều kết quả để tìm kiếm
      });

      const response = await fetch(`http://localhost:8080/api/v1/flights?${queryParams}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      // Lọc và sắp xếp kết quả phù hợp nhất
      let searchResults = data.data?.flights || [];

      if (searchResults.length === 0) {
        // Không tìm thấy kết quả, thử tìm kiếm gần đúng
        const fuzzyQueryParams = new URLSearchParams({
          route: `${from}|${to}`,
          limit: '20'
        });

        const fuzzyResponse = await fetch(`http://localhost:8080/api/v1/flights?${fuzzyQueryParams}`);
        const fuzzyData = await fuzzyResponse.json();
        searchResults = fuzzyData.data?.flights || [];

        if (searchResults.length === 0) {
          setFlights([]);
          return;
        }
      }

      setFlights(searchResults);
    } catch (error) {
      console.error('Lỗi khi tìm kiếm chuyến bay:', error);
      alert('Không thể tìm kiếm chuyến bay. Vui lòng thử lại sau!');
      setFlights([]);
    }
  };

  // Đóng dropdown khi click bên ngoài
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showUserMenu && !event.target.closest('.user-menu')) {
        setShowUserMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showUserMenu]);

  // Hàm xử lý đặt vé
  const handleBooking = (flight) => {
    if (!user) {
      alert('Vui lòng đăng nhập để đặt vé!');
      return;
    }

    // Điều hướng đến trang đặt vé với flightId
    window.location.href = `/booking/${flight._id}`;
  };

  // Hàm xử lý đăng xuất
  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    setUser(null);
  };

  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <h2 className="logo">✈️ FlightBooking</h2>
        </div>
        <div className="nav">
          <button className="nav-btn">Flights</button>
          <button className="nav-btn">Hotels</button>
          {user ? (
            <div className="user-menu">
              <div
                className="user-info"
                onClick={() => setShowUserMenu(!showUserMenu)}
              >
                <div className="user-avatar">
                  {user.fullName.charAt(0).toUpperCase()}
                </div>
                <div className="user-details">
                  <span className="username">{user.fullName}</span>
                  <span className="user-email">{user.email}</span>
                </div>
                <span className="dropdown-arrow">▼</span>
              </div>

              {showUserMenu && (
                <div className={`dropdown-menu ${showUserMenu ? 'show' : ''}`}>
                  <button onClick={() => {
                    navigate('/profile');
                    setShowUserMenu(false);
                  }}>
                    <span className="icon">👤</span>
                    Hồ sơ cá nhân
                  </button>
                  <button onClick={() => {
                    handleLogout();
                    setShowUserMenu(false);
                  }}>
                    <span className="icon">🚪</span>
                    Đăng xuất
                  </button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login">
              <button className="login-btn">Đăng nhập</button>
            </Link>
          )}
        </div>
      </header>

      {/* Banner */}
      <BannerSlider />

      {/* Search Box */}
      <section className="search-box">
        <div className="search-container">
          <h3 className="search-title">Tìm chuyến bay giá tốt nhất</h3>
          <form className="search-form" onSubmit={handleSearch}>
            <div className="form-group route-inputs">
              <div className="input-wrapper">
                <label className="input-label">Từ</label>
                <input name="from" type="text" placeholder="Thành phố khởi hành" required />
              </div>
              <div className="swap-icon">⇄</div>
              <div className="input-wrapper">
                <label className="input-label">Đến</label>
                <input name="to" type="text" placeholder="Thành phố đích" required />
              </div>
            </div>

            <div className="form-group date-passenger">
              <div className="input-wrapper date-input">
                <label className="input-label">Ngày đi</label>
                <input type="date" required />
              </div>

              <div className="input-wrapper passenger-input">
                <label className="input-label">Hành khách</label>
                <select name="passengers" defaultValue="1">
                  <option value="1">1 hành khách</option>
                  <option value="2">2 hành khách</option>
                  <option value="3">3 hành khách</option>
                  <option value="4">4 hành khách</option>
                  <option value="5">5 hành khách</option>
                </select>
              </div>

              <div className="input-wrapper class-input">
                <label className="input-label">Hạng ghế</label>
                <select name="class">
                  <option value="economy">Economy</option>
                  <option value="business">Business</option>
                  <option value="first">First Class</option>
                </select>
              </div>
            </div>

            <button type="submit" className="btn-search">
              <span className="search-icon">🔍</span>
              Tìm chuyến bay
            </button>
          </form>
        </div>
      </section>

      {/* Flight Results */}
      <section className="results">
        <div className="results-header">
          <div className="results-info">
            <h3>Kết quả tìm kiếm</h3>
            {flights.length > 0 && (
              <p className="results-count">Tìm thấy {flights.length} chuyến bay</p>
            )}
          </div>
          <div className="results-actions">
            <select className="sort-select">
              <option value="price">Sắp xếp theo giá</option>
              <option value="time">Sắp xếp theo thời gian</option>
              <option value="duration">Sắp xếp theo thời lượng</option>
            </select>
            <button className="filter-btn">🔧 Bộ lọc</button>
            {flights.length > 0 && (
              <button className="clear-btn" onClick={handleClearResults}>
                Tìm kiếm mới
              </button>
            )}
          </div>
        </div>

        {flights.length > 0 ? (
          <div className="flight-list">
            {flights.map((flight) => (
              <div key={flight._id} className="flight-card">
                <div className="flight-info">
                  <div className="flight-route">
                    <div className="flight-time">
                      <span className="time">{new Date(flight.departureTime).toLocaleTimeString('vi-VN', { hour: '2-digit', minute: '2-digit' })}</span>
                      <span className="airport">{flight.route.split(' - ')[0]}</span>
                    </div>
                    <div className="flight-duration">
                      <div className="duration-line">
                        <div className="duration-dot"></div>
                        <div className="duration-text">
                          {Math.floor((new Date(flight.arrivalTime) - new Date(flight.departureTime)) / (1000 * 60 * 60))}h {Math.floor(((new Date(flight.arrivalTime) - new Date(flight.departureTime)) % (1000 * 60 * 60)) / (1000 * 60))}m
                        </div>
                        <div className="duration-dot"></div>
                      </div>
                    </div>
                    <div className="flight-time">
                      <span className="time">{new Date(flight.arrivalTime).toLocaleTimeString('vi-VN', { hour: '2-digit', minute: '2-digit' })}</span>
                      <span className="airport">{flight.route.split(' - ')[1]}</span>
                    </div>
                  </div>

                  <div className="flight-details">
                    <div className="flight-code">{flight.flightCode}</div>
                    <div className="flight-date">{new Date(flight.departureTime).toLocaleDateString('vi-VN')}</div>
                    <div className="seats-left">{flight.availableSeats} ghế trống</div>
                  </div>
                </div>

                <div className="flight-pricing">
                  <div className="price-options">
                    {flight.economyPrice && (
                      <div className="price-option">
                        <span className="price-class">Economy</span>
                        <span className="price-value">
                          {new Intl.NumberFormat('vi-VN', {
                            style: 'currency',
                            currency: 'VND'
                          }).format(flight.economyPrice)}
                        </span>
                      </div>
                    )}
                    {flight.businessPrice && (
                      <div className="price-option">
                        <span className="price-class">Business</span>
                        <span className="price-value">
                          {new Intl.NumberFormat('vi-VN', {
                            style: 'currency',
                            currency: 'VND'
                          }).format(flight.businessPrice)}
                        </span>
                      </div>
                    )}
                    {flight.firstClassPrice && (
                      <div className="price-option">
                        <span className="price-class">First Class</span>
                        <span className="price-value">
                          {new Intl.NumberFormat('vi-VN', {
                            style: 'currency',
                            currency: 'VND'
                          }).format(flight.firstClassPrice)}
                        </span>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flight-actions">
                  <button
                    className="btn-book"
                    onClick={() => handleBooking(flight)}
                    disabled={!user}
                  >
                    {user ? 'Đặt ngay' : 'Đăng nhập'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">✈️</div>
            <h3>Chưa có kết quả tìm kiếm</h3>
            <p>Nhập thông tin chuyến bay để bắt đầu tìm kiếm</p>
            <div className="search-tips">
              <h4>💡 Mẹo tìm kiếm:</h4>
              <ul>
                <li>Nhập tên thành phố chính xác (VD: Hà Nội, TP.HCM, Đà Nẵng)</li>
                <li>Kiểm tra ngày đi và số lượng hành khách</li>
                <li>Thử tìm kiếm với các ngày gần đó nếu không có kết quả</li>
              </ul>
            </div>
          </div>
        )}
      </section>

      {/* Promo Section */}
      <section className="promo-section">
        <h3>✈️ Dịch vụ nổi bật</h3>
        <div className="promo-grid">
          <div className="promo-card">
             <p>Ưu đãi vé máy bay giá rẻ</p>
          </div>
          <div className="promo-card">
            <p>Khách sạn tiện nghi</p>
          </div>
          <div className="promo-card">
            <p>Bảo hiểm du lịch an toàn</p>
          </div>
          <div className="promo-card">
            <p>Thuê xe du lịch</p>
          </div>
        </div>
      </section>

      {/* Promo Flights Section */}
<section className="promo-flights">
  <h3>✈️ Ưu đãi vé máy bay dành cho bạn!</h3>

  {/* Promo Banners */}
  <div className="promo-banners">
    <div className="banner-card">
      <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRj6m2XYpqNmSmMYvG2YK4jtEr_c2tSMCGm0w&s" alt="Promo 1" />
      <p>10.10 Travel Sale - Giảm đến 30%</p>
    </div>
    <div className="banner-card">
      <img src="https://ik.imagekit.io/tvlk/image/imageResource/2025/09/11/1757555758021-be0e2d169a3d27772f10080585eaeaca.jpeg?tr=q-75" alt="Promo 2" />
      <p>Quà tặng 10.000 voucher hấp dẫn</p>
    </div>
  </div>

  {/* New User Promo Codes */}
  <div className="promo-codes">
    <h4>Mã ưu đãi tặng bạn mới</h4>
    <div className="codes-grid">
      <div className="code-card">
        <p>Giảm ngay 50K</p>
        <button onClick={() => navigator.clipboard.writeText("TVLKBANMOI")}>Copy</button>
      </div>
      <div className="code-card">
        <p>8% giảm giá khách sạn</p>
        <button onClick={() => navigator.clipboard.writeText("TVLKBANMOI")}>Copy</button>
      </div>
      <div className="code-card">
        <p>8% giảm hoạt động du lịch</p>
        <button onClick={() => navigator.clipboard.writeText("TVLKBANMOI")}>Copy</button>
      </div>
    </div>
  </div>

  {/* Flight Deals */}
<div className="flight-deals">
  <h4>Vé máy bay nội địa giá tốt nhất</h4>

  {/* Tabs chọn thành phố */}
