// App component
function App() {
  const [user, setUser] = useState(null);

  // Hàm lấy thông tin user từ token
  const refreshUser = async () => {
    const token = localStorage.getItem("accessToken");
    if (!token) {
      setUser(null);
      return;
    }

    try {
      console.log('🔄 Đang lấy thông tin user từ API...');
      const res = await fetch("http://localhost:8080/api/v1/auth/get-profile", {
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json"
        },
      });

      if (!res.ok) {
        if (res.status === 401) {
          console.log('❌ Token hết hạn, đăng xuất...');
          localStorage.removeItem("accessToken");
          setUser(null);
        }
        return;
      }

      const data = await res.json();
      if (data.data) {
        console.log('✅ Đã lấy thông tin user:', data.data.fullName);
        setUser(data.data);
      }
    } catch (err) {
      console.error("❌ Lỗi khi lấy thông tin người dùng:", err);
      // Không xóa token nếu chỉ là lỗi mạng
      // localStorage.removeItem("accessToken");
      setUser(null);
    }
  };

  // Lấy profile khi load app
  useEffect(() => {
    refreshUser();
  }, []);

  // Hàm xử lý đặt vé - CHỈ KIỂM TRA TOKEN
  const handleBooking = (flight) => {
    const token = localStorage.getItem("accessToken");

    if (!token) {
      alert('Vui lòng đăng nhập để đặt vé!');
      window.location.href = '/login';
      return;
    }

    // Nếu có token → Cho phép đặt vé ngay lập tức
    console.log('✅ Đã đăng nhập - Cho phép đặt vé:', flight.flightCode);
    window.location.href = `/booking/${flight._id}`;
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home user={user} setUser={setUser} />} />
        <Route path="/login" element={<Login setUser={setUser} refreshUser={refreshUser} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/profile" element={<Profile user={user} setUser={setUser} />} />
        <Route path="/booking/:flightId" element={<Booking user={user} />} />
        <Route path="/verification" element={<Verification setUser={setUser} refreshUser={refreshUser} />} />
      </Routes>
    </Router>
  );
}

export default App;
