import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import './Payment.css';

// Kh·ªüi t·∫°o Stripe v·ªõi publishable key t·ª´ backend
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || 'pk_test_your_publishable_key_here');

// Ki·ªÉm tra v√† log c·∫•u h√¨nh Stripe
console.log('üîß Stripe Configuration:', {
  publishableKey: process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY ? '***configured***' : 'NOT SET',
  apiUrl: process.env.REACT_APP_API_URL
});

// Form x·ª≠ l√Ω thanh to√°n th·ª±c t·∫ø v·ªõi Stripe
const CheckoutForm = ({
  amount,
  currency,
  ticketId,
  onPaymentSuccess,
  onPaymentError
}) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [clientSecret, setClientSecret] = useState('');
  const [error, setError] = useState('');

  // T·∫°o Payment Intent khi component mount
  useEffect(() => {
    const createPaymentIntent = async () => {
      try {
        console.log('üí≥ B·∫Øt ƒë·∫ßu t·∫°o Payment Intent...');
        console.log('üìã Th√¥ng tin thanh to√°n:', {
          ticketId,
          amount,
          currency,
          hasToken: !!localStorage.getItem('accessToken')
        });

        const token = localStorage.getItem('accessToken');
        if (!token) {
          setError('Kh√¥ng t√¨m th·∫•y token x√°c th·ª±c. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.');
          return;
        }

        const response = await fetch('http://localhost:8080/api/v1/payments/create-payment-intent', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({
            ticketId: ticketId,
            amount: amount,
            currency: currency
          }),
        });

        console.log('üîó Response status:', response.status);

        if (response.ok) {
          const data = await response.json();
          console.log('‚úÖ Payment Intent t·∫°o th√†nh c√¥ng:', {
            hasClientSecret: !!data.data?.clientSecret,
            clientSecret: data.data?.clientSecret ? '***received***' : 'NOT RECEIVED'
          });

          if (data.data?.clientSecret) {
            setClientSecret(data.data.clientSecret);
            setError('');
          } else {
            setError('Kh√¥ng nh·∫≠n ƒë∆∞·ª£c client secret t·ª´ server');
          }
        } else {
          const errorData = await response.json();
          console.error('‚ùå L·ªói t·∫°o Payment Intent:', errorData);
          setError(errorData.message || 'Kh√¥ng th·ªÉ t·∫°o payment intent');
        }
      } catch (error) {
        console.error('‚ùå L·ªói k·∫øt n·ªëi server:', error);
        setError('L·ªói k·∫øt n·ªëi server. Vui l√≤ng ki·ªÉm tra k·∫øt n·ªëi m·∫°ng.');
      }
    };

    if (ticketId && amount && ticketId !== 'undefined' && amount > 0) {
      createPaymentIntent();
    } else {
      console.warn('‚ö†Ô∏è Thi·∫øu th√¥ng tin thanh to√°n:', { ticketId, amount });
      setError('Thi·∫øu th√¥ng tin v√© ho·∫∑c s·ªë ti·ªÅn kh√¥ng h·ª£p l·ªá');
    }
  }, [ticketId, amount, currency]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      setError('Stripe ch∆∞a ƒë∆∞·ª£c kh·ªüi t·∫°o');
      return;
    }

    if (!clientSecret) {
      setError('Ch∆∞a nh·∫≠n ƒë∆∞·ª£c client secret t·ª´ server');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const cardElement = elements.getElement(CardElement);

      if (!cardElement) {
        setError('Kh√¥ng t√¨m th·∫•y form th·∫ª t√≠n d·ª•ng');
        setLoading(false);
        return;
      }

      // X√°c nh·∫≠n thanh to√°n v·ªõi Stripe
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
          billing_details: {
            name: event.target.name?.value || 'Kh√°ch h√†ng',
          },
        }
      });

      if (error) {
        console.error('L·ªói thanh to√°n:', error);
        setError(error.message || 'Thanh to√°n th·∫•t b·∫°i');
      } else if (paymentIntent.status === 'succeeded') {
        // G·ªçi API x√°c nh·∫≠n thanh to√°n v·ªõi backend
        const token = localStorage.getItem('accessToken');
        const confirmResponse = await fetch('http://localhost:8080/api/v1/payments/confirm-payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({
            paymentIntentId: paymentIntent.id
          }),
        });

        if (confirmResponse.ok) {
          onPaymentSuccess(paymentIntent.id);
        } else {
          const errorData = await confirmResponse.json();
          setError(errorData.message || 'Kh√¥ng th·ªÉ x√°c nh·∫≠n thanh to√°n');
        }
      } else {
        setError('Thanh to√°n kh√¥ng th√†nh c√¥ng');
      }
    } catch (error) {
      console.error('L·ªói x·ª≠ l√Ω thanh to√°n:', error);
      setError('L·ªói x·ª≠ l√Ω thanh to√°n');
    }

    setLoading(false);
  };

  const cardStyle = {
    style: {
      base: {
        color: '#32325d',
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: 'antialiased',
        fontSize: '16px',
        '::placeholder': {
          color: '#aab7c4'
        }
      },
      invalid: {
        color: '#fa755a',
        iconColor: '#fa755a'
      }
    }
  };

  return (
    <div className="payment-form">
      <div className="payment-amount">
        <h3>T·ªïng ti·ªÅn: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}</h3>
        <p className="currency-note">Thanh to√°n an to√†n v·ªõi Stripe</p>
      </div>

      {error && (
        <div className="error-message" style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '10px',
          borderRadius: '4px',
          marginBottom: '15px',
          border: '1px solid #f5c6cb'
        }}>
          <strong>L·ªói:</strong> {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="card-name">T√™n ch·ªß th·∫ª</label>
          <input
            type="text"
            id="card-name"
            name="name"
            placeholder="NGUYEN VAN A"
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Th√¥ng tin th·∫ª</label>
          <div className="card-element-container" style={{
            padding: '12px',
            border: '2px solid #e1e5e9',
            borderRadius: '6px',
            background: loading ? '#f8f9fa' : '#fff',
            minHeight: '40px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            {stripe && elements ? (
              <CardElement
                options={{
                  style: {
                    base: {
                      fontSize: '16px',
                      color: '#424770',
                      '::placeholder': {
                        color: '#aab7c4',
                      },
                    },
                  },
                }}
              />
            ) : (
              <div style={{ color: '#666', textAlign: 'center' }}>
                {loading ? '‚è≥ ƒêang t·∫£i...' : '‚è≥ Chu·∫©n b·ªã form thanh to√°n...'}
              </div>
            )}
          </div>
        </div>

        <button
          type="submit"
          disabled={!stripe || !clientSecret || loading}
          className="payment-button"
          style={{
            width: '100%',
            padding: '15px',
            background: (!stripe || !clientSecret || loading) ? '#6c757d' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontSize: '16px',
            fontWeight: '600',
            cursor: (!stripe || !clientSecret || loading) ? 'not-allowed' : 'pointer',
            marginTop: '20px'
          }}
        >
          {loading ? 'ƒêang x·ª≠ l√Ω...' : `Thanh to√°n ${new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}`}
        </button>
      </form>

      <div className="payment-notice">
        <p>üîí Thanh to√°n ƒë∆∞·ª£c b·∫£o m·∫≠t b·ªüi Stripe</p>
        <small>Th√¥ng tin th·∫ª ƒë∆∞·ª£c m√£ h√≥a v√† b·∫£o v·ªá</small>
      </div>
    </div>
  );
};

// Wrapper component v·ªõi Stripe Elements
