import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      const token = localStorage.getItem('accessToken');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await fetch('http://localhost:8080/api/v1/users', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error('Không có quyền truy cập');
        }

        const data = await response.json();
        setUsers(data.data);
      } catch (err) {
        setError(err.message);
        if (err.message === 'Không có quyền truy cập') {
          navigate('/');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, [navigate]);

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa người dùng này?')) {
      return;
    }

    const token = localStorage.getItem('accessToken');
    try {
      const response = await fetch(`http://localhost:8080/api/v1/users/${userId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Xóa người dùng thất bại');
      }

      // Cập nhật danh sách người dùng sau khi xóa
      setUsers(users.filter(user => user._id !== userId));
      alert('Xóa người dùng thành công');
    } catch (err) {
      console.error('Lỗi khi xóa người dùng:', err);
      alert(err.message);
    }
  };

  if (loading) {
    return <div className="loading">Đang tải...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h1>Trang quản trị</h1>
      </div>

      <div className="admin-menu">
        <div className="menu-item" onClick={() => navigate('/admin/seats')}>
          <div className="menu-icon">💺</div>
          <div className="menu-content">
            <h3>Quản lý ghế ngồi</h3>
            <p>Xem và quản lý trạng thái ghế ngồi của các chuyến bay</p>
          </div>
        </div>

        <div className="menu-item" onClick={() => navigate('/admin/users')}>
          <div className="menu-icon">👥</div>
          <div className="menu-content">
            <h3>Quản lý người dùng</h3>
            <p>Xem và quản lý thông tin người dùng hệ thống</p>
          </div>
        </div>
      </div>

      <div className="admin-content">
        <h2>Quản lý người dùng</h2>
        <div className="user-list">
          <table>
            <thead>
              <tr>
                <th>STT</th>
                <th>Họ tên</th>
                <th>Email</th>
                <th>Số điện thoại</th>
                <th>Vai trò</th>
                <th>Trạng thái</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr key={user._id}>
                  <td>{index + 1}</td>
                  <td>{user.fullName}</td>
                  <td>{user.email}</td>
                  <td>{user.phoneNumber}</td>
                  <td>{user.role}</td>
                  <td>{user.isActive ? 'Hoạt động' : 'Bị khóa'}</td>
                  <td>
                    <button
                      className="btn-delete"
                      onClick={() => handleDeleteUser(user._id)}
                    >
                      Xóa
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
