import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const DatabaseTest = () => {
  const [user, setUser] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    checkConnection();
  }, []);

  const checkConnection = async () => {
    try {
      // Kiểm tra kết nối cơ sở dữ liệu
      console.log('=== KIỂM TRA KẾT NỐI CƠ SỞ DỮ LIỆU ===');

      // 1. Kiểm tra thông tin user hiện tại
      const token = localStorage.getItem('accessToken');
      console.log('Token hiện tại:', token);

      if (token) {
        const userResponse = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          setUser(userData.data);
          console.log('Thông tin user:', userData.data);
        }
      }

      // 2. Kiểm tra danh sách vé
      if (token) {
        const ticketsResponse = await fetch(`http://localhost:8080/api/v1/tickets?userId=${user?._id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (ticketsResponse.ok) {
          const ticketsData = await ticketsResponse.json();
          console.log('Dữ liệu vé trả về:', ticketsData);
          setTickets(ticketsData.tickets || []);
        } else {
          console.error('Lỗi lấy vé:', ticketsResponse.status);
        }
      }

      // 3. Kiểm tra danh sách chuyến bay
      const flightsResponse = await fetch('http://localhost:8080/api/v1/flights?limit=5', {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (flightsResponse.ok) {
        const flightsData = await flightsResponse.json();
        console.log('Dữ liệu chuyến bay:', flightsData);
        setFlights(flightsData.data?.flights || []);
      }

    } catch (error) {
      console.error('Lỗi kết nối:', error);
    } finally {
      setLoading(false);
    }
  };

  const testCreateTicket = async () => {
    if (!user) {
      alert('Vui lòng đăng nhập trước');
      return;
    }

    try {
      const testTicketData = {
        flightCode: 'TEST001',
        seatNumber: 'A1',
        price: 100000,
        passengerCount: 1,
        passengerName: user.fullName || 'Test User',
        email: user.email,
        phoneNumber: user.phoneNumber || '0123456789',
        userId: user._id
      };

      console.log('Tạo vé test với dữ liệu:', testTicketData);

      const response = await fetch('http://localhost:8080/api/v1/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        },
        body: JSON.stringify(testTicketData)
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Vé test được tạo:', result);
        alert('Vé test đã được tạo thành công!');
        checkConnection(); // Refresh dữ liệu
      } else {
        const error = await response.json();
        console.error('Lỗi tạo vé test:', error);
        alert(`Lỗi tạo vé test: ${error.message}`);
      }
    } catch (error) {
      console.error('Lỗi tạo vé test:', error);
      alert('Không thể tạo vé test');
    }
  };

  if (loading) {
    return (
      <div style={{ padding: '50px', textAlign: 'center' }}>
        <h2>Đang kiểm tra kết nối cơ sở dữ liệu...</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <div style={{ marginBottom: '30px', textAlign: 'center' }}>
        <h1>🗄️ Kiểm tra kết nối cơ sở dữ liệu</h1>
        <button onClick={() => navigate('/')} style={{ marginRight: '10px' }}>
          ← Về trang chủ
        </button>
        <button onClick={checkConnection} style={{ marginRight: '10px' }}>
          🔄 Kiểm tra lại
        </button>
        <button onClick={testCreateTicket} style={{ background: '#28a745', color: 'white' }}>
          🧪 Tạo vé test
        </button>
      </div>

      {/* Thông tin user hiện tại */}
      <div style={{ marginBottom: '30px', padding: '20px', background: '#f8f9fa', borderRadius: '8px' }}>
        <h3>👤 Thông tin người dùng hiện tại</h3>
        {user ? (
          <div>
            <p><strong>ID:</strong> {user._id}</p>
            <p><strong>Tên:</strong> {user.fullName || `${user.firstName} ${user.lastName}`}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Token:</strong> {localStorage.getItem('accessToken') ? 'Có' : 'Không có'}</p>
          </div>
        ) : (
          <p>Chưa đăng nhập</p>
        )}
      </div>

      {/* Danh sách vé */}
      <div style={{ marginBottom: '30px' }}>
        <h3>🎫 Danh sách vé ({tickets.length} vé)</h3>
        {tickets.length === 0 ? (
          <div style={{ padding: '20px', background: '#fff3cd', borderRadius: '8px', textAlign: 'center' }}>
            <p>Không có vé nào</p>
          </div>
        ) : (
          <div style={{ display: 'grid', gap: '15px' }}>
            {tickets.map(ticket => (
              <div key={ticket._id} style={{
                padding: '15px',
                background: 'white',
                borderRadius: '8px',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                borderLeft: `4px solid ${ticket.paymentStatus === 'paid' ? '#28a745' : '#ffc107'}`
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <h4 style={{ margin: '0 0 5px 0' }}>Mã vé: {ticket.ticketCode}</h4>
                    <p style={{ margin: '0', color: '#666' }}>
                      {ticket.passengerName} - {ticket.seatNumber} - {ticket.paymentStatus === 'paid' ? 'Đã thanh toán' : 'Chờ thanh toán'}
                    </p>
                  </div>
                  <div style={{ textAlign: 'right' }}>
                    <p style={{ margin: '0', fontSize: '18px', fontWeight: 'bold', color: '#007bff' }}>
                      {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(ticket.price)}
                    </p>
                    <p style={{ margin: '0', fontSize: '12px', color: '#666' }}>
                      {new Date(ticket.createdAt).toLocaleString('vi-VN')}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Danh sách chuyến bay */}
      <div>
        <h3>✈️ Danh sách chuyến bay ({flights.length} chuyến)</h3>
        {flights.length === 0 ? (
          <p>Không có dữ liệu chuyến bay</p>
        ) : (
          <div style={{ display: 'grid', gap: '10px' }}>
            {flights.slice(0, 3).map(flight => (
              <div key={flight._id} style={{
                padding: '10px',
                background: '#e9ecef',
                borderRadius: '5px'
              }}>
                <strong>{flight.flightCode}</strong> - {flight.route} - {flight.availableSeats} ghế
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DatabaseTest;
