import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Booking.css';

const Success = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [ticketData, setTicketData] = useState(null);
  const [countdown, setCountdown] = useState(30 * 60); // 30 minutes in seconds

  useEffect(() => {
    // L·∫•y th√¥ng tin v√© t·ª´ state ƒë∆∞·ª£c truy·ªÅn t·ª´ trang Booking
    const data = location.state?.ticketData;
    if (data) {
      setTicketData(data);
    } else {
      // N·∫øu kh√¥ng c√≥ d·ªØ li·ªáu, chuy·ªÉn v·ªÅ trang ch·ªß
      navigate('/');
      return;
    }

    // Countdown timer
    const timer = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [location.state, navigate]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const handlePayment = () => {
    // Chuy·ªÉn ƒë·∫øn trang thanh to√°n v·ªõi th√¥ng tin v√©
    navigate('/booking', {
      state: {
        ticketId: ticketData?.ticketId,
        showPayment: true
      }
    });
  };

  const handleGoHome = () => {
    navigate('/');
  };

  if (!ticketData) {
    return (
      <div className="booking-container">
        <div className="loading">ƒêang t·∫£i th√¥ng tin...</div>
      </div>
    );
  }

  return (
    <div className="booking-container">
      <div className="booking-header">
        <h2>ƒê·∫∑t v√© th√†nh c√¥ng!</h2>
        <button onClick={handleGoHome} className="back-btn">
          ‚Üê V·ªÅ trang ch·ªß
        </button>
      </div>

      <div className="booking-content">
        <div className="booking-confirmation">
          <div className="confirmation-card">
            <div className="confirmation-icon">‚úÖ</div>
            <div className="confirmation-message">
              <h3>V√© c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t th√†nh c√¥ng!</h3>
              <p>V√© ƒëang ·ªü tr·∫°ng th√°i <strong>"Ch·ªù thanh to√°n"</strong></p>
              <p>Vui l√≤ng thanh to√°n trong v√≤ng <strong>{formatTime(countdown)}</strong> ƒë·ªÉ gi·ªØ ch·ªó.</p>
            </div>

            <div className="booking-details">
              <div className="detail-item">
                <span><strong>M√£ v√©:</strong></span>
                <span>{ticketData.ticketId?.slice(-8).toUpperCase()}</span>
              </div>
              <div className="detail-item">
                <span><strong>M√£ chuy·∫øn bay:</strong></span>
                <span>{ticketData.flightCode}</span>
              </div>
              <div className="detail-item">
                <span><strong>Tuy·∫øn bay:</strong></span>
                <span>{ticketData.route}</span>
              </div>
              <div className="detail-item">
                <span><strong>S·ªë l∆∞·ª£ng h√†nh kh√°ch:</strong></span>
                <span>{ticketData.passengerCount}</span>
              </div>
              <div className="detail-item">
                <span><strong>H·∫°ng v√©:</strong></span>
                <span className="capitalize">{ticketData.class}</span>
              </div>
              <div className="detail-item">
                <span><strong>T·ªïng ti·ªÅn:</strong></span>
                <span className="price">
                  {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(ticketData.totalPrice)}
                </span>
              </div>
              <div className="detail-item">
                <span><strong>H·∫°n thanh to√°n:</strong></span>
              </div>
            </div>

            <div className="confirmation-actions">
              <button onClick={handlePayment} className="pay-now-btn">
                Thanh to√°n ngay
              </button>
              <button onClick={handleGoHome} className="continue-shopping-btn">
                ƒê·ªÉ thanh to√°n sau
              </button>
            </div>

            <div className="booking-actions" style={{ marginTop: '20px', justifyContent: 'center' }}>
              <button
                onClick={() => navigate('/tickets')}
                className="check-tickets-btn"
                style={{
                  background: '#17a2b8',
                  color: 'white',
                  border: 'none',
                  padding: '10px 20px',
                  borderRadius: '5px',
                  cursor: 'pointer',
                  fontSize: '14px'
                }}
              >
                üîç Ki·ªÉm tra v√© ƒë√£ ƒë·∫∑t
              </button>
            </div>

            {countdown < 300 && ( // Hi·ªÉn th·ªã c·∫£nh b√°o khi c√≤n d∆∞·ªõi 5 ph√∫t
              <div className="warning-message">
                ‚ö†Ô∏è Ch·ªâ c√≤n {formatTime(countdown)} ƒë·ªÉ thanh to√°n! V√© s·∫Ω b·ªã h·ªßy n·∫øu kh√¥ng thanh to√°n k·ªãp th·ªùi.
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Success;
