import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [activeTab, setActiveTab] = useState('profile');
  const [tickets, setTickets] = useState([]);
  const [ticketsLoading, setTicketsLoading] = useState(false);

  // Láº¥y danh sÃ¡ch vÃ© Ä‘Ã£ Ä‘áº·t khi chuyá»ƒn sang tab bookings
  useEffect(() => {
    if (activeTab === 'bookings' && user) {
      fetchTickets();
    }
  }, [activeTab, user]);

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchProfile();
  }, [navigate]);


  const fetchProfile = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken');

      const response = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        throw new Error('KhÃ´ng thá»ƒ táº£i thÃ´ng tin há»“ sÆ¡');
      }

      const data = await response.json();
      const userData = data.data || data;
      setProfile({
        fullName: userData.fullName || '',
        email: userData.email || '',
        phoneNumber: userData.phoneNumber || ''
      });
    } catch (error) {
      console.error('Lá»—i khi táº£i thÃ´ng tin há»“ sÆ¡:', error);
      setMessage({ type: 'error', text: 'KhÃ´ng thá»ƒ táº£i thÃ´ng tin há»“ sÆ¡' });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (field) => (event) => {
    setProfile(prev => ({
      ...prev,
      [field]: event.target.value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      setSaving(true);
      setMessage({ type: '', text: '' });

      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/auth/update-profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(profile)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'KhÃ´ng thá»ƒ cáº­p nháº­t há»“ sÆ¡');
      }

      const data = await response.json();
      setMessage({ type: 'success', text: 'Cáº­p nháº­t há»“ sÆ¡ thÃ nh cÃ´ng!' });

      // Cáº­p nháº­t thÃ´ng tin user trong state
      if (data.data) {
        setUser(prev => ({ ...prev, ...data.data }));
      }
    } catch (error) {
      console.error('Lá»—i khi cáº­p nháº­t há»“ sÆ¡:', error);
      setMessage({
        type: 'error',
        text: error.message || 'KhÃ´ng thá»ƒ cáº­p nháº­t há»“ sÆ¡'
      });
    } finally {
      setSaving(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    setUser(null);
    navigate('/');
  };

  const fetchTickets = async () => {
    try {
      setTicketsLoading(true);
      const token = localStorage.getItem('accessToken');

      const userResponse = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!userResponse.ok) {
        setTickets([]);
        return;
      }

      const userData = await userResponse.json();
      const currentUserId = userData.data._id;

      const response = await fetch(`http://localhost:8080/api/v1/tickets?userId=${currentUserId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        let ticketsArray = [];
        if (data.tickets && Array.isArray(data.tickets)) {
          ticketsArray = data.tickets
            .filter(ticket => ticket.userId === currentUserId)
            .map(ticket => ({
              _id: ticket._id,
              ticketCode: ticket.ticketCode,
              passengerName: ticket.passengerName,
              email: ticket.email,
              phoneNumber: ticket.phoneNumber,
              seatNumber: ticket.seatNumber,
              price: ticket.price,
              passengerCount: ticket.passengerCount,
              paymentStatus: ticket.paymentStatus,
              bookingDate: ticket.bookingDate,
              paidAt: ticket.paidAt,
              refundedAt: ticket.refundedAt,
              status: ticket.status
            }));
        }
        setTickets(ticketsArray);
      } else {
        setTickets([]);
      }
    } catch (error) {
      console.error('Lá»—i káº¿t ná»‘i:', error);
      setTickets([]);
    } finally {
      setTicketsLoading(false);
    }
  };

  const handleCancelTicket = async (ticketId) => {
    const confirmed = window.confirm('Báº¡n cÃ³ cháº¯c cháº¯n muá»‘n há»§y vÃ© nÃ y?');
    if (!confirmed) return;

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/payments/refund', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ticketId,
          reason: 'Customer requested cancellation'
        })
      });

      if (response.ok) {
        alert('VÃ© Ä‘Ã£ Ä‘Æ°á»£c há»§y thÃ nh cÃ´ng!');
        setTickets(prevTickets =>
          prevTickets.map(ticket =>
            ticket._id === ticketId
              ? { ...ticket, paymentStatus: 'refunded', status: 'cancelled' }
              : ticket
          )
        );
      } else {
        const error = await response.json();
        alert(`Lá»—i há»§y vÃ©: ${error.message}`);
      }
    } catch (error) {
      console.error('Lá»—i há»§y vÃ©:', error);
      alert('KhÃ´ng thá»ƒ há»§y vÃ©. Vui lÃ²ng thá»­ láº¡i sau!');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
  };

  const handlePayment = (ticketId) => {
    // Chuyá»ƒn Ä‘áº¿n trang thanh toÃ¡n vá»›i thÃ´ng tin vÃ©
    navigate('/booking', {
      state: {
        ticketId,
        showPayment: true
      }
    });
  };

  // TÃ­nh ngÃ y háº¡n thanh toÃ¡n (vÃ­ dá»¥: 24h sau khi Ä‘áº·t vÃ©)
  const getPaymentDeadline = (bookingDate) => {
    const deadline = new Date(bookingDate);
    deadline.setHours(deadline.getHours() + 24); // ThÃªm 24h
    return deadline;
  };

  const isExpired = (bookingDate) => {
    const deadline = getPaymentDeadline(bookingDate);
    return new Date() > deadline;
  };

  // HÃ m xÃ¡c Ä‘á»‹nh cáº¥p báº­c thÃ nh viÃªn dá»±a trÃªn sá»‘ vÃ© Ä‘Ã£ Ä‘áº·t hoáº·c tiÃªu chÃ­ khÃ¡c
  const getMembershipTier = (userData) => {
    // ÄÃ¢y lÃ  logic máº«u - báº¡n cÃ³ thá»ƒ Ä‘iá»u chá»‰nh theo yÃªu cáº§u thá»±c táº¿
    // VÃ­ dá»¥: dá»±a trÃªn sá»‘ vÃ© Ä‘Ã£ Ä‘áº·t, tá»•ng tiá»n Ä‘Ã£ chi tiÃªu, v.v.
    if (userData.membershipTier) {
      return userData.membershipTier;
    }

    // Logic máº·c Ä‘á»‹nh náº¿u khÃ´ng cÃ³ thÃ´ng tin tá»« API
    return 'Báº¡c'; // Máº·c Ä‘á»‹nh lÃ  Báº¡c cho thÃ nh viÃªn má»›i
  };

  const getMembershipColor = (tier) => {
    switch (tier) {
      case 'Kim cÆ°Æ¡ng': return '#E8E8E8';
      case 'VÃ ng': return '#FFD700';
      case 'Báº¡c': return '#C0C0C0';
      default: return '#C0C0C0';
    }
  };

  const getMembershipBenefits = (tier) => {
    switch (tier) {
      case 'Kim cÆ°Æ¡ng':
        return ['Æ¯u tiÃªn Ä‘áº·t vÃ©', 'HoÃ n tiá»n 100%', 'Há»— trá»£ 24/7', 'TÃ­ch Ä‘iá»ƒm 3x'];
      case 'VÃ ng':
        return ['Æ¯u tiÃªn Ä‘áº·t vÃ©', 'HoÃ n tiá»n 50%', 'Há»— trá»£ nhanh', 'TÃ­ch Ä‘iá»ƒm 2x'];
      case 'Báº¡c':
        return ['TÃ­ch Ä‘iá»ƒm cÆ¡ báº£n', 'Há»— trá»£ tiÃªu chuáº©n'];
      default:
        return ['TÃ­ch Ä‘iá»ƒm cÆ¡ báº£n', 'Há»— trá»£ tiÃªu chuáº©n'];
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">Äang táº£i...</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>Há»“ sÆ¡ cÃ¡ nhÃ¢n</h2>
        <button className="btn-logout" onClick={handleLogout}>
          ÄÄƒng xuáº¥t
        </button>
      </div>

      <div className="profile-content">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            ThÃ´ng tin cÃ¡ nhÃ¢n
          </button>
          <button
            className={`tab ${activeTab === 'bookings' ? 'active' : ''}`}
            onClick={() => setActiveTab('bookings')}
          >
            Lá»‹ch sá»­ Ä‘áº·t vÃ©
          </button>
        </div>

        {activeTab === 'profile' && (
          <div className="profile-info">
            <div className="profile-avatar">
              <div className="avatar-circle">
                {profile.fullName ? profile.fullName.charAt(0).toUpperCase() : 'U'}
              </div>
              <h3>{profile.fullName}</h3>
              <div className="membership-tier" style={{ backgroundColor: getMembershipColor(getMembershipTier(user)) }}>
                <span className="tier-icon">
                  {getMembershipTier(user) === 'Kim cÆ°Æ¡ng' ? 'ğŸ’' :
                   getMembershipTier(user) === 'VÃ ng' ? 'ğŸ¥‡' : 'ğŸ¥ˆ'}
                </span>
                <span className="tier-name">ThÃ nh viÃªn {getMembershipTier(user)}</span>
              </div>
            </div>

            <div className="membership-benefits">
              <h4>Quyá»n lá»£i thÃ nh viÃªn</h4>
              <ul>
                {getMembershipBenefits(getMembershipTier(user)).map((benefit, index) => (
                  <li key={index}>âœ“ {benefit}</li>
                ))}
              </ul>
            </div>

            {message.text && (
              <div className={`message ${message.type}`}>
                {message.text}
              </div>
            )}

            <form className="profile-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Há» vÃ  tÃªn</label>
                <input
                  type="text"
                  value={profile.fullName}
                  onChange={handleChange('fullName')}
                  placeholder="Nháº­p há» vÃ  tÃªn"
                  required
                />
              </div>

              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  value={profile.email}
                  onChange={handleChange('email')}
                  placeholder="Nháº­p email"
                  required
                />
              </div>

              <div className="form-group">
                <label>Sá»‘ Ä‘iá»‡n thoáº¡i</label>
                <input
                  type="tel"
                  value={profile.phoneNumber}
                  onChange={handleChange('phoneNumber')}
                  placeholder="Nháº­p sá»‘ Ä‘iá»‡n thoáº¡i"
                  required
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="btn-cancel"
                  onClick={fetchProfile}
                  disabled={saving}
                >
                  LÃ m má»›i
                </button>
                <button
                  type="submit"
                  className="btn-save"
                  disabled={saving}
                >
                  {saving ? 'Äang lÆ°u...' : 'Cáº­p nháº­t'}
                </button>
              </div>
            </form>
          </div>
        )}

