import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import PaymentForm from '../components/PaymentForm';
import './Booking.css';

const Payment = ({ user }) => {
  const { ticketId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [paymentError, setPaymentError] = useState(null);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        // ‚úÖ N·∫øu c√≥ d·ªØ li·ªáu v√© ƒë∆∞·ª£c truy·ªÅn t·ª´ trang Profile, d√πng lu√¥n
        if (location.state?.ticketData) {
          console.log('üìã D·ªØ li·ªáu v√© t·ª´ Profile:', location.state.ticketData);
          const ticketData = location.state.ticketData;

          // ƒê·∫£m b·∫£o c√°c tr∆∞·ªùng c·∫ßn thi·∫øt c√≥ m·∫∑t
          const normalizedTicket = {
            ...ticketData,
            ticketId: ticketData.ticketId || ticketData._id,
            totalPrice: ticketData.totalPrice || ticketData.price,
            flightCode: ticketData.flightId?.flightCode || ticketData.flightCode || 'N/A',
            route: ticketData.flightId?.route || ticketData.route || 'N/A',
            passengerCount: ticketData.passengerCount || 1,
            seatClasses: ticketData.seatClasses || [ticketData.ticketClass || ticketData.class || 'economy'],
            seatNumbers: ticketData.seatNumbers || [ticketData.seatNumber || 'N/A'],
            bookingDate: ticketData.bookingDate,
            paymentStatus: ticketData.paymentStatus
          };

          setTicket(normalizedTicket);
          setLoading(false);
          return;
        }

        // ‚úÖ N·∫øu kh√¥ng c√≥, fallback sang g·ªçi API
        if (ticketId) {
          const response = await fetch(`http://localhost:8080/api/v1/tickets/${ticketId}`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
              'Content-Type': 'application/json'
            }
          });

          if (response.ok) {
            const data = await response.json();
            setTicket(data.data);
          } else {
            alert('Kh√¥ng t√¨m th·∫•y th√¥ng tin v√©');
            navigate('/');
          }
        } else {
          alert('Kh√¥ng c√≥ th√¥ng tin v√© ƒë·ªÉ thanh to√°n');
          navigate('/');
        }
      } catch (error) {
        console.error('L·ªói l·∫•y th√¥ng tin v√©:', error);
        alert('L·ªói k·∫øt n·ªëi server');
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    const token = localStorage.getItem('accessToken');
    if (!token) {
      navigate('/login');
      return;
    }

    fetchTicket();
  }, [ticketId, location.state, navigate]);



  // H√†m x·ª≠ l√Ω thanh to√°n th√†nh c√¥ng
  const handlePaymentSuccess = async (paymentIntentId) => {
    setPaymentSuccess(true);
    setPaymentError(null);

    try {
      const token = localStorage.getItem('accessToken');

      // G·ªçi API x√°c nh·∫≠n thanh to√°n t·ª´ backend
      const confirmResponse = await fetch('http://localhost:8080/api/v1/payments/confirm', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          paymentIntentId: paymentIntentId
        }),
      });

      if (confirmResponse.ok) {
        const confirmData = await confirmResponse.json();
        console.log('‚úÖ Thanh to√°n ƒë√£ ƒë∆∞·ª£c x√°c nh·∫≠n t·ª´ backend:', confirmData);

        // Th√¥ng b√°o th√†nh c√¥ng s·∫Ω hi·ªÉn th·ªã trong giao di·ªán b√™n d∆∞·ªõi
        // T·ª± ƒë·ªông chuy·ªÉn v·ªÅ trang v√© sau 5 gi√¢y
        setTimeout(() => {
          navigate('/tickets');
        }, 5000);

      } else {
        const errorData = await confirmResponse.json();
        console.error('‚ùå L·ªói x√°c nh·∫≠n thanh to√°n:', errorData.message || confirmResponse.statusText);
        setPaymentError('X√°c nh·∫≠n thanh to√°n th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.');
      }
    } catch (error) {
      console.error('‚ùå L·ªói k·∫øt n·ªëi khi x√°c nh·∫≠n thanh to√°n:', error);
      setPaymentError('L·ªói k·∫øt n·ªëi server. Vui l√≤ng th·ª≠ l·∫°i.');
    }
  };


  // H√†m x·ª≠ l√Ω l·ªói thanh to√°n
  const handlePaymentError = (error) => {
    setPaymentError(error);
