          <div className="success-buttons">
            <button onClick={() => navigate('/')} className="back-btn">
              ‚Üê V·ªÅ trang ch·ªß
            </button>
            <button onClick={() => navigate('/tickets')} className="profile-btn">
              üë§ Xem v√© c·ªßa t√¥i
            </button>
          </div>
        </div>

        <div className="booking-content">
          <div className="booking-confirmation">
            <div className="confirmation-card">
              <div className="confirmation-icon">‚úÖ</div>
              <div className="confirmation-message">
                <h3>üéâ Thanh to√°n th√†nh c√¥ng!</h3>
                <p>Ch√∫c m·ª´ng! Thanh to√°n c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c x·ª≠ l√Ω th√†nh c√¥ng.</p>
                <p>Email x√°c nh·∫≠n ƒë√£ ƒë∆∞·ª£c g·ª≠i ƒë·∫øn ƒë·ªãa ch·ªâ <strong>{ticket.email || user?.email}</strong></p>
                <p>M√£ thanh to√°n: <strong>{ticket.ticketId?.slice(-8).toUpperCase()}</strong></p>
                <p>‚úÖ Tr·∫°ng th√°i v√© ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t th√†nh <strong>"ƒê√£ thanh to√°n"</strong></p>
                <p>V√© c·ªßa b·∫°n ƒë√£ s·∫µn s√†ng ƒë·ªÉ s·ª≠ d·ª•ng!</p>

                <div className="countdown-notice">
                  <p>T·ª± ƒë·ªông chuy·ªÉn v·ªÅ trang v√© trong <strong>{countdown}</strong> gi√¢y...</p>
                  <button onClick={() => navigate('/tickets')} className="immediate-btn">
                    Xem v√© ngay
                  </button>
                </div>
              </div>

              <div className="booking-details">
                <div className="detail-item">
                  <span><strong>M√£ v√©:</strong></span>
                  <span>{ticket.ticketId?.slice(-8).toUpperCase()}</span>
                </div>
                <div className="detail-item">
                  <span><strong>M√£ chuy·∫øn bay:</strong></span>
                  <span>{ticket.flightCode}</span>
                </div>
                <div className="detail-item">
                  <span><strong>Tuy·∫øn bay:</strong></span>
                  <span>{ticket.route}</span>
                </div>
                <div className="detail-item">
                  <span><strong>S·ªë h√†nh kh√°ch:</strong></span>
                  <span>{ticket.passengerCount}</span>
                </div>
                <div className="detail-item">
                  <span><strong>Chi ti·∫øt gh·∫ø:</strong></span>
                  <span>
                    {ticket.seatClasses && ticket.seatNumbers && ticket.seatClasses.length > 0 ? (
                      ticket.seatClasses.map((seatClass, index) => (
                        <div key={index} style={{ marginBottom: '2px' }}>
                          <span className="capitalize">
                            Gh·∫ø {ticket.seatNumbers[index]} - {seatClass}
                          </span>
                        </div>
                      ))
                    ) : (
                      <span className="capitalize">{ticket.seatClasses || ticket.class}</span>
                    )}
                  </span>
                </div>
                <div className="detail-item">
                  <span><strong>T·ªïng ti·ªÅn:</strong></span>
                  <span className="price">
                    {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(ticket.totalPrice)}
                  </span>
                </div>
                <div className="detail-item">
                  <span><strong>Ng√†y thanh to√°n:</strong></span>
                  <span>{new Date().toLocaleString('vi-VN')}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="booking-container">
      <div className="booking-header">
        <h2>üß™ Thanh to√°n Demo</h2>
        <button onClick={() => navigate('/')} className="back-btn">
          ‚Üê V·ªÅ trang ch·ªß
        </button>
      </div>

      <div className="booking-content">
        {/* Th√¥ng tin v√© c·∫ßn thanh to√°n */}
        <div className="payment-ticket-info">
          <h3>Th√¥ng tin v√© thanh to√°n</h3>
          <div className="ticket-summary-card">
            <div className="ticket-details">
              <div className="detail-item">
                <span><strong>M√£ v√©:</strong></span>
                <span>{ticket.ticketId?.slice(-8).toUpperCase()}</span>
              </div>
              <div className="detail-item">
                <span><strong>M√£ chuy·∫øn bay:</strong></span>
                <span>{ticket.flightCode}</span>
              </div>
              <div className="detail-item">
                <span><strong>Tuy·∫øn bay:</strong></span>
                <span>{ticket.route}</span>
              </div>
              <div className="detail-item">
                <span><strong>S·ªë l∆∞·ª£ng h√†nh kh√°ch:</strong></span>
                <span>{ticket.passengerCount}</span>
              </div>
              <div className="detail-item">
                <span><strong>Chi ti·∫øt gh·∫ø:</strong></span>
                <span>
                  {ticket.seatClasses && ticket.seatNumbers && ticket.seatClasses.length > 0 ? (
