import React, { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  Typography,
  Alert,
  Chip,
  Grid,
  Card,
  CardContent,
  InputAdornment,
  IconButton,
} from '@mui/material';
import {
  LocalOffer as CouponIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  Close as CloseIcon,
} from '@mui/icons-material';

const CouponInput = ({ onCouponApplied, onCouponRemoved, initialCoupon = null }) => {
  const [couponCode, setCouponCode] = useState(initialCoupon?.code || '');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(initialCoupon);
  const [originalAmount, setOriginalAmount] = useState(0);

  const handleApplyCoupon = async () => {
    if (!couponCode.trim()) {
      setError('Vui lòng nhập mã giảm giá');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/coupons/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          code: couponCode.trim(),
          orderValue: originalAmount,
          userId: JSON.parse(localStorage.getItem('user') || '{}')._id,
        }),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setAppliedCoupon(data.data.coupon);
        onCouponApplied(data.data);
        setError('');
      } else {
        setError(data.message || 'Mã giảm giá không hợp lệ');
        setAppliedCoupon(null);
        onCouponRemoved();
      }
    } catch (err) {
      setError('Không thể kiểm tra mã giảm giá. Vui lòng thử lại.');
      console.error('Error validating coupon:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveCoupon = () => {
    setCouponCode('');
    setAppliedCoupon(null);
    setError('');
    onCouponRemoved();
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
  };

  return (
    <Card sx={{ mb: 3, border: appliedCoupon ? '2px solid #4caf50' : '1px solid #e0e0e0' }}>
      <CardContent>
        <Box display="flex" alignItems="center" mb={2}>
          <CouponIcon sx={{ mr: 1, color: appliedCoupon ? '#4caf50' : '#ff6b35' }} />
          <Typography variant="h6">
            {appliedCoupon ? 'Mã giảm giá đã áp dụng' : 'Mã giảm giá'}
          </Typography>
        </Box>

        {!appliedCoupon ? (
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} sm={8}>
              <TextField
                fullWidth
                label="Nhập mã giảm giá"
                value={couponCode}
                onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
                placeholder="WELCOME10, SAVE20..."
                InputProps={{
                  style: { textTransform: 'uppercase' },
                }}
                error={!!error}
                helperText={error}
              />
            </Grid>
            <Grid item xs={12} sm={4}>
              <Button
                fullWidth
                variant="contained"
                onClick={handleApplyCoupon}
                disabled={loading || !couponCode.trim()}
                sx={{
                  backgroundColor: '#ff6b35',
                  '&:hover': { backgroundColor: '#e55a2b' },
                  height: '56px'
                }}
              >
                {loading ? 'Đang kiểm tra...' : 'Áp dụng'}
              </Button>
            </Grid>
          </Grid>
        ) : (
          <Box>
            <Box display="flex" alignItems="center" justifyContent="space-between" mb={1}>
              <Box display="flex" alignItems="center">
                <CheckIcon sx={{ color: '#4caf50', mr: 1 }} />
                <Typography variant="h6" sx={{ color: '#4caf50' }}>
                  {appliedCoupon.code}
                </Typography>
              </Box>
              <IconButton onClick={handleRemoveCoupon} size="small">
                <CloseIcon />
              </IconButton>
            </Box>

            <Typography variant="body2" color="textSecondary" mb={1}>
              {appliedCoupon.description || `${appliedCoupon.discountType === 'percentage' ? 'Giảm ' + appliedCoupon.discountValue + '%' : 'Giảm ' + formatCurrency(appliedCoupon.discountValue)}`}
            </Typography>

            <Box display="flex" justifyContent="space-between" alignItems="center">
              <Typography variant="body2">
                Hết hạn: {new Date(appliedCoupon.expiryDate).toLocaleDateString('vi-VN')}
              </Typography>
              <Chip
                label={appliedCoupon.discountType === 'percentage' ? `${appliedCoupon.discountValue}%` : formatCurrency(appliedCoupon.discountValue)}
                color="success"
                size="small"
              />
            </Box>
          </Box>
        )}
      </CardContent>
    </Card>
  );
};

export default CouponInput;
