import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [activeTab, setActiveTab] = useState('profile');
  const [tickets, setTickets] = useState([]);
  const [ticketsLoading, setTicketsLoading] = useState(false);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 5,
    totalRecord: 0,
    totalPages: 0
  });

  // Láº¥y danh sÃ¡ch vÃ© Ä‘Ã£ Ä‘áº·t khi chuyá»ƒn sang tab bookings
  useEffect(() => {
    if (activeTab === 'bookings' && user) {
      fetchTickets();
    }
  }, [activeTab, user]);

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchProfile();
  }, [navigate]);


  const fetchProfile = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken');

      const response = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        throw new Error('KhÃ´ng thá»ƒ táº£i thÃ´ng tin há»“ sÆ¡');
      }

      const data = await response.json();
      const userData = data.data || data;
      setProfile({
        fullName: userData.fullName || '',
        email: userData.email || '',
        phoneNumber: userData.phoneNumber || ''
      });
    } catch (error) {
      console.error('Lá»—i khi táº£i thÃ´ng tin há»“ sÆ¡:', error);
      setMessage({ type: 'error', text: 'KhÃ´ng thá»ƒ táº£i thÃ´ng tin há»“ sÆ¡' });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (field) => (event) => {
    setProfile(prev => ({
      ...prev,
      [field]: event.target.value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      setSaving(true);
      setMessage({ type: '', text: '' });

      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/auth/update-profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(profile)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'KhÃ´ng thá»ƒ cáº­p nháº­t há»“ sÆ¡');
      }

      const data = await response.json();
      setMessage({ type: 'success', text: 'Cáº­p nháº­t há»“ sÆ¡ thÃ nh cÃ´ng!' });

      // Cáº­p nháº­t thÃ´ng tin user trong state
      if (data.data) {
        setUser(prev => ({ ...prev, ...data.data }));
      }
    } catch (error) {
      console.error('Lá»—i khi cáº­p nháº­t há»“ sÆ¡:', error);
      setMessage({
        type: 'error',
        text: error.message || 'KhÃ´ng thá»ƒ cáº­p nháº­t há»“ sÆ¡'
      });
    } finally {
      setSaving(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    setUser(null);
    navigate('/');
  };

  const fetchTickets = async (page = 1) => {
    try {
      setTicketsLoading(true);
      const token = localStorage.getItem('accessToken');

      // Láº¥y thÃ´ng tin user hiá»‡n táº¡i
      const userResponse = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!userResponse.ok) {
        throw new Error('KhÃ´ng thá»ƒ láº¥y thÃ´ng tin ngÆ°á»i dÃ¹ng');
      }

      const userData = await userResponse.json();
      const currentUserId = userData.data?._id;

      // Gá»i API láº¥y táº¥t cáº£ vÃ© vá»›i phÃ¢n trang
      const response = await fetch(`http://localhost:8080/api/v1/tickets?page=${page}&limit=${pagination.limit}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('KhÃ´ng thá»ƒ táº£i danh sÃ¡ch vÃ©');
      }

      const data = await response.json();
      const allTickets = data.data?.tickets || [];

      // Lá»c vÃ© thuá»™c vá» user hiá»‡n táº¡i
      const userTickets = allTickets.filter(
        ticket => ticket.userId?._id === currentUserId
      );

      setTickets(userTickets);

      // Cáº­p nháº­t thÃ´ng tin phÃ¢n trang
      if (data.data?.pagination) {
        setPagination(prev => ({
          ...prev,
          page,
          totalRecord: data.data.pagination.totalRecord || 0,
          totalPages: Math.ceil((data.data.pagination.totalRecord || 0) / pagination.limit)
        }));
      }
    } catch (error) {
      console.error('Lá»—i táº£i vÃ©:', error);
      setTickets([]);
    } finally {
      setTicketsLoading(false);
    }
  };


  const handleCancelTicket = async (ticketId) => {
    const confirmed = window.confirm('Báº¡n cÃ³ cháº¯c cháº¯n muá»‘n há»§y vÃ© nÃ y?');
    if (!confirmed) return;

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/payment/refund', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ticketId,
          reason: 'Customer requested cancellation'
        })
      });

      if (response.ok) {
        alert('VÃ© Ä‘Ã£ Ä‘Æ°á»£c há»§y thÃ nh cÃ´ng!');
        setTickets(prevTickets =>
          prevTickets.map(ticket =>
            ticket._id === ticketId
              ? { ...ticket, paymentStatus: 'refunded', status: 'cancelled' }
              : ticket
          )
        );
      } else {
        const error = await response.json();
        alert(`Lá»—i há»§y vÃ©: ${error.message}`);
      }
    } catch (error) {
      console.error('Lá»—i há»§y vÃ©:', error);
      alert('KhÃ´ng thá»ƒ há»§y vÃ©. Vui lÃ²ng thá»­ láº¡i sau!');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
  };

  const handlePayment = (ticket) => {
    // Äáº£m báº£o truyá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin cáº§n thiáº¿t cho trang payment
    const paymentTicketData = {
      ...ticket,
      // Äáº£m báº£o cÃ¡c trÆ°á»ng cáº§n thiáº¿t cÃ³ máº·t
      ticketId: ticket._id,
      totalPrice: ticket.price,
      flightCode: ticket.flightId?.flightCode || ticket.flightCode || 'N/A',
      route: ticket.flightId?.route || ticket.route || 'N/A',
      passengerCount: ticket.passengerCount || 1,
      seatClasses: ticket.seatClasses || [ticket.ticketClass || ticket.class || 'economy'],
      seatNumbers: ticket.seatNumbers || [ticket.seatNumber || 'N/A'],
      bookingDate: ticket.bookingDate,
      paymentStatus: ticket.paymentStatus
    };

    navigate(`/payment/${ticket._id}`, {
      state: {
        ticketData: paymentTicketData,
        showPayment: true,
      },
    });
  };

  // TÃ­nh ngÃ y háº¡n thanh toÃ¡n (vÃ­ dá»¥: 1h sau khi Ä‘áº·t vÃ©)
  const getPaymentDeadline = (bookingDate) => {
    const deadline = new Date(bookingDate);
    deadline.setHours(deadline.getHours() + 1); // ThÃªm 1h
    return deadline;
  };

  const isExpired = (bookingDate) => {
    const deadline = getPaymentDeadline(bookingDate);
    return new Date() > deadline;
  };

  // HÃ m xÃ¡c Ä‘á»‹nh cáº¥p báº­c thÃ nh viÃªn dá»±a trÃªn sá»‘ vÃ© Ä‘Ã£ Ä‘áº·t hoáº·c tiÃªu chÃ­ khÃ¡c
  const getMembershipTier = (userData) => {
    // ÄÃ¢y lÃ  logic máº«u - báº¡n cÃ³ thá»ƒ Ä‘iá»u chá»‰nh theo yÃªu cáº§u thá»±c táº¿
    // VÃ­ dá»¥: dá»±a trÃªn sá»‘ vÃ© Ä‘Ã£ Ä‘áº·t, tá»•ng tiá»n Ä‘Ã£ chi tiÃªu, v.v.
    if (userData.membershipTier) {
      return userData.membershipTier;
    }

    // Logic máº·c Ä‘á»‹nh náº¿u khÃ´ng cÃ³ thÃ´ng tin tá»« API
    return 'Báº¡c'; // Máº·c Ä‘á»‹nh lÃ  Báº¡c cho thÃ nh viÃªn má»›i
  };

  const getMembershipColor = (tier) => {
    switch (tier) {
      case 'Kim cÆ°Æ¡ng': return '#E8E8E8';
      case 'VÃ ng': return '#FFD700';
      case 'Báº¡c': return '#C0C0C0';
      default: return '#C0C0C0';
    }
  };

  const getMembershipBenefits = (tier) => {
    switch (tier) {
      case 'Kim cÆ°Æ¡ng':
        return ['Æ¯u tiÃªn Ä‘áº·t vÃ©', 'HoÃ n tiá»n 100%', 'Há»— trá»£ 24/7', 'TÃ­ch Ä‘iá»ƒm 3x'];
      case 'VÃ ng':
        return ['Æ¯u tiÃªn Ä‘áº·t vÃ©', 'HoÃ n tiá»n 50%', 'Há»— trá»£ nhanh', 'TÃ­ch Ä‘iá»ƒm 2x'];
      case 'Báº¡c':
        return ['TÃ­ch Ä‘iá»ƒm cÆ¡ báº£n', 'Há»— trá»£ tiÃªu chuáº©n'];
      default:
        return ['TÃ­ch Ä‘iá»ƒm cÆ¡ báº£n', 'Há»— trá»£ tiÃªu chuáº©n'];
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">Äang táº£i...</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>Há»“ sÆ¡ cÃ¡ nhÃ¢n</h2>
        <div className="header-buttons">
          <button className="btn-home" onClick={() => navigate('/')}>
            ðŸ  Trang chá»§
          </button>
          <button className="btn-logout" onClick={handleLogout}>
            ÄÄƒng xuáº¥t
          </button>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            ThÃ´ng tin cÃ¡ nhÃ¢n
          </button>
          <button
            className={`tab ${activeTab === 'bookings' ? 'active' : ''}`}
            onClick={() => setActiveTab('bookings')}
          >
            Lá»‹ch sá»­ Ä‘áº·t vÃ©
          </button>
        </div>

        {activeTab === 'profile' && (
          <div className="profile-info">
            {/* Profile Header */}
            <div className="profile-header-card">
              <div className="profile-avatar-section">
                <div className="avatar-container">
                  <div className="avatar-circle">
                    {profile.fullName ? profile.fullName.charAt(0).toUpperCase() : 'U'}
                  </div>
                </div>
                <div className="profile-details">
                  <h3 className="profile-name">{profile.fullName || 'ChÆ°a cáº­p nháº­t tÃªn'}</h3>
                  <p className="profile-email">{profile.email}</p>
                  <div className="membership-badge" style={{ backgroundColor: getMembershipColor(getMembershipTier(user)) }}>
                    <span className="membership-icon">
                      {getMembershipTier(user) === 'Kim cÆ°Æ¡ng' ? 'ðŸ’Ž' :
                       getMembershipTier(user) === 'VÃ ng' ? 'ðŸ¥‡' : 'ðŸ¥ˆ'}
                    </span>
                    <span className="membership-text">ThÃ nh viÃªn {getMembershipTier(user)}</span>
                  </div>
                </div>
              </div>

              {/* Membership Benefits */}
              <div className="membership-benefits-card">
                <h4 className="benefits-title">ðŸŽ Quyá»n lá»£i thÃ nh viÃªn</h4>
                <div className="benefits-grid">
                  {getMembershipBenefits(getMembershipTier(user)).map((benefit, index) => (
                    <div key={index} className="benefit-item">
                      <span className="benefit-icon">âœ“</span>
                      <span className="benefit-text">{benefit}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Message */}
            {message.text && (
              <div className={`message-card ${message.type}`}>
                <span className="message-icon">
                  {message.type === 'success' ? 'âœ“' : 'âš ï¸'}
                </span>
                <span className="message-text">{message.text}</span>
              </div>
            )}

            {/* Edit Form */}
            <div className="profile-form-card">
              <div className="form-header">
                <h4 className="form-title">ðŸ“ Chá»‰nh sá»­a thÃ´ng tin</h4>
                <p className="form-subtitle">Cáº­p nháº­t thÃ´ng tin cÃ¡ nhÃ¢n cá»§a báº¡n</p>
              </div>

              <form className="edit-form" onSubmit={handleSubmit}>
                <div className="form-grid">
                  <div className="form-field">
                    <label className="field-label">
                      <span className="label-icon">ðŸ‘¤</span>
                      Há» vÃ  tÃªn
                    </label>
                    <input
                      type="text"
                      value={profile.fullName}
                      onChange={handleChange('fullName')}
                      placeholder="Nháº­p há» vÃ  tÃªn Ä‘áº§y Ä‘á»§"
                      className="field-input"
                      required
                    />
                  </div>

                  <div className="form-field">
                    <label className="field-label">
                      <span className="label-icon">âœ‰ï¸</span>
                      Email
                    </label>
                    <input
                      type="email"
                      value={profile.email}
                      onChange={handleChange('email')}
                      placeholder="Nháº­p Ä‘á»‹a chá»‰ email"
                      className="field-input"
                      required
                    />
                  </div>

                  <div className="form-field">
                    <label className="field-label">
                      <span className="label-icon">ðŸ“±</span>
                      Sá»‘ Ä‘iá»‡n thoáº¡i
                    </label>
                    <input
                      type="tel"
                      value={profile.phoneNumber}
                      onChange={handleChange('phoneNumber')}
                      placeholder="Nháº­p sá»‘ Ä‘iá»‡n thoáº¡i"
                      className="field-input"
                      required
                    />
                  </div>
                </div>

                <div className="form-actions">
                  <button
                    type="button"
                    className="btn-secondary"
                    onClick={fetchProfile}
                    disabled={saving}
                  >
                    <span className="btn-icon">ðŸ”„</span>
                    LÃ m má»›i
                  </button>
                  <button
                    type="submit"
                    className="btn-primary"
                    disabled={saving}
                  >
                    <span className="btn-icon">
                      {saving ? 'â³' : 'ðŸ’¾'}
                    </span>
                    {saving ? 'Äang lÆ°u...' : 'Cáº­p nháº­t thÃ´ng tin'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

{activeTab === 'bookings' && (
  <div className="bookings-history">
    <div className="bookings-header">
      <h3>ðŸ“œ Lá»‹ch sá»­ Ä‘áº·t vÃ©</h3>
      <div className="bookings-summary">
        <span className="total-tickets">Tá»•ng cá»™ng: {tickets.length} vÃ©</span>
      </div>
    </div>

    {ticketsLoading ? (
      <div className="loading-state">
        <div className="loading-spinner"></div>
        <p>Äang táº£i danh sÃ¡ch vÃ©...</p>
      </div>
    ) : Array.isArray(tickets) && tickets.length === 0 ? (
      <div className="empty-state">
        <div className="empty-icon">âœˆï¸</div>
        <h4>Báº¡n chÆ°a cÃ³ vÃ© nÃ o</h4>
        <p>HÃ£y Ä‘áº·t vÃ© mÃ¡y bay Ä‘á»ƒ báº¯t Ä‘áº§u hÃ nh trÃ¬nh cá»§a báº¡n!</p>
        <button onClick={() => navigate('/')} className="book-now-btn">
          Äáº·t vÃ© ngay
        </button>
      </div>
    ) : Array.isArray(tickets) ? (
      <div className="tickets-container">
        {tickets.map((ticket) => (
          <div key={ticket._id} className={`ticket-card ${ticket.paymentStatus}`}>
            {/* Ticket Header */}
            <div className="ticket-header">
              <div className="ticket-code-section">
                <span className="ticket-code-label">MÃ£ vÃ©</span>
                <span className="ticket-code">{ticket.ticketCode}</span>
              </div>
              <div className={`ticket-status-badge ${ticket.paymentStatus}`}>
                {ticket.paymentStatus === 'paid' && (
                  <>
                    <span className="status-icon">âœ“</span>
                    <span className="status-text">ÄÃ£ thanh toÃ¡n</span>
                  </>
                )}
                {ticket.paymentStatus === 'pending' && (
                  <>
                    <span className="status-icon">â³</span>
                    <span className="status-text">Chá» thanh toÃ¡n</span>
                  </>
                )}
                {ticket.paymentStatus === 'refunded' && (
                  <>
                    <span className="status-icon">â†©</span>
                    <span className="status-text">ÄÃ£ hoÃ n tiá»n</span>
                  </>
                )}
                {ticket.paymentStatus === 'failed' && (
                  <>
                    <span className="status-icon">âœ—</span>
                    <span className="status-text">Tháº¥t báº¡i</span>
                  </>
                )}
              </div>
            </div>

            {/* Flight Info */}
            <div className="ticket-flight-info">
              <div className="flight-route">
                <div className="route-info">
                  <span className="route-text">
                    {ticket.flightId?.route || ticket.route || 'N/A'}
                  </span>
                  <span className="flight-code">
                    {ticket.flightId?.flightCode || ticket.flightCode || 'N/A'}
                  </span>
                </div>
                <div className="seat-info">
                  <span className="seat-class">
                    {ticket.seatClasses?.[0] || ticket.ticketClass || ticket.class || 'Economy'}
                  </span>
                  <span className="seat-number">Gháº¿ {ticket.seatNumbers?.[0] || ticket.seatNumber || 'N/A'}</span>
                </div>
              </div>
            </div>

            {/* Ticket Details */}
            <div className="ticket-details">
              <div className="detail-row">
                <div className="detail-item">
                  <span className="detail-label">ðŸ‘¤ HÃ nh khÃ¡ch</span>
                  <div className="detail-content">
                    <span className="passenger-name">{ticket.email}</span>
                    <span className="passenger-phone">{ticket.phoneNumber}</span>
                  </div>
                </div>

                <div className="detail-item">
                  <span className="detail-label">ðŸ“… NgÃ y Ä‘áº·t</span>
                  <span className="detail-content">{formatDate(ticket.bookingDate)}</span>
                </div>

                <div className="detail-item">
                  <span className="detail-label">ðŸ’° GiÃ¡ vÃ©</span>
                  <span className="detail-content price">{formatCurrency(ticket.price)}</span>
                </div>
              </div>
            </div>

            {/* Payment Timeline */}
            {(ticket.paidAt || ticket.refundedAt) && (
              <div className="payment-timeline">
                {ticket.paidAt && (
                  <div className="timeline-item">
                    <span className="timeline-dot paid"></span>
                    <div className="timeline-content">
                      <span className="timeline-label">Thanh toÃ¡n thÃ nh cÃ´ng</span>
                      <span className="timeline-date">{formatDate(ticket.paidAt)}</span>
                    </div>
                  </div>
                )}
                {ticket.refundedAt && (
                  <div className="timeline-item">
                    <span className="timeline-dot refunded"></span>
                    <div className="timeline-content">
                      <span className="timeline-label">ÄÃ£ hoÃ n tiá»n</span>
                      <span className="timeline-date">{formatDate(ticket.refundedAt)}</span>
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* Actions */}
            <div className="ticket-actions">
              {ticket.paymentStatus === 'paid' && (
                <button onClick={() => handleCancelTicket(ticket._id)} className="action-btn cancel-btn">
                  <span className="btn-icon">âŒ</span>
                  <span className="btn-text">Há»§y vÃ©</span>
                </button>
              )}

              {ticket.paymentStatus === 'pending' && (
                <>
                  {!isExpired(ticket.bookingDate) ? (
                    <>
                      <button
                        onClick={() => handlePayment(ticket)}
                        className="action-btn pay-btn primary"
                      >
                        <span className="btn-icon">ðŸ’³</span>
                        <span className="btn-text">Thanh toÃ¡n</span>
                      </button>

                      <div className="payment-deadline">
