      {
        from: "Huế",
        to: "Hà Nội",
        price: "680.600 VND",
        date: "2 thg 10 2025",
        img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTvsE2rmsNhgXZRT-U-OucddnukxMVa3RTb5w&s"
      },
    ].map((f, idx) => (
      <div key={idx} className="deal-card">
        <span className="label">MỘT CHIỀU</span>
        <img src={f.img} alt={`Flight ${idx + 1}`} />
        <p>{f.from} - {f.to}</p>
        <p>{f.date}</p>
