import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import './Payment.css';

// Initialize Stripe with your publishable key
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || 'pk_test_your_publishable_key');

const CheckoutForm = ({
  amount,
  currency,
  ticketId,
  onPaymentSuccess,
  onPaymentError
}) => {
  const [loading, setLoading] = useState(false);
  const [sessionId, setSessionId] = useState('');

  useEffect(() => {
    // Create Checkout Session as soon as the page loads
    const createCheckoutSession = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/payment/create-checkout-session', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
          },
          body: JSON.stringify({
            ticketId,
            amount,
            currency,
            success_url: `${window.location.origin}/success?ticketId=${ticketId}`,
            cancel_url: `${window.location.origin}/payment/${ticketId}`,
          }),
        });

        if (!response.ok) {
          throw new Error('Failed to create checkout session');
        }

        const data = await response.json();
        setSessionId(data.data.sessionId);
      } catch (error) {
        console.error('Error creating checkout session:', error);
        onPaymentError('Failed to initialize payment');
      }
    };

    createCheckoutSession();
  }, [amount, currency, ticketId, onPaymentError]);

  const handleCheckout = async () => {
    if (!sessionId) {
      onPaymentError('Payment session not ready');
      return;
    }

    setLoading(true);

    try {
      const stripe = await stripePromise;

      const { error } = await stripe.redirectToCheckout({
        sessionId: sessionId,
      });

      if (error) {
        console.error('Stripe Checkout error:', error);
        onPaymentError(error.message || 'Failed to redirect to checkout');
      }
    } catch (error) {
      console.error('Checkout error:', error);
      onPaymentError('An error occurred during checkout');
    }

    setLoading(false);
  };

  return (
    <div className="payment-form">
      <div className="payment-amount">
        <h3>Tổng tiền: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}</h3>
        <p className="currency-note">Thanh toán an toàn qua Stripe</p>
      </div>

      <button
        onClick={handleCheckout}
        disabled={!sessionId || loading}
        className="payment-button"
      >
        {loading ? 'Đang chuyển hướng...' : `Thanh toán ${new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}`}
      </button>

      {!sessionId && (
        <div className="payment-status">
          <p>Đang chuẩn bị thanh toán...</p>
        </div>
      )}
    </div>
  );
};

const PaymentForm = (props) => {
  return <CheckoutForm {...props} />;
};

export default PaymentForm;
