import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import './Payment.css';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || 'pk_test_your_publishable_key_here');

const CheckoutForm = ({ amount, currency, ticketId, onPaymentSuccess, onPaymentError }) => {
  const stripe = useStripe();
  const elements = useElements();

  const [loading, setLoading] = useState(false);
  const [clientSecret, setClientSecret] = useState('');
  const [error, setError] = useState('');

  // Các state cho form fake
  const [name, setName] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [expiry, setExpiry] = useState('');
  const [cvc, setCvc] = useState('');

  useEffect(() => {
    const createPaymentIntent = async () => {
      try {
        const token = localStorage.getItem('accessToken');
        if (!token) {
          setError('Không tìm thấy token xác thực');
          return;
        }

        const response = await fetch('http://localhost:8080/api/v1/payments/create-payment-intent', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({ ticketId, amount, currency }),
        });

        if (response.ok) {
          const data = await response.json();
          setClientSecret(data.data?.clientSecret || '');
          if (!data.data?.clientSecret) setError('Không nhận được client secret từ server');
        } else {
          const err = await response.json();
          setError(err.message || 'Không thể tạo payment intent');
        }
      } catch (err) {
        console.error(err);
        setError('Lỗi kết nối server');
      }
    };

    if (ticketId && amount > 0) createPaymentIntent();
    else setError('Thiếu thông tin vé hoặc số tiền không hợp lệ');
  }, [ticketId, amount, currency]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!stripe || !elements) return setError('Stripe chưa được khởi tạo');
    if (!clientSecret) return setError('Chưa nhận được client secret');

    setLoading(true);
    setError('');

    try {
      const cardElement = elements.getElement(CardElement);
      if (!cardElement) return setError('Không tìm thấy form thẻ');

      const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
          billing_details: { name: name || 'Khách hàng' },
        },
      });

      if (stripeError) setError(stripeError.message || 'Thanh toán thất bại');
      else if (paymentIntent.status === 'succeeded') {
        const token = localStorage.getItem('accessToken');
        const confirmResponse = await fetch('http://localhost:8080/api/v1/payments/confirm-payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({ paymentIntentId: paymentIntent.id }),
        });

        if (confirmResponse.ok) onPaymentSuccess(paymentIntent.id);
        else {
          const errData = await confirmResponse.json();
          setError(errData.message || 'Không thể xác nhận thanh toán');
        }
      } else setError('Thanh toán không thành công');
    } catch (err) {
      console.error(err);
      setError('Lỗi xử lý thanh toán');
    }
    setLoading(false);
  };

  const cardStyle = {
    style: {
      base: { fontSize: '16px', color: '#424770', '::placeholder': { color: '#aab7c4' } },
      invalid: { color: '#fa755a', iconColor: '#fa755a' },
    },
  };

  return (
    <div className="payment-form" style={{ maxWidth: '500px', margin: '0 auto', padding: '20px', border: '1px solid #e1e5e9', borderRadius: '8px', backgroundColor: '#fff' }}>
      <h3 style={{ textAlign: 'center', marginBottom: '10px', color: '#333' }}>
        Tổng tiền: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency }).format(amount)}
      </h3>
      <p style={{ textAlign: 'center', marginBottom: '20px', color: '#666' }}>Thanh toán an toàn với Stripe</p>

      {error && (
        <div style={{
          backgroundColor: '#f8d7da',
          color: '#721c24',
          padding: '10px',
          borderRadius: '4px',
          marginBottom: '15px',
          border: '1px solid #f5c6cb',
          textAlign: 'center'
        }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Thông tin cá nhân */}
        <fieldset style={{ border: '1px solid #ddd', borderRadius: '6px', padding: '15px', marginBottom: '20px' }}>
          <legend style={{ fontWeight: 'bold', color: '#333', padding: '0 10px' }}>Thông tin chủ thẻ</legend>
          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold', color: '#555' }}>Tên chủ thẻ</label>
            <input
              type="text"
              placeholder="NGUYEN VAN A"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              disabled={loading}
              style={{
                width: '100%',
                padding: '10px',
                border: '1px solid #ccc',
                borderRadius: '4px',
                fontSize: '16px',
                boxSizing: 'border-box'
              }}
            />
          </div>
        </fieldset>

        {/* Thông tin thẻ giả lập (chỉ để demo) */}
        <fieldset style={{ border: '1px solid #ddd', borderRadius: '6px', padding: '15px', marginBottom: '20px' }}>
          <legend style={{ fontWeight: 'bold', color: '#333', padding: '0 10px' }}>Thông tin thẻ (Demo - không ảnh hưởng thanh toán)</legend>
          <p style={{ fontSize: '12px', color: '#888', marginBottom: '10px' }}>
            Các trường dưới đây chỉ để minh họa. Thanh toán thực tế sử dụng phần "Thông tin thẻ" bên dưới.
          </p>
          <div className="form-group-inline" style={{
            display: 'flex',
            gap: '10px',
            flexWrap: 'wrap',
            '@media (max-width: 768px)': { flexDirection: 'column' }
          }}>
            <input
              type="text"
              placeholder="Số thẻ 1234 5678 9012 3456"
              value={cardNumber}
              onChange={(e) => setCardNumber(e.target.value)}
              disabled={loading}
              style={{ flex: '2', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', fontSize: '16px' }}
            />
            <input
              type="text"
              placeholder="MM/YY"
              value={expiry}
              onChange={(e) => setExpiry(e.target.value)}
              disabled={loading}
              style={{ flex: '1', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', fontSize: '16px' }}
            />
            <input
              type="text"
              placeholder="CVC"
              value={cvc}
              onChange={(e) => setCvc(e.target.value)}
              disabled={loading}
              style={{ flex: '1', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', fontSize: '16px' }}
            />
          </div>
        </fieldset>

        {/* Stripe CardElement thực tế */}
        <fieldset style={{ border: '1px solid #ddd', borderRadius: '6px', padding: '15px', marginBottom: '20px' }}>
          <legend style={{ fontWeight: 'bold', color: '#333', padding: '0 10px' }}>Thông tin thẻ (Thực tế)</legend>
          <div className="form-group">
            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold', color: '#555' }}>Thông tin thẻ tín dụng</label>
            <div className="card-element-container" style={{
              padding: '12px',
              border: '2px solid #e1e5e9',
              borderRadius: '6px',
              backgroundColor: loading ? '#f8f9fa' : '#fff',
              minHeight: '40px'
            }}>
              {stripe && elements ? <CardElement options={cardStyle} /> : <div style={{ textAlign: 'center', color: '#666' }}>Đang tải...</div>}
            </div>
          </div>
        </fieldset>

        <button
          type="submit"
          disabled={!stripe || !clientSecret || loading}
          style={{
            width: '100%',
            padding: '15px',
            backgroundColor: (!stripe || !clientSecret || loading) ? '#6c757d' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontSize: '16px',
            fontWeight: '600',
            cursor: (!stripe || !clientSecret || loading) ? 'not-allowed' : 'pointer',
            marginTop: '10px'
          }}
        >
          {loading ? 'Đang xử lý...' : `Thanh toán ${new Intl.NumberFormat('vi-VN', { style: 'currency', currency }).format(amount)}`}
        </button>
      </form>

      <p style={{ textAlign: 'center', marginTop: '20px', fontSize: '12px', color: '#888' }}>
        🔒 Thanh toán được bảo mật bởi Stripe. Thông tin thẻ được mã hóa và bảo vệ.
      </p>
    </div>
  );
};

const PaymentForm = (props) => (
  <Elements stripe={stripePromise}>
    <CheckoutForm {...props} />
  </Elements>
);

export default PaymentForm;
