import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [activeTab, setActiveTab] = useState('profile');
  const [tickets, setTickets] = useState([]);
  const [ticketsLoading, setTicketsLoading] = useState(false);

  // Lấy danh sách vé đã đặt khi chuyển sang tab bookings
  useEffect(() => {
    if (activeTab === 'bookings' && user) {
      fetchTickets();
    }
  }, [activeTab, user]);

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchProfile();
  }, [navigate]);


  const fetchProfile = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken');

      const response = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        throw new Error('Không thể tải thông tin hồ sơ');
      }

      const data = await response.json();
      const userData = data.data || data;
      setProfile({
        fullName: userData.fullName || '',
        email: userData.email || '',
        phoneNumber: userData.phoneNumber || ''
      });
    } catch (error) {
      console.error('Lỗi khi tải thông tin hồ sơ:', error);
      setMessage({ type: 'error', text: 'Không thể tải thông tin hồ sơ' });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (field) => (event) => {
    setProfile(prev => ({
      ...prev,
      [field]: event.target.value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      setSaving(true);
      setMessage({ type: '', text: '' });

      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/auth/update-profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(profile)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Không thể cập nhật hồ sơ');
      }

      const data = await response.json();
      setMessage({ type: 'success', text: 'Cập nhật hồ sơ thành công!' });

      // Cập nhật thông tin user trong state
      if (data.data) {
        setUser(prev => ({ ...prev, ...data.data }));
      }
    } catch (error) {
      console.error('Lỗi khi cập nhật hồ sơ:', error);
      setMessage({
        type: 'error',
        text: error.message || 'Không thể cập nhật hồ sơ'
      });
    } finally {
      setSaving(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    setUser(null);
    navigate('/');
  };

  const fetchTickets = async () => {
    try {
      setTicketsLoading(true);
      const token = localStorage.getItem('accessToken');

      // Lấy thông tin user hiện tại
      const userResponse = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!userResponse.ok) {
        throw new Error('Không thể lấy thông tin người dùng');
      }

      const userData = await userResponse.json();
      const currentUserId = userData.data?._id;

      // Gọi API lấy tất cả vé
      const response = await fetch('http://localhost:8080/api/v1/tickets', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Không thể tải danh sách vé');
      }

      const data = await response.json();
      const allTickets = data.data?.tickets || [];

      // Lọc vé thuộc về user hiện tại
      const userTickets = allTickets.filter(
        ticket => ticket.userId?._id === currentUserId
      );

      setTickets(userTickets);
    } catch (error) {
      console.error('Lỗi tải vé:', error);
      setTickets([]);
    } finally {
      setTicketsLoading(false);
    }
  };


  const handleCancelTicket = async (ticketId) => {
    const confirmed = window.confirm('Bạn có chắc chắn muốn hủy vé này?');
    if (!confirmed) return;

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/payment/refund', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ticketId,
          reason: 'Customer requested cancellation'
        })
      });

      if (response.ok) {
        alert('Vé đã được hủy thành công!');
        setTickets(prevTickets =>
          prevTickets.map(ticket =>
            ticket._id === ticketId
              ? { ...ticket, paymentStatus: 'refunded', status: 'cancelled' }
              : ticket
          )
        );
      } else {
        const error = await response.json();
        alert(`Lỗi hủy vé: ${error.message}`);
      }
    } catch (error) {
      console.error('Lỗi hủy vé:', error);
      alert('Không thể hủy vé. Vui lòng thử lại sau!');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
  };

  const handlePayment = (ticket) => {
    // Đảm bảo truyền đầy đủ thông tin cần thiết cho trang payment
    const paymentTicketData = {
      ...ticket,
      // Đảm bảo các trường cần thiết có mặt
      ticketId: ticket._id,
      totalPrice: ticket.price,
      flightCode: ticket.flightId?.flightCode || ticket.flightCode || 'N/A',
      route: ticket.flightId?.route || ticket.route || 'N/A',
      passengerCount: ticket.passengerCount || 1,
      seatClasses: ticket.seatClasses || [ticket.ticketClass || ticket.class || 'economy'],
      seatNumbers: ticket.seatNumbers || [ticket.seatNumber || 'N/A'],
      bookingDate: ticket.bookingDate,
      paymentStatus: ticket.paymentStatus
    };

    navigate(`/payment/${ticket._id}`, {
      state: {
        ticketData: paymentTicketData,
        showPayment: true,
      },
    });
  };

  // Tính ngày hạn thanh toán (ví dụ: 24h sau khi đặt vé)
  const getPaymentDeadline = (bookingDate) => {
    const deadline = new Date(bookingDate);
    deadline.setHours(deadline.getHours() + 24); // Thêm 24h
    return deadline;
  };

  const isExpired = (bookingDate) => {
    const deadline = getPaymentDeadline(bookingDate);
    return new Date() > deadline;
  };

  // Hàm xác định cấp bậc thành viên dựa trên số vé đã đặt hoặc tiêu chí khác
  const getMembershipTier = (userData) => {
    // Đây là logic mẫu - bạn có thể điều chỉnh theo yêu cầu thực tế
    // Ví dụ: dựa trên số vé đã đặt, tổng tiền đã chi tiêu, v.v.
    if (userData.membershipTier) {
      return userData.membershipTier;
    }

    // Logic mặc định nếu không có thông tin từ API
    return 'Bạc'; // Mặc định là Bạc cho thành viên mới
  };

  const getMembershipColor = (tier) => {
    switch (tier) {
      case 'Kim cương': return '#E8E8E8';
      case 'Vàng': return '#FFD700';
      case 'Bạc': return '#C0C0C0';
      default: return '#C0C0C0';
    }
  };

  const getMembershipBenefits = (tier) => {
    switch (tier) {
      case 'Kim cương':
        return ['Ưu tiên đặt vé', 'Hoàn tiền 100%', 'Hỗ trợ 24/7', 'Tích điểm 3x'];
      case 'Vàng':
        return ['Ưu tiên đặt vé', 'Hoàn tiền 50%', 'Hỗ trợ nhanh', 'Tích điểm 2x'];
      case 'Bạc':
        return ['Tích điểm cơ bản', 'Hỗ trợ tiêu chuẩn'];
      default:
        return ['Tích điểm cơ bản', 'Hỗ trợ tiêu chuẩn'];
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">Đang tải...</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>Hồ sơ cá nhân</h2>
        <div className="header-buttons">
          <button className="btn-home" onClick={() => navigate('/')}>
            🏠 Trang chủ
          </button>
          <button className="btn-logout" onClick={handleLogout}>
            Đăng xuất
          </button>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            Thông tin cá nhân
          </button>
          <button
            className={`tab ${activeTab === 'bookings' ? 'active' : ''}`}
            onClick={() => setActiveTab('bookings')}
          >
            Lịch sử đặt vé
          </button>
        </div>

        {activeTab === 'profile' && (
          <div className="profile-info">
            <div className="profile-avatar">
              <div className="avatar-circle">
                {profile.fullName ? profile.fullName.charAt(0).toUpperCase() : 'U'}
              </div>
              <h3>{profile.fullName}</h3>
              <div className="membership-tier" style={{ backgroundColor: getMembershipColor(getMembershipTier(user)) }}>
                <span className="tier-icon">
                  {getMembershipTier(user) === 'Kim cương' ? '💎' :
                   getMembershipTier(user) === 'Vàng' ? '🥇' : '🥈'}
                </span>
                <span className="tier-name">Thành viên {getMembershipTier(user)}</span>
              </div>
            </div>

            <div className="membership-benefits">
              <h4>Quyền lợi thành viên</h4>
              <ul>
                {getMembershipBenefits(getMembershipTier(user)).map((benefit, index) => (
                  <li key={index}>✓ {benefit}</li>
                ))}
              </ul>
            </div>

            {message.text && (
              <div className={`message ${message.type}`}>
                {message.text}
              </div>
            )}

            <form className="profile-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Họ và tên</label>
                <input
                  type="text"
                  value={profile.fullName}
                  onChange={handleChange('fullName')}
                  placeholder="Nhập họ và tên"
                  required
                />
              </div>

              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  value={profile.email}
                  onChange={handleChange('email')}
                  placeholder="Nhập email"
                  required
                />
              </div>

              <div className="form-group">
                <label>Số điện thoại</label>
                <input
                  type="tel"
                  value={profile.phoneNumber}
                  onChange={handleChange('phoneNumber')}
                  placeholder="Nhập số điện thoại"
                  required
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="btn-cancel"
                  onClick={fetchProfile}
                  disabled={saving}
                >
                  Làm mới
                </button>
                <button
                  type="submit"
                  className="btn-save"
                  disabled={saving}
                >
                  {saving ? 'Đang lưu...' : 'Cập nhật'}
                </button>
              </div>
            </form>
          </div>
        )}

{activeTab === 'bookings' && (
  <div className="bookings-history">
    <div className="bookings-header">
      <h3>📜 Lịch sử đặt vé</h3>
      <div className="bookings-summary">
        <span className="total-tickets">Tổng cộng: {tickets.length} vé</span>
      </div>
    </div>

    {ticketsLoading ? (
      <div className="loading-state">
        <div className="loading-spinner"></div>
        <p>Đang tải danh sách vé...</p>
      </div>
    ) : Array.isArray(tickets) && tickets.length === 0 ? (
      <div className="empty-state">
        <div className="empty-icon">✈️</div>
        <h4>Bạn chưa có vé nào</h4>
        <p>Hãy đặt vé máy bay để bắt đầu hành trình của bạn!</p>
        <button onClick={() => navigate('/')} className="book-now-btn">
          Đặt vé ngay
        </button>
      </div>
    ) : Array.isArray(tickets) ? (
      <div className="tickets-container">
        {tickets.map((ticket) => (
          <div key={ticket._id} className={`ticket-card ${ticket.paymentStatus}`}>
            {/* Ticket Header */}
            <div className="ticket-header">
              <div className="ticket-code-section">
                <span className="ticket-code-label">Mã vé</span>
                <span className="ticket-code">{ticket.ticketCode}</span>
              </div>
              <div className={`ticket-status-badge ${ticket.paymentStatus}`}>
                {ticket.paymentStatus === 'paid' && (
                  <>
                    <span className="status-icon">✓</span>
                    <span className="status-text">Đã thanh toán</span>
                  </>
                )}
                {ticket.paymentStatus === 'pending' && (
                  <>
                    <span className="status-icon">⏳</span>
                    <span className="status-text">Chờ thanh toán</span>
                  </>
                )}
                {ticket.paymentStatus === 'refunded' && (
                  <>
                    <span className="status-icon">↩</span>
                    <span className="status-text">Đã hoàn tiền</span>
                  </>
                )}
                {ticket.paymentStatus === 'failed' && (
                  <>
                    <span className="status-icon">✗</span>
                    <span className="status-text">Thất bại</span>
                  </>
                )}
              </div>
            </div>

            {/* Flight Info */}
            <div className="ticket-flight-info">
              <div className="flight-route">
                <div className="route-info">
                  <span className="route-text">
                    {ticket.flightId?.route || ticket.route || 'N/A'}
                  </span>
                  <span className="flight-code">
                    {ticket.flightId?.flightCode || ticket.flightCode || 'N/A'}
                  </span>
                </div>
                <div className="seat-info">
                  <span className="seat-class">
                    {ticket.seatClasses?.[0] || ticket.ticketClass || ticket.class || 'Economy'}
                  </span>
                  <span className="seat-number">Ghế {ticket.seatNumbers?.[0] || ticket.seatNumber || 'N/A'}</span>
                </div>
              </div>
            </div>

            {/* Ticket Details */}
            <div className="ticket-details">
              <div className="detail-row">
                <div className="detail-item">
                  <span className="detail-label">👤 Hành khách</span>
                  <div className="detail-content">
                    <span className="passenger-name">{ticket.email}</span>
                    <span className="passenger-phone">{ticket.phoneNumber}</span>
                  </div>
                </div>

                <div className="detail-item">
                  <span className="detail-label">📅 Ngày đặt</span>
                  <span className="detail-content">{formatDate(ticket.bookingDate)}</span>
                </div>

                <div className="detail-item">
                  <span className="detail-label">💰 Giá vé</span>
                  <span className="detail-content price">{formatCurrency(ticket.price)}</span>
                </div>
              </div>
            </div>

            {/* Payment Timeline */}
            {(ticket.paidAt || ticket.refundedAt) && (
              <div className="payment-timeline">
                {ticket.paidAt && (
                  <div className="timeline-item">
                    <span className="timeline-dot paid"></span>
                    <div className="timeline-content">
                      <span className="timeline-label">Thanh toán thành công</span>
                      <span className="timeline-date">{formatDate(ticket.paidAt)}</span>
                    </div>
                  </div>
                )}
                {ticket.refundedAt && (
                  <div className="timeline-item">
                    <span className="timeline-dot refunded"></span>
                    <div className="timeline-content">
                      <span className="timeline-label">Đã hoàn tiền</span>
                      <span className="timeline-date">{formatDate(ticket.refundedAt)}</span>
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* Actions */}
            <div className="ticket-actions">
              {ticket.paymentStatus === 'paid' && (
                <button onClick={() => handleCancelTicket(ticket._id)} className="action-btn cancel-btn">
                  <span className="btn-icon">❌</span>
                  <span className="btn-text">Hủy vé</span>
                </button>
              )}

              {ticket.paymentStatus === 'pending' && (
                <>
                  {!isExpired(ticket.bookingDate) ? (
                    <>
                      <button
                        onClick={() => handlePayment(ticket)}
                        className="action-btn pay-btn primary"
                      >
                        <span className="btn-icon">💳</span>
                        <span className="btn-text">Thanh toán</span>
                      </button>

                      <div className="payment-deadline">
                        <div className="deadline-info">
                          <span className="deadline-icon">⏰</span>
                          <div className="deadline-text">
                            <span className="deadline-label">Hạn thanh toán</span>
                            <span className="deadline-time">
                              {formatDate(getPaymentDeadline(ticket.bookingDate))}
                            </span>
                            <span className="deadline-countdown">
                              Còn {Math.ceil((getPaymentDeadline(ticket.bookingDate) - new Date()) / (1000 * 60 * 60))} giờ
                            </span>
                          </div>
                        </div>
                      </div>
                    </>
                  ) : (
                    <div className="expired-notice">
                      <span className="expired-icon">⚠️</span>
                      <span className="expired-text">Vé đã hết hạn thanh toán</span>
                    </div>
                  )}
                </>
              )}

              {ticket.paymentStatus === 'refunded' && (
                <div className="refund-notice">
                  <span className="refund-icon">💸</span>
                  <span className="refund-text">Vé đã được hoàn tiền</span>
                  {ticket.refundReason && (
                    <span className="refund-reason">Lý do: {ticket.refundReason}</span>
                  )}
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    ) : (
      <div className="error-state">
        <div className="error-icon">⚠️</div>
        <h4>Có lỗi khi tải danh sách vé</h4>
        <p>Không thể tải danh sách vé của bạn. Vui lòng thử lại.</p>
        <button onClick={fetchTickets} className="retry-btn">
          <span className="btn-icon">🔄</span>
          <span className="btn-text">Thử lại</span>
        </button>
      </div>
    )}
  </div>
)}

      </div>
    </div>
  );
};

export default Profile;
