import React, { useState } from 'react';
import './Payment.css';

const PaymentForm = ({ amount, currency, ticketId }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [checkoutUrl, setCheckoutUrl] = useState('');

  const handleCreateCheckout = async () => {
    setLoading(true);
    setError('');

    try {
      // ⚠️ TEST ONLY: Stripe Secret Key (chỉ dùng thử, không commit lên git)
      const STRIPE_SECRET_KEY = 'sk_test_your_secret_key_here';

      const response = await fetch('https://api.stripe.com/v1/checkout/sessions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${STRIPE_SECRET_KEY}`,
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams({
          'payment_method_types[0]': 'card',
          'mode': 'payment',
          'line_items[0][price_data][currency]': currency || 'vnd',
          'line_items[0][price_data][product_data][name]': `Thanh toán vé #${ticketId || 'demo'}`,
          'line_items[0][price_data][unit_amount]': amount || '2000000', // VND -> tính bằng đồng nhỏ nhất
          'line_items[0][quantity]': '1',
          'success_url': 'http://localhost:3000/payment-success',
          'cancel_url': 'http://localhost:3000/payment-cancel',
        }),
      });

      const data = await response.json();

      if (data.url) {
        setCheckoutUrl(data.url);
        // 👉 Redirect thẳng sang Stripe Checkout:
        window.location.href = data.url;
      } else {
        console.error(data);
        setError(data.error?.message || 'Không nhận được URL thanh toán');
      }
    } catch (err) {
      console.error(err);
      setError('Lỗi khi tạo phiên thanh toán');
    }

    setLoading(false);
  };

  return (
    <div className="payment-form">
      <h3>Thanh toán vé máy bay</h3>
      <p>
        Tổng tiền:{' '}
        <strong>
          {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}
        </strong>
      </p>

      {error && (
        <div className="error-message" style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '10px',
          borderRadius: '4px',
          marginBottom: '15px',
          border: '1px solid #f5c6cb'
        }}>
          <strong>Lỗi:</strong> {error}
        </div>
      )}

      <button
        onClick={handleCreateCheckout}
        disabled={loading}
        className="payment-button"
        style={{
          width: '100%',
          padding: '15px',
          background: loading ? '#6c757d' : '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '6px',
          fontSize: '16px',
          fontWeight: '600',
          cursor: loading ? 'not-allowed' : 'pointer',
          marginTop: '20px'
        }}
      >
        {loading ? 'Đang tạo phiên...' : 'Thanh toán với Stripe'}
      </button>

      {checkoutUrl && (
        <p style={{ marginTop: '15px' }}>
          Hoặc mở thủ công: <a href={checkoutUrl} target="_blank" rel="noreferrer">{checkoutUrl}</a>
        </p>
      )}

      <div className="payment-notice">
        <p>🔒 Thanh toán được bảo mật bởi Stripe</p>
        <small>Thông tin thẻ được mã hóa và bảo vệ</small>
      </div>
    </div>
  );
};

export default PaymentForm;
