import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Booking.css';

const Booking = ({ user }) => {
  const { flightId } = useParams();
  const navigate = useNavigate();
  const [flight, setFlight] = useState(null);
  const [selectedClass, setSelectedClass] = useState('economy');
  const [loading, setLoading] = useState(true);
  const [booking, setBooking] = useState(false);

  // L·∫•y th√¥ng tin chuy·∫øn bay
  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/flights/${flightId}`);
        if (response.ok) {
          const data = await response.json();
          setFlight(data.data);
        } else {
          alert('Kh√¥ng t√¨m th·∫•y th√¥ng tin chuy·∫øn bay');
          navigate('/');
        }
      } catch (error) {
        console.error('L·ªói l·∫•y th√¥ng tin chuy·∫øn bay:', error);
        alert('L·ªói k·∫øt n·ªëi server');
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    if (flightId) {
      fetchFlight();
    } else {
      navigate('/');
    }
  }, [flightId, navigate]);

  // H√†m x·ª≠ l√Ω ƒë·∫∑t v√©
  const handleBooking = async () => {
    if (!user) {
      alert('Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ƒë·∫∑t v√©!');
      navigate('/login');
      return;
    }

    setBooking(true);

    const bookingData = {
      flightCode: flight.flightCode,
      passengerName: user.fullName,
      email: user.email,
      phoneNumber: user.phoneNumber || '',
      seatNumber: `A${Math.floor(Math.random() * 50) + 1}`,
      price: getPriceByClass(flight, selectedClass)
    };

    try {
      const token = localStorage.getItem('accessToken');
      console.log('üîç Booking attempt - Token exists:', !!token);
      if (token) {
        console.log('üîç Token preview:', token.substring(0, 20) + '...');
      }

      const response = await fetch('http://localhost:8080/api/v1/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(bookingData)
      });

      if (response.ok) {
        const result = await response.json();
        alert(`ƒê·∫∑t v√© th√†nh c√¥ng! M√£ v√©: ${result.data.ticketCode}\nV√© s·∫Ω ƒë∆∞·ª£c g·ª≠i v·ªÅ email c·ªßa b·∫°n.`);
        navigate('/');
      } else {
        const error = await response.json();
        console.error('‚ùå Booking failed:', error);
        alert(`L·ªói ƒë·∫∑t v√©: ${error.message}`);
      }
    } catch (error) {
      console.error('L·ªói ƒë·∫∑t v√©:', error);
      alert('Kh√¥ng th·ªÉ ƒë·∫∑t v√©. Vui l√≤ng th·ª≠ l·∫°i sau!');
    } finally {
      setBooking(false);
    }
  };

  // H√†m l·∫•y gi√° theo h·∫°ng v√©
  const getPriceByClass = (flight, classType) => {
    switch (classType) {
      case 'business': return flight.businessPrice || flight.economyPrice;
      case 'first': return flight.firstClassPrice || flight.economyPrice;
      default: return flight.economyPrice;
    }
  };

  if (loading) {
    return (
      <div className="booking-container">
        <div className="loading">ƒêang t·∫£i th√¥ng tin chuy·∫øn bay...</div>
      </div>
    );
  }

  if (!flight) {
    return (
      <div className="booking-container">
        <div className="error">Kh√¥ng t√¨m th·∫•y th√¥ng tin chuy·∫øn bay</div>
      </div>
    );
  }

  return (
    <div className="booking-container">
      <div className="booking-header">
        <h2>ƒê·∫∑t v√© m√°y bay</h2>
        <button onClick={() => navigate('/')} className="back-btn">
          ‚Üê Quay l·∫°i t√¨m ki·∫øm
        </button>
      </div>

      <div className="booking-content">
        {/* Th√¥ng tin chuy·∫øn bay */}
        <div className="flight-details">
          <h3>Th√¥ng tin chuy·∫øn bay</h3>
          <div className="flight-card">
            <div className="flight-info">
              <p><strong>M√£ chuy·∫øn bay:</strong> {flight.flightCode}</p>
              <p><strong>Tuy·∫øn bay:</strong> {flight.route}</p>
              <p><strong>Kh·ªüi h√†nh:</strong> {new Date(flight.departureTime).toLocaleString('vi-VN')}</p>
              <p><strong>ƒê·∫øn n∆°i:</strong> {new Date(flight.arrivalTime).toLocaleString('vi-VN')}</p>
              <p><strong>S·ªë gh·∫ø c√≤n:</strong> {flight.availableSeats}/{flight.totalSeats}</p>
            </div>
          </div>
        </div>

