import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';
import Wallet from '../components/Wallet';

const Profile = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [activeTab, setActiveTab] = useState('profile');
  const [tickets, setTickets] = useState([]);
  const [ticketsLoading, setTicketsLoading] = useState(false);

  // Pagination state cho l·ªãch s·ª≠ ƒë·∫∑t v√©
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [totalItems, setTotalItems] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  // L·∫•y danh s√°ch v√© ƒë√£ ƒë·∫∑t khi chuy·ªÉn sang tab bookings
  useEffect(() => {
    if (activeTab === 'bookings' && user) {
      fetchTickets();
    }
  }, [activeTab, user, currentPage, itemsPerPage]);

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchProfile();
  }, [navigate]);


  const fetchProfile = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken');

      const response = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        throw new Error('Kh√¥ng th·ªÉ t·∫£i th√¥ng tin h·ªì s∆°');
      }

      const data = await response.json();
      const userData = data.data || data;
      setProfile({
        fullName: userData.fullName || '',
        email: userData.email || '',
        phoneNumber: userData.phoneNumber || ''
      });
    } catch (error) {
      console.error('L·ªói khi t·∫£i th√¥ng tin h·ªì s∆°:', error);
      setMessage({ type: 'error', text: 'Kh√¥ng th·ªÉ t·∫£i th√¥ng tin h·ªì s∆°' });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (field) => (event) => {
    setProfile(prev => ({
      ...prev,
      [field]: event.target.value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      setSaving(true);
      setMessage({ type: '', text: '' });

      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/auth/update-profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(profile)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t h·ªì s∆°');
      }

      const data = await response.json();
      setMessage({ type: 'success', text: 'C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!' });

      // C·∫≠p nh·∫≠t th√¥ng tin user trong state
      if (data.data) {
        setUser(prev => ({ ...prev, ...data.data }));
      }
    } catch (error) {
      console.error('L·ªói khi c·∫≠p nh·∫≠t h·ªì s∆°:', error);
      setMessage({
        type: 'error',
        text: error.message || 'Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t h·ªì s∆°'
      });
    } finally {
      setSaving(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    setUser(null);
    navigate('/');
  };

  const fetchTickets = async () => {
    try {
      setTicketsLoading(true);
      const token = localStorage.getItem('accessToken');

      // L·∫•y th√¥ng tin user hi·ªán t·∫°i ƒë·ªÉ c√≥ userId
      const userResponse = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!userResponse.ok) {
        throw new Error('Kh√¥ng th·ªÉ l·∫•y th√¥ng tin ng∆∞·ªùi d√πng');
      }

      const userData = await userResponse.json();
      const currentUserId = userData.data?._id;

      if (!currentUserId) {
        throw new Error('Kh√¥ng t√¨m th·∫•y th√¥ng tin ng∆∞·ªùi d√πng');
      }

      // Chu·∫©n b·ªã query parameters cho pagination v√† userId
      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: itemsPerPage.toString(),
        userId: currentUserId
      });

      // G·ªçi API l·∫•y v√© v·ªõi pagination
      const response = await fetch(`http://localhost:8080/api/v1/tickets?${params}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Kh√¥ng th·ªÉ t·∫£i danh s√°ch v√©');
      }

      const data = await response.json();
      const ticketsData = data.data?.tickets || [];
      const paginationData = data.data?.pagination || data.pagination || {};

      setTickets(ticketsData);
      setTotalItems(paginationData.totalRecord || 0);
      setTotalPages(Math.ceil((paginationData.totalRecord || 0) / itemsPerPage));
    } catch (error) {
      console.error('L·ªói t·∫£i v√©:', error);
      setTickets([]);
    } finally {
      setTicketsLoading(false);
    }
  };


  // X·ª≠ l√Ω thay ƒë·ªïi trang
  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  // X·ª≠ l√Ω thay ƒë·ªïi s·ªë items per page
  const handleItemsPerPageChange = (newItemsPerPage) => {
    setItemsPerPage(newItemsPerPage);
    setCurrentPage(1); // Reset v·ªÅ trang ƒë·∫ßu khi thay ƒë·ªïi s·ªë items
  };

  const handleCancelTicket = async (ticketId) => {
    const confirmed = window.confirm('B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën h·ªßy v√© n√†y?');
    if (!confirmed) return;

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/payments/refund', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ticketId,
          reason: 'Customer requested cancellation'
        })
      });

      if (response.ok) {
        alert('V√© ƒë√£ ƒë∆∞·ª£c h·ªßy th√†nh c√¥ng!');

        // Refresh th√¥ng tin user ƒë·ªÉ c·∫≠p nh·∫≠t ƒëi·ªÉm loyalty v√† c·∫•p ƒë·ªô m·ªõi
        await fetchProfile();

        // C·∫≠p nh·∫≠t UI c·ªßa ticket
        setTickets(prevTickets =>
          prevTickets.map(ticket =>
            ticket._id === ticketId
              ? { ...ticket, paymentStatus: 'refunded', status: 'cancelled' }
              : ticket
          )
        );
      } else {
        const error = await response.json();
        alert(`L·ªói h·ªßy v√©: ${error.message}`);
      }
    } catch (error) {
      console.error('L·ªói h·ªßy v√©:', error);
      alert('Kh√¥ng th·ªÉ h·ªßy v√©. Vui l√≤ng th·ª≠ l·∫°i sau!');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
  };

  const formatSeatInfo = (ticket) => {
    const seatClasses = ticket.seatClasses || [ticket.ticketClass || ticket.class || 'Economy'];
    const seatNumbers = ticket.seatNumbers || [ticket.seatNumber || 'N/A'];

    if (seatClasses.length === 1) {
      return {
        class: seatClasses[0],
        seats: seatNumbers[0]
      };
    }

    return {
      class: `${seatClasses.length} gh·∫ø (${seatClasses.join(', ')})`,
      seats: seatNumbers.join(', ')
    };
  };

  const renderSeatInfo = (ticket) => {
    const seatInfo = formatSeatInfo(ticket);

    return (
      <>
        <span className="seat-class" style={{
          fontSize: '12px',
          opacity: 0.8,
          textTransform: 'uppercase',
          letterSpacing: '1px',
          display: 'block'
        }}>
          {seatInfo.class}
        </span>
        <span className="seat-number" style={{
          fontSize: '18px',
          fontWeight: '600'
        }}>Gh·∫ø {seatInfo.seats}</span>
      </>
    );
  };

  const handlePayment = (ticket) => {
    // ƒê·∫£m b·∫£o truy·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin c·∫ßn thi·∫øt cho trang payment
    const paymentTicketData = {
      ...ticket,
      // ƒê·∫£m b·∫£o c√°c tr∆∞·ªùng c·∫ßn thi·∫øt c√≥ m·∫∑t
      ticketId: ticket._id,
      totalPrice: ticket.price,
      flightCode: ticket.flightId?.flightCode || ticket.flightCode || 'N/A',
      route: ticket.flightId?.route || ticket.route || 'N/A',
      passengerCount: ticket.passengerCount || 1,
      seatClasses: ticket.seatClasses || [ticket.ticketClass || ticket.class || 'economy'],
      seatNumbers: ticket.seatNumbers || [ticket.seatNumber || 'N/A'],
      bookingDate: ticket.bookingDate,
      paymentStatus: ticket.paymentStatus
    };

    navigate(`/payment/${ticket._id}`, {
      state: {
        ticketData: paymentTicketData,
        showPayment: true,
      },
    });
  };

  // T√≠nh ng√†y h·∫°n thanh to√°n (v√≠ d·ª•: 1h sau khi ƒë·∫∑t v√©)
  const getPaymentDeadline = (bookingDate) => {
    const deadline = new Date(bookingDate);
    deadline.setHours(deadline.getHours() + 1); // Th√™m 1h
    return deadline;
  };

  const isExpired = (bookingDate) => {
    const deadline = getPaymentDeadline(bookingDate);
    return new Date() > deadline;
  };

  // H√†m t√≠nh to√°n progress bar cho loyalty points
  const calculateLoyaltyProgress = (userData) => {
    if (!userData || !userData.totalSpentInWallet) {
      return {
        currentLevel: 'Bronze',
        nextLevel: 'Silver',
        progress: 0,
        currentSpent: 0,
        nextLevelAmount: 100000000,
        remainingAmount: 100000000
      };
    }

    const totalSpent = userData.totalSpentInWallet;

    // ƒê·ªãnh nghƒ©a c√°c m·ªëc c·∫•p ƒë·ªô
    const levels = [
      { name: 'Bronze', min: 0, max: 100000000, multiplier: 1 },
      { name: 'Silver', min: 100000000, max: 200000000, multiplier: 1.5 },
      { name: 'Gold', min: 200000000, max: 500000000, multiplier: 2 },
      { name: 'Diamond', min: 500000000, max: Infinity, multiplier: 3 }
    ];

    // T√¨m c·∫•p ƒë·ªô hi·ªán t·∫°i
    let currentLevelIndex = 0;
    for (let i = 0; i < levels.length; i++) {
      if (totalSpent >= levels[i].min && totalSpent < levels[i].max) {
        currentLevelIndex = i;
        break;
      }
    }

    const currentLevel = levels[currentLevelIndex];
    const currentLevelName = currentLevel.name;

    // Ki·ªÉm tra n·∫øu ƒë√£ ƒë·∫°t c·∫•p ƒë·ªô t·ªëi ƒëa
    if (currentLevelIndex === levels.length - 1) {
      return {
        currentLevel: currentLevelName,
        nextLevel: null,
        progress: 100,
        currentSpent: totalSpent,
        nextLevelAmount: null,
        remainingAmount: 0,
        isMaxLevel: true
      };
    }

    const nextLevel = levels[currentLevelIndex + 1];

    // T√≠nh to√°n progress trong c·∫•p ƒë·ªô hi·ªán t·∫°i
    const levelMin = currentLevel.min;
    const levelMax = currentLevel.max;
    const levelRange = levelMax - levelMin;
    const progressInLevel = ((totalSpent - levelMin) / levelRange) * 100;

    return {
      currentLevel: currentLevelName,
      nextLevel: nextLevel.name,
      progress: Math.min(progressInLevel, 100),
      currentSpent: totalSpent,
      nextLevelAmount: nextLevel.min,
      remainingAmount: nextLevel.min - totalSpent,
      isMaxLevel: false
    };
  };

  // H√†m t√≠nh ƒëi·ªÉm loyalty d·ª±a tr√™n t·ªïng chi ti√™u (1 ƒëi·ªÉm = 1000 VND chi ti√™u)
  const calculateLoyaltyPoints = (userData) => {
    if (!userData || !userData.totalSpentInWallet) return 0;
    return Math.floor(userData.totalSpentInWallet / 1000);
  };

  // H√†m x√°c ƒë·ªãnh c·∫•p b·∫≠c th√†nh vi√™n d·ª±a tr√™n t·ªïng chi ti√™u qua v√≠
  const getMembershipTier = (userData) => {
    if (!userData) return 'Bronze';

    // ∆Øu ti√™n s·ª≠ d·ª•ng walletLevel t·ª´ API
    if (userData.walletLevel) {
      // Chuy·ªÉn ƒë·ªïi walletLevel th√†nh ti·∫øng Vi·ªát
      switch (userData.walletLevel.toLowerCase()) {
        case 'diamond': return 'Kim c∆∞∆°ng';
        case 'gold': return 'V√†ng';
        case 'silver': return 'B·∫°c';
        case 'bronze': return 'ƒê·ªìng';
        default: return 'ƒê·ªìng';
      }
    }

    // Fallback logic d·ª±a tr√™n t·ªïng chi ti√™u
    const totalSpent = userData.totalSpentInWallet || 0;
    if (totalSpent >= 500000000) return 'Kim c∆∞∆°ng'; // 500 tri·ªáu VND
    if (totalSpent >= 200000000) return 'V√†ng'; // 200 tri·ªáu VND
    if (totalSpent >= 100000000) return 'B·∫°c'; // 100 tri·ªáu VND
    return 'ƒê·ªìng';
  };

  const getMembershipColor = (tier) => {
    switch (tier) {
      case 'Kim c∆∞∆°ng': return '#E8E8E8';
      case 'V√†ng': return '#FFD700';
      case 'B·∫°c': return '#C0C0C0';
      case 'ƒê·ªìng': return '#CD7F32';
      default: return '#C0C0C0';
    }
  };

  const getMembershipBenefits = (tier) => {
    switch (tier) {
      case 'Kim c∆∞∆°ng':
        return ['∆Øu ti√™n ƒë·∫∑t v√©', 'Ho√†n ti·ªÅn 100%', 'H·ªó tr·ª£ 24/7', 'T√≠ch ƒëi·ªÉm 3x'];
      case 'V√†ng':
        return ['∆Øu ti√™n ƒë·∫∑t v√©', 'Ho√†n ti·ªÅn 50%', 'H·ªó tr·ª£ nhanh', 'T√≠ch ƒëi·ªÉm 2x'];
      case 'B·∫°c':
        return ['T√≠ch ƒëi·ªÉm c∆° b·∫£n', 'H·ªó tr·ª£ ti√™u chu·∫©n'];
      case 'ƒê·ªìng':
        return ['T√≠ch ƒëi·ªÉm c∆° b·∫£n'];
      default:
        return ['T√≠ch ƒëi·ªÉm c∆° b·∫£n'];
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">ƒêang t·∫£i...</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>H·ªì s∆° c√° nh√¢n</h2>
        <div className="header-buttons">
          <button className="btn-home" onClick={() => navigate('/')}>
            üè† Trang ch·ªß
          </button>
          <button className="btn-logout" onClick={handleLogout}>
            ƒêƒÉng xu·∫•t
          </button>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-tabs">
          <button
            className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            Th√¥ng tin c√° nh√¢n
          </button>
          <button
            className={`tab ${activeTab === 'bookings' ? 'active' : ''}`}
            onClick={() => setActiveTab('bookings')}
          >
            L·ªãch s·ª≠ ƒë·∫∑t v√©
          </button>
          <button
            className={`tab ${activeTab === 'wallet' ? 'active' : ''}`}
            onClick={() => setActiveTab('wallet')}
          >
            üëõ V√≠ ƒëi·ªán t·ª≠
          </button>
        </div>

        {activeTab === 'profile' && (
          <div className="profile-info">
            {/* Profile Header */}
            <div className="profile-header-card">
              <div className="profile-avatar-section">
                <div className="avatar-container">
                  <div className="avatar-circle">
                    {profile.fullName ? profile.fullName.charAt(0).toUpperCase() : 'U'}
                  </div>
                </div>
                <div className="profile-details">
                  <h3 className="profile-name">{profile.fullName || 'Ch∆∞a c·∫≠p nh·∫≠t t√™n'}</h3>
                  <p className="profile-email">{profile.email}</p>
                  <div className="membership-badge" style={{ backgroundColor: getMembershipColor(getMembershipTier(user)) }}>
                    <span className="membership-icon">
                      {getMembershipTier(user) === 'Kim c∆∞∆°ng' ? 'üíé' :
                       getMembershipTier(user) === 'V√†ng' ? 'ü•á' :
                       getMembershipTier(user) === 'B·∫°c' ? 'ü•à' : 'ü•â'}
                    </span>
                    <span className="membership-text">Th√†nh vi√™n {getMembershipTier(user)}</span>
                  </div>
                  <div className="loyalty-points-info" style={{
                    marginTop: '8px',
                    fontSize: '12px',
                    color: 'rgba(255, 255, 255, 0.9)',
                    textAlign: 'center'
                  }}>
                    <div className="points-display" style={{
                      fontWeight: '600',
                      fontSize: '16px',
                      color: '#FFD700'
                    }}>
                      ‚≠ê {calculateLoyaltyPoints(user).toLocaleString()} ƒëi·ªÉm
                    </div>
                    <div className="spent-info" style={{
                      fontSize: '10px',
                      opacity: 0.8,
                      marginTop: '2px'
                    }}>
                      ƒê√£ chi: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(user.totalSpentInWallet || 0)}
                    </div>

                    {/* Mini Progress Bar in Badge */}
                    {(() => {
                      const progressData = calculateLoyaltyProgress(user);
                      if (!progressData.isMaxLevel) {
                        return (
                          <div style={{
                            marginTop: '6px',
                            backgroundColor: 'rgba(255, 255, 255, 0.1)',
                            padding: '6px',
                            borderRadius: '6px',
                            border: '1px solid rgba(255, 255, 255, 0.2)'
                          }}>
                            <div style={{
                              display: 'flex',
                              justifyContent: 'space-between',
                              alignItems: 'center',
                              marginBottom: '4px'
                            }}>
                              <span style={{
                                fontSize: '9px',
                                fontWeight: '500',
                                color: '#FFD700'
                              }}>
                                {progressData.currentLevel} ‚Üí {progressData.nextLevel}
                              </span>
                              <span style={{
                                fontSize: '8px',
                                opacity: 0.8
                              }}>
                                {Math.round(progressData.progress)}%
                              </span>
                            </div>

                            <div style={{
                              backgroundColor: 'rgba(255, 255, 255, 0.2)',
                              height: '4px',
                              borderRadius: '2px',
                              overflow: 'hidden'
                            }}>
                              <div style={{
                                width: `${progressData.progress}%`,
                                height: '100%',
                                backgroundColor: '#FFD700',
                                borderRadius: '2px',
                                transition: 'width 0.3s ease'
                              }}></div>
                            </div>

                            <div style={{
                              fontSize: '8px',
                              textAlign: 'center',
                              opacity: 0.7,
                              marginTop: '2px'
                            }}>
                              C√≤n {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(progressData.remainingAmount)}
                            </div>
                          </div>
                        );
                      } else {
                        return (
                          <div style={{
                            marginTop: '6px',
                            backgroundColor: 'rgba(255, 215, 0, 0.1)',
                            padding: '6px',
                            borderRadius: '6px',
                            border: '1px solid rgba(255, 215, 0, 0.3)'
                          }}>
                            <div style={{
                              fontSize: '9px',
                              textAlign: 'center',
                              color: '#FFD700',
                              fontWeight: '500'
                            }}>
                              üèÜ C·∫•p ƒë·ªô cao nh·∫•t!
                            </div>
                            <div style={{
                              backgroundColor: '#FFD700',
                              height: '4px',
                              borderRadius: '2px',
                              marginTop: '2px'
                            }}></div>
                          </div>
                        );
                      }
                    })()}
                  </div>
                </div>
              </div>

              {/* Membership Benefits */}
              <div className="membership-benefits-card">
                <h4 className="benefits-title">üéÅ Quy·ªÅn l·ª£i th√†nh vi√™n</h4>
                <div className="benefits-grid">
                  {getMembershipBenefits(getMembershipTier(user)).map((benefit, index) => (
                    <div key={index} className="benefit-item">
                      <span className="benefit-icon">‚úì</span>
                      <span className="benefit-text">{benefit}</span>
                    </div>
                  ))}
                </div>
              </div>

              {/* Loyalty Points Card */}
              <div className="loyalty-points-card" style={{
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                color: 'white',
                padding: '20px',
                borderRadius: '12px',
