import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Payment.css';

const Payment = ({ user }) => {
  const { bookingId } = useParams();
  const navigate = useNavigate();
  const [booking, setBooking] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('');
  const [cardInfo, setCardInfo] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardHolder: ''
  });
  const [loading, setLoading] = useState(false);
  const [processing, setProcessing] = useState(false);

  // Lấy thông tin booking
  useEffect(() => {
    const fetchBooking = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/bookings/${bookingId}`);
        if (response.ok) {
          const data = await response.json();
          setBooking(data.data);
        } else {
          alert('Không tìm thấy thông tin đặt chỗ');
          navigate('/');
        }
      } catch (error) {
        console.error('Lỗi lấy thông tin booking:', error);
        alert('Lỗi kết nối server');
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    if (bookingId) {
      fetchBooking();
    } else {
      navigate('/');
    }
  }, [bookingId, navigate]);

  // Mô phỏng xử lý thanh toán
  const handlePayment = async () => {
    if (!paymentMethod) {
      alert('Vui lòng chọn phương thức thanh toán');
      return;
    }

    // Validate thông tin thẻ (nếu chọn thẻ)
    if (paymentMethod === 'card') {
      if (!cardInfo.cardNumber || !cardInfo.expiryDate || !cardInfo.cvv || !cardInfo.cardHolder) {
        alert('Vui lòng nhập đầy đủ thông tin thẻ');
        return;
      }

      // Kiểm tra định dạng thẻ đơn giản
      if (cardInfo.cardNumber.length < 16 || cardInfo.cvv.length < 3) {
        alert('Thông tin thẻ không hợp lệ');
        return;
      }
    }

    setProcessing(true);

    try {
      // Mô phỏng thời gian xử lý thanh toán
      await new Promise(resolve => setTimeout(resolve, 3000));

      // Mô phỏng kết quả thanh toán (90% thành công, 10% thất bại)
      const isSuccess = Math.random() > 0.1;

      const paymentData = {
        bookingId: booking._id,
        paymentMethod,
        amount: booking.price,
        status: isSuccess ? 'completed' : 'failed',
        cardInfo: paymentMethod === 'card' ? cardInfo : null
      };

      const response = await fetch('http://localhost:8080/api/v1/payments/process', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(paymentData)
      });

      if (response.ok) {
        const result = await response.json();

        if (isSuccess) {
          alert(`Thanh toán thành công! Mã giao dịch: ${result.data.transactionId}\nVé của bạn đã được xác nhận.`);
          navigate('/');
        } else {
          alert('Thanh toán thất bại! Vui lòng thử lại hoặc chọn phương thức khác.');
        }
      } else {
        alert('Lỗi xử lý thanh toán. Vui lòng thử lại sau.');
      }
    } catch (error) {
      console.error('Lỗi thanh toán:', error);
      alert('Không thể xử lý thanh toán. Vui lòng thử lại sau!');
    } finally {
      setProcessing(false);
    }
  };

  if (loading) {
    return (
      <div className="payment-container">
        <div className="loading">Đang tải thông tin thanh toán...</div>
      </div>
    );
  }

  if (!booking) {
    return (
      <div className="payment-container">
        <div className="error">Không tìm thấy thông tin đặt chỗ</div>
      </div>
    );
  }

  return (
    <div className="payment-container">
      <div className="payment-header">
        <h2>Thanh toán</h2>
        <button onClick={() => navigate('/')} className="back-btn">
          ← Quay lại trang chủ
        </button>
      </div>

      <div className="payment-content">
        {/* Thông tin đặt chỗ */}
        <div className="booking-summary">
          <h3>Tóm tắt đặt chỗ</h3>
          <div className="summary-card">
            <div className="summary-row">
              <span>Mã chuyến bay:</span>
              <strong>{booking.flightCode}</strong>
            </div>
            <div className="summary-row">
              <span>Tuyến bay:</span>
              <strong>{booking.route}</strong>
            </div>
            <div className="summary-row">
              <span>Hành khách:</span>
              <strong>{booking.passengerName}</strong>
            </div>
            <div className="summary-row">
              <span>Ghế ngồi:</span>
              <strong>{booking.seatNumber}</strong>
            </div>
            <div className="summary-row total">
              <span>Tổng tiền:</span>
              <strong className="price">
                {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(booking.price)}
              </strong>
            </div>
          </div>
        </div>

        {/* Chọn phương thức thanh toán */}
        <div className="payment-methods">
          <h3>Chọn phương thức thanh toán</h3>
          <div className="methods-grid">
            <label className={`method-card ${paymentMethod === 'momo' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="momo"
                checked={paymentMethod === 'momo'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              <div className="method-info">
                <img src="https://cdn.haitrieu.com/wp-content/uploads/2022/10/Logo-MoMo-Square.png" alt="Momo" />
                <span>Ví MoMo</span>
              </div>
            </label>

            <label className={`method-card ${paymentMethod === 'zalopay' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="zalopay"
                checked={paymentMethod === 'zalopay'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              <div className="method-info">
                <img src="https://cdn.jsdelivr.net/gh/vietqr-org/vietqr/logo/zalopay.png" alt="ZaloPay" />
                <span>ZaloPay</span>
              </div>
            </label>

            <label className={`method-card ${paymentMethod === 'card' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="card"
                checked={paymentMethod === 'card'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              <div className="method-info">
                <img src="https://cdn-icons-png.flaticon.com/512/6404/6404138.png" alt="Thẻ tín dụng" />
                <span>Thẻ tín dụng</span>
              </div>
            </label>

            <label className={`method-card ${paymentMethod === 'bank' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="bank"
                checked={paymentMethod === 'bank'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              <div className="method-info">
                <img src="https://cdn-icons-png.flaticon.com/512/2830/2830284.png" alt="Internet Banking" />
                <span>Internet Banking</span>
              </div>
            </label>
          </div>
        </div>

        {/* Form thông tin thanh toán */}
        {paymentMethod === 'card' && (
          <div className="card-form">
            <h3>Thông tin thẻ thanh toán</h3>
            <div className="form-group">
              <label>Số thẻ:</label>
              <input
                type="text"
                placeholder="1234 5678 9012 3456"
                value={cardInfo.cardNumber}
                onChange={(e) => setCardInfo({...cardInfo, cardNumber: e.target.value})}
                maxLength="19"
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>Ngày hết hạn:</label>
                <input
                  type="text"
                  placeholder="MM/YY"
                  value={cardInfo.expiryDate}
                  onChange={(e) => setCardInfo({...cardInfo, expiryDate: e.target.value})}
                  maxLength="5"
                />
              </div>
              <div className="form-group">
                <label>CVV:</label>
                <input
                  type="text"
                  placeholder="123"
                  value={cardInfo.cvv}
                  onChange={(e) => setCardInfo({...cardInfo, cvv: e.target.value})}
                  maxLength="4"
                />
              </div>
            </div>
            <div className="form-group">
              <label>Tên chủ thẻ:</label>
              <input
                type="text"
                placeholder="NGUYEN VAN A"
                value={cardInfo.cardHolder}
                onChange={(e) => setCardInfo({...cardInfo, cardHolder: e.target.value})}
              />
            </div>
          </div>
        )}

        {/* Nút thanh toán */}
        <div className="payment-actions">
          <button onClick={() => navigate('/')} className="cancel-btn">
            Hủy thanh toán
          </button>
          <button
            onClick={handlePayment}
            disabled={processing}
            className="pay-btn"
          >
            {processing ? 'Đang xử lý...' : `Thanh toán ${new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(booking.price)}`}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Payment;
