import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import PaymentForm from '../components/PaymentForm';
import './Booking.css';

const Payment = ({ user }) => {
  const { ticketId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [paymentError, setPaymentError] = useState(null);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        if (ticketId) {
          const response = await fetch(`http://localhost:8080/api/v1/tickets/${ticketId}`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
              'Content-Type': 'application/json'
            }
          });

          if (response.ok) {
            const data = await response.json();
            setTicket(data.data);
          } else {
            alert('Không tìm thấy thông tin vé');
            navigate('/');
            return;
          }
        } else if (location.state?.ticketData) {
          setTicket(location.state.ticketData);
        } else {
          alert('Không có thông tin vé để thanh toán');
          navigate('/');
          return;
        }
      } catch (error) {
        console.error('Lỗi lấy thông tin vé:', error);
        alert('Lỗi kết nối server');
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    const token = localStorage.getItem('accessToken');
    if (!token) {
      navigate('/login');
      return;
    }

    fetchTicket();
  }, [ticketId, location.state, navigate]);


  // Hàm xử lý thanh toán thành công
  const handlePaymentSuccess = (paymentIntentId) => {
    setPaymentSuccess(true);
    setPaymentError(null);

    // Hiển thị thông báo thành công và chuyển về trang chủ sau 3 giây
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  // Hàm xử lý lỗi thanh toán
  const handlePaymentError = (error) => {
    setPaymentError(error);
  };

  if (loading) {
    return (
      <div className="booking-container">
        <div className="loading">Đang tải thông tin vé...</div>
      </div>
    );
  }

  if (!ticket) {
    return (
      <div className="booking-container">
        <div className="error">Không tìm thấy thông tin vé</div>
      </div>
    );
  }

  if (paymentSuccess) {
    return (
      <div className="booking-container">
        <div className="booking-header">
          <h2>Thanh toán thành công!</h2>
          <button onClick={() => navigate('/')} className="back-btn">
            ← Về trang chủ
          </button>
        </div>

        <div className="booking-content">
          <div className="booking-confirmation">
            <div className="confirmation-card">
              <div className="confirmation-icon">✅</div>
              <div className="confirmation-message">
                <h3>Thanh toán thành công!</h3>
                <p>Vé của bạn đã được xác nhận và sẽ được gửi về email của bạn.</p>
                <p>Tự động chuyển về trang chủ trong 3 giây...</p>
              </div>

              <div className="booking-details">
                <div className="detail-item">
                  <span><strong>Mã vé:</strong></span>
                  <span>{ticket.ticketId?.slice(-8).toUpperCase()}</span>
                </div>
                <div className="detail-item">
                  <span><strong>Mã chuyến bay:</strong></span>
                  <span>{ticket.flightCode}</span>
                </div>
                <div className="detail-item">
                  <span><strong>Tổng tiền:</strong></span>
                  <span className="price">
                    {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(ticket.totalPrice)}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="booking-container">
      <div className="booking-header">
        <h2>Thanh toán vé máy bay</h2>
        <button onClick={() => navigate('/')} className="back-btn">
          ← Về trang chủ
        </button>
      </div>

      <div className="booking-content">
        {/* Thông tin vé cần thanh toán */}
        <div className="payment-ticket-info">
          <h3>Thông tin vé thanh toán</h3>
          <div className="ticket-summary-card">
            <div className="ticket-details">
              <div className="detail-item">
                <span><strong>Mã vé:</strong></span>
                <span>{ticket.ticketId?.slice(-8).toUpperCase()}</span>
              </div>
              <div className="detail-item">
                <span><strong>Mã chuyến bay:</strong></span>
                <span>{ticket.flightCode}</span>
              </div>
              <div className="detail-item">
                <span><strong>Tuyến bay:</strong></span>
                <span>{ticket.route}</span>
              </div>
              <div className="detail-item">
                <span><strong>Số lượng hành khách:</strong></span>
                <span>{ticket.passengerCount}</span>
              </div>
              <div className="detail-item">
                <span><strong>Hạng vé:</strong></span>
                <span className="capitalize">{ticket.class}</span>
              </div>
              <div className="detail-item">
                <span><strong>Tổng tiền:</strong></span>
                <span className="price">
                  {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(ticket.totalPrice)}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Thông tin hành khách */}
        <div className="passenger-details">
          <h3>Thông tin hành khách</h3>
          <div className="passenger-info">
            <p><strong>Họ tên:</strong> {user?.fullName || `${user?.firstName} ${user?.lastName}`}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>Số điện thoại:</strong> {user?.phoneNumber || 'Chưa cập nhật'}</p>
          </div>
        </div>

        {/* Form thanh toán */}
        <div className="payment-section">
          <h3>Thông tin thanh toán</h3>
          <div className="payment-container">
            <PaymentForm
              amount={ticket.totalPrice}
              currency="vnd"
              ticketId={ticket.ticketId}
              onPaymentSuccess={handlePaymentSuccess}
              onPaymentError={handlePaymentError}
            />
            {paymentError && (
              <div className="error-message" style={{ marginTop: '15px', textAlign: 'center' }}>
                {paymentError}
              </div>
            )}
          </div>
        </div>

        {/* Thông báo quan trọng */}
        <div className="payment-notice">
          <div className="warning-message">
            ⚠️ Vui lòng hoàn tất thanh toán trong thời gian còn lại để giữ chỗ. Vé sẽ bị hủy nếu không thanh toán kịp thời.
          </div>
        </div>
      </div>
    </div>
  );
};

export default Payment;
