import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import './Payment.css';

// Kh·ªüi t·∫°o Stripe v·ªõi publishable key t·ª´ backend
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || 'pk_test_your_publishable_key_here');

console.log('üîë Stripe Key:', process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY ? '***LOADED***' : 'NOT LOADED');
console.log('üîë Fallback Key:', process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY ? 'Using real key' : 'Using fallback key');

// Form x·ª≠ l√Ω thanh to√°n th·ª±c t·∫ø v·ªõi Stripe
const CheckoutForm = ({
  amount,
  currency,
  ticketId,
  onPaymentSuccess,
  onPaymentError
}) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [clientSecret, setClientSecret] = useState('');

  // T·∫°o Payment Intent khi component mount
  useEffect(() => {
    const createPaymentIntent = async () => {
      try {
        const token = localStorage.getItem('accessToken');
        const response = await fetch('http://localhost:8080/api/v1/payments/create-payment-intent', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({
            ticketId: ticketId,
            amount: amount,
            currency: currency
          }),
        });

        if (response.ok) {
          const data = await response.json();
          setClientSecret(data.data.clientSecret);
        } else {
          const errorData = await response.json();
          onPaymentError(errorData.message || 'Kh√¥ng th·ªÉ t·∫°o payment intent');
        }
      } catch (error) {
        console.error('L·ªói t·∫°o payment intent:', error);
        onPaymentError('L·ªói k·∫øt n·ªëi server');
      }
    };

    if (ticketId && amount) {
      createPaymentIntent();
    }
  }, [ticketId, amount, currency, onPaymentError]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements || !clientSecret) {
      onPaymentError('Stripe ch∆∞a ƒë∆∞·ª£c kh·ªüi t·∫°o');
      return;
    }

    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);

      // X√°c nh·∫≠n thanh to√°n v·ªõi Stripe
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
          billing_details: {
            name: event.target.name?.value || 'Kh√°ch h√†ng',
          },
        }
      });

      if (error) {
        console.error('L·ªói thanh to√°n:', error);
        onPaymentError(error.message || 'Thanh to√°n th·∫•t b·∫°i');
      } else if (paymentIntent.status === 'succeeded') {
        // G·ªçi API x√°c nh·∫≠n thanh to√°n v·ªõi backend
        const token = localStorage.getItem('accessToken');
        const confirmResponse = await fetch('http://localhost:8080/api/v1/payments/confirm-payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({
            paymentIntentId: paymentIntent.id
          }),
        });

        if (confirmResponse.ok) {
          onPaymentSuccess(paymentIntent.id);
        } else {
          const errorData = await confirmResponse.json();
          onPaymentError(errorData.message || 'Kh√¥ng th·ªÉ x√°c nh·∫≠n thanh to√°n');
        }
      } else {
        onPaymentError('Thanh to√°n kh√¥ng th√†nh c√¥ng');
      }
    } catch (error) {
      console.error('L·ªói x·ª≠ l√Ω thanh to√°n:', error);
      onPaymentError('L·ªói x·ª≠ l√Ω thanh to√°n');
    }

    setLoading(false);
  };

  const cardStyle = {
    style: {
      base: {
        color: '#32325d',
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: 'antialiased',
        fontSize: '16px',
        '::placeholder': {
          color: '#aab7c4'
        }
      },
      invalid: {
        color: '#fa755a',
        iconColor: '#fa755a'
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <div className="payment-amount">
        <h3>T·ªïng ti·ªÅn: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}</h3>
        <p className="currency-note">Thanh to√°n an to√†n v·ªõi Stripe</p>
      </div>

      <div className="form-group">
        <label htmlFor="card-name">T√™n ch·ªß th·∫ª</label>
        <input
          type="text"
          id="card-name"
          name="name"
          placeholder="NGUYEN VAN A"
          required
        />
      </div>

      <div className="form-group">
        <label>Th√¥ng tin th·∫ª</label>
        <div className="card-element-container">
          <CardElement options={cardStyle} />
        </div>
      </div>

      <button
        type="submit"
        disabled={!stripe || loading}
        className="payment-button"
      >
        {loading ? 'ƒêang x·ª≠ l√Ω...' : `Thanh to√°n ${new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount)}`}
      </button>

      <div className="payment-notice">
        <p>üîí Thanh to√°n ƒë∆∞·ª£c b·∫£o m·∫≠t b·ªüi Stripe</p>
        <small>Th√¥ng tin th·∫ª ƒë∆∞·ª£c m√£ h√≥a v√† b·∫£o v·ªá</small>
      </div>
    </form>
  );
};

// Wrapper component v·ªõi Stripe Elements
const PaymentForm = (props) => {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm {...props} />
    </Elements>
  );
};

export default PaymentForm;
