import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import PaymentForm from '../components/PaymentForm';
import './Booking.css';

const Booking = ({ user }) => {
  const { flightId } = useParams();
  const navigate = useNavigate();
  const [flight, setFlight] = useState(null);
  const [selectedClass, setSelectedClass] = useState('economy');
  const [loading, setLoading] = useState(true);
  const [booking, setBooking] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [ticketId, setTicketId] = useState(null);
  const [bookingCompleted, setBookingCompleted] = useState(false);

  // Lấy thông tin chuyến bay
  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/flights/${flightId}`);
        if (response.ok) {
          const data = await response.json();
          setFlight(data.data);
        } else {
          alert('Không tìm thấy thông tin chuyến bay');
          navigate('/');
        }
      } catch (error) {
        console.error('Lỗi lấy thông tin chuyến bay:', error);
        alert('Lỗi kết nối server');
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    if (flightId) {
      fetchFlight();
    } else {
      navigate('/');
    }
  }, [flightId, navigate]);

  // Hàm xử lý đặt vé (bước 1 - tạo vé trước)
  const handleBooking = async () => {
    if (!user) {
      alert('Vui lòng đăng nhập để đặt vé!');
      navigate('/login');
      return;
    }

    // ✅ Kiểm tra số ghế còn lại trước khi gửi yêu cầu
    if (passengerCount > flight.availableSeats) {
      alert(`Không đủ ghế! Chỉ còn ${flight.availableSeats} ghế, bạn đã chọn ${passengerCount} hành khách.`);
      return;
    }

    setBooking(true);

    const bookingData = {
      flightCode: flight.flightCode,
      seatNumber: `A${Math.floor(Math.random() * 50) + 1}`,
      price: getPriceByClass(flight, selectedClass) * passengerCount,
      passengerCount: passengerCount,
      passengerName: user.fullName || `${user.firstName} ${user.lastName}` || 'Khách hàng',
      email: user.email,
      phoneNumber: user.phoneNumber || 'Chưa cập nhật',
      userId: user._id || user.id
    };

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8080/api/v1/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(bookingData)
      });

      if (response.ok) {
        const result = await response.json();
        setTicketId(result.data._id);
        setBookingCompleted(true); // Đặt vé hoàn thành, chờ thanh toán
        setShowPayment(false); // Ẩn form thanh toán ban đầu
      } else {
        const error = await response.json();
        alert(`Lỗi đặt vé: ${error.message}`);
      }
    } catch (error) {
      console.error('Lỗi đặt vé:', error);
      alert('Không thể đặt vé. Vui lòng thử lại sau!');
    } finally {
      setBooking(false);
    }
  };

  // Hàm xử lý thanh toán thành công
  const handlePaymentSuccess = (paymentIntentId) => {
    alert(`Thanh toán thành công! Mã thanh toán: ${paymentIntentId}\nVé đã được xác nhận và sẽ được gửi về email của bạn.`);
    navigate('/');
  };

  // Hàm xử lý lỗi thanh toán
  const handlePaymentError = (error) => {
    setPaymentError(error);
  };

  // Hàm lấy giá theo hạng vé và số lượng hành khách
  const getPriceByClass = (flight, classType) => {
    let basePrice = 0;
    switch (classType) {
      case 'business':
        basePrice = flight.businessPrice || flight.economyPrice;
        break;
      case 'first':
        basePrice = flight.firstClassPrice || flight.economyPrice;
        break;
      default:
        basePrice = flight.economyPrice;
    }
    return basePrice;
  };

  // Hàm tính tổng tiền
  const getTotalPrice = () => {
    return getPriceByClass(flight, selectedClass) * passengerCount;
  };

  if (loading) {
    return (
      <div className="booking-container">
        <div className="loading">Đang tải thông tin chuyến bay...</div>
      </div>
    );
  }

  if (!flight) {
    return (
      <div className="booking-container">
        <div className="error">Không tìm thấy thông tin chuyến bay</div>
      </div>
    );
  }

  return (
    <div className="booking-container">
      <div className="booking-header">
        <h2>Đặt vé máy bay</h2>
        <button onClick={() => navigate('/')} className="back-btn">
          ← Quay lại tìm kiếm
        </button>
      </div>

      <div className="booking-content">
        {/* Thông tin chuyến bay */}
        <div className="flight-details">
          <h3>Thông tin chuyến bay</h3>
          <div className="flight-card">
            <div className="flight-info">
              <p><strong>Mã chuyến bay:</strong> {flight.flightCode}</p>
              <p><strong>Tuyến bay:</strong> {flight.route}</p>
              <p><strong>Khởi hành:</strong> {new Date(flight.departureTime).toLocaleString('vi-VN')}</p>
              <p><strong>Đến nơi:</strong> {new Date(flight.arrivalTime).toLocaleString('vi-VN')}</p>
              <p><strong>Số ghế còn:</strong> {flight.availableSeats}/{flight.totalSeats}</p>
            </div>
          </div>
        </div>

        {/* Thông tin hành khách */}
        <div className="passenger-details">
          <h3>Thông tin hành khách</h3>
          <div className="passenger-info">
            <p><strong>Họ tên:</strong> {user?.fullName}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>Số điện thoại:</strong> {user?.phoneNumber || 'Chưa cập nhật'}</p>
          </div>
        </div>

        {/* Chọn số lượng hành khách */}
        <div className="passenger-count">
          <h3>Số lượng hành khách</h3>
          <div className="passenger-selector">
            <label>Số lượng vé:</label>
            <select
              value={passengerCount}
              onChange={(e) => setPassengerCount(parseInt(e.target.value))}
              className="passenger-select"
            >
              {Array.from({ length: Math.min(flight.availableSeats, 20) }, (_, i) => i + 1).map(num => (
                <option key={num} value={num}>{num} hành khách</option>
              ))}
            </select>
            <span className="seats-remaining">
              (Còn {flight.availableSeats} ghế)
            </span>
          </div>
          <div className="price-breakdown">
            <div className="price-item">
              <span>Giá vé ({selectedClass}):</span>
              <span>{new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(getPriceByClass(flight, selectedClass))}</span>
            </div>
            <div className="price-item">
              <span>Số lượng:</span>
              <span>{passengerCount} vé</span>
            </div>
            <div className="price-item total">
              <span><strong>Tổng tiền:</strong></span>
              <span><strong>{new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(getTotalPrice())}</strong></span>
            </div>
          </div>
          {passengerCount > flight.availableSeats && (
            <div className="warning-message">
              ⚠️ Số lượng hành khách vượt quá số ghế còn lại của chuyến bay!
            </div>
          )}
        </div>

        {/* Chọn hạng vé */}
        <div className="class-selection">
          <h3>Chọn hạng vé</h3>
          <div className="class-options">
            <label className={`class-option ${selectedClass === 'economy' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="economy"
                checked={selectedClass === 'economy'}
                onChange={(e) => setSelectedClass(e.target.value)}
              />
              <div className="class-info">
                <span className="class-name">Economy</span>
                <span className="class-price">
                  {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(flight.economyPrice || 0)}
                </span>
              </div>
            </label>

            <label className={`class-option ${selectedClass === 'business' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="business"
                checked={selectedClass === 'business'}
                onChange={(e) => setSelectedClass(e.target.value)}
              />
              <div className="class-info">
                <span className="class-name">Business</span>
                <span className="class-price">
                  {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(flight.businessPrice || flight.economyPrice || 0)}
                </span>
              </div>
            </label>

            <label className={`class-option ${selectedClass === 'first' ? 'selected' : ''}`}>
              <input
                type="radio"
                value="first"
                checked={selectedClass === 'first'}
                onChange={(e) => setSelectedClass(e.target.value)}
              />
              <div className="class-info">
                <span className="class-name">First Class</span>
                <span className="class-price">
                  {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(flight.firstClassPrice || flight.economyPrice || 0)}
                </span>
              </div>
            </label>
          </div>
        </div>

        {/* Thanh toán */}
        {showPayment && ticketId && (
          <div className="payment-section">
            <h3>Thanh toán</h3>
            <div className="payment-container">
              <PaymentForm
                amount={getTotalPrice()}
                currency="usd"
                ticketId={ticketId}
                onPaymentSuccess={handlePaymentSuccess}
                onPaymentError={handlePaymentError}
              />
              {paymentError && (
                <div className="error-message" style={{ marginTop: '15px', textAlign: 'center' }}>
                  {paymentError}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Tổng tiền và hành động */}
        <div className="booking-summary">
          <div className="total-price">
            <h3>Tổng tiền:</h3>
            <span className="final-price">
              {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(getTotalPrice())}
            </span>
          </div>

          <div className="booking-actions">
            <button onClick={() => navigate('/')} className="cancel-btn">
              Hủy
            </button>
            {!showPayment ? (
              <button
                onClick={handleBooking}
                disabled={booking}
                className="confirm-btn"
              >
                {booking ? 'Đang xử lý...' : 'Đặt vé xong'}
              </button>
            ) : (
              <button
                onClick={() => setShowPayment(false)}
                className="back-payment-btn"
              >
                ← Quay lại chọn vé
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Booking;
