import './DatabaseTest.css';

const DatabaseTest = () => {
  const [user, setUser] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    checkConnection();
  }, []);

  const checkConnection = async () => {
    try {
      // Kiểm tra kết nối cơ sở dữ liệu
      console.log('=== KIỂM TRA KẾT NỐI CƠ SỞ DỮ LIỆU ===');

      // 1. Kiểm tra thông tin user hiện tại
      const token = localStorage.getItem('accessToken');
      console.log('Token hiện tại:', token);

      if (token) {
        const userResponse = await fetch('http://localhost:8080/api/v1/auth/get-profile', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          setUser(userData.data);
          console.log('Thông tin user:', userData.data);
        }
      }

      // 2. Kiểm tra danh sách vé
      if (token) {
        const ticketsResponse = await fetch(`http://localhost:8080/api/v1/tickets?userId=${user?._id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (ticketsResponse.ok) {
          const ticketsData = await ticketsResponse.json();
          console.log('Dữ liệu vé trả về:', ticketsData);
          setTickets(ticketsData.tickets || []);
        } else {
          console.error('Lỗi lấy vé:', ticketsResponse.status);
        }
      }

      // 3. Kiểm tra danh sách chuyến bay
      const flightsResponse = await fetch('http://localhost:8080/api/v1/flights?limit=5', {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (flightsResponse.ok) {
        const flightsData = await flightsResponse.json();
        console.log('Dữ liệu chuyến bay:', flightsData);
        setFlights(flightsData.data?.flights || []);
      }

    } catch (error) {
      console.error('Lỗi kết nối:', error);
    } finally {
      setLoading(false);
    }
  };

  const testCreateTicket = async () => {
    if (!user) {
      alert('Vui lòng đăng nhập trước');
      return;
    }

    try {
      const testTicketData = {
        flightCode: 'TEST001',
        seatNumber: 'A1',
        price: 100000,
        passengerCount: 1,
        passengerName: user.fullName || 'Test User',
        email: user.email,
        phoneNumber: user.phoneNumber || '0123456789',
        userId: user._id
      };

      console.log('Tạo vé test với dữ liệu:', testTicketData);

      const response = await fetch('http://localhost:8080/api/v1/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        },
        body: JSON.stringify(testTicketData)
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Vé test được tạo:', result);
        alert('Vé test đã được tạo thành công!');
        checkConnection(); // Refresh dữ liệu
      } else {
        const error = await response.json();
        console.error('Lỗi tạo vé test:', error);
        alert(`Lỗi tạo vé test: ${error.message}`);
      }
    } catch (error) {
      console.error('Lỗi tạo vé test:', error);
      alert('Không thể tạo vé test');
    }
  };

  if (loading) {
    return (
      <div className="debug-container">
        <div className="loading">Đang kiểm tra kết nối cơ sở dữ liệu...</div>
      </div>
    );
  }

  return (
    <div className="debug-container">
      <div className="debug-header">
        <h1>🗄️ Kiểm tra kết nối cơ sở dữ liệu</h1>
        <div className="debug-actions">
          <button onClick={() => navigate('/')} className="debug-btn back-btn">
            ← Về trang chủ
          </button>
          <button onClick={checkConnection} className="debug-btn refresh-btn">
            🔄 Kiểm tra lại
          </button>
          <button onClick={testCreateTicket} className="debug-btn test-btn">
            🧪 Tạo vé test
          </button>
        </div>
      </div>

      {/* Thông tin user hiện tại */}
      <div className="debug-section">
        <h3>👤 Thông tin người dùng hiện tại</h3>
        {user ? (
          <div className="user-info">
            <p><strong>ID:</strong> {user._id}</p>
            <p><strong>Tên:</strong> {user.fullName || `${user.firstName} ${user.lastName}`}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Token:</strong> {localStorage.getItem('accessToken') ? 'Có' : 'Không có'}</p>
          </div>
        ) : (
          <p>Chưa đăng nhập</p>
        )}
      </div>

      {/* Danh sách vé */}
      <div className="debug-section">
        <h3>🎫 Danh sách vé ({tickets.length} vé)</h3>
        {tickets.length === 0 ? (
          <div className="error-message">
            <p>Không có vé nào</p>
          </div>
        ) : (
          <div className="tickets-list">
            {tickets.map(ticket => (
              <div key={ticket._id} className="ticket-item">
                <h4>Mã vé: {ticket.ticketCode}</h4>
                <p><strong>Người đặt:</strong> {ticket.passengerName}</p>
                <p><strong>Email:</strong> {ticket.email}</p>
                <p><strong>Giá:</strong> {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(ticket.price)}</p>
                <p><strong>Trạng thái:</strong> {ticket.paymentStatus === 'paid' ? 'Đã thanh toán' : ticket.paymentStatus === 'refunded' ? 'Đã hoàn tiền' : 'Chờ thanh toán'}</p>
                <span className={`ticket-status ${ticket.paymentStatus === 'paid' ? 'status-paid' : ticket.paymentStatus === 'pending' ? 'status-pending' : 'status-cancelled'}`}>
                  {ticket.paymentStatus === 'paid' && '✓ Đã thanh toán'}
                  {ticket.paymentStatus === 'pending' && '⏳ Chờ thanh toán'}
                  {ticket.paymentStatus === 'refunded' && '↩ Đã hoàn tiền'}
                </span>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Danh sách chuyến bay */}
      <div className="debug-section">
        <h3>✈️ Danh sách chuyến bay ({flights.length} chuyến)</h3>
        {flights.length === 0 ? (
          <p>Không có dữ liệu chuyến bay</p>
        ) : (
          <div className="flights-list">
            {flights.slice(0, 3).map(flight => (
              <div key={flight._id} className="flight-item">
                <strong>{flight.flightCode}</strong> - {flight.route} - {flight.availableSeats} ghế
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DatabaseTest;
