import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardNumberElement,
  CardExpiryElement,
  CardCvcElement,
  PostalCodeElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import './Payment.css';

// Kh·ªüi t·∫°o Stripe v·ªõi publishable key t·ª´ backend
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || 'pk_test_your_publishable_key_here');

// Form x·ª≠ l√Ω thanh to√°n th·ª±c t·∫ø v·ªõi Stripe
const CheckoutForm = ({
  amount,
  currency,
  ticketId,
  onPaymentSuccess,
  onPaymentError
}) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [clientSecret, setClientSecret] = useState('');
  const [error, setError] = useState('');

  // T·∫°o Payment Intent khi component mount
  useEffect(() => {
    const createPaymentIntent = async () => {
      try {
        const token = localStorage.getItem('accessToken');
        if (!token) {
          setError('Kh√¥ng t√¨m th·∫•y token x√°c th·ª±c');
          return;
        }

        const response = await fetch('http://localhost:8080/api/v1/payments/create-payment-intent', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({
            ticketId: ticketId,
            amount: amount,
            currency: currency
          }),
        });

        if (response.ok) {
          const data = await response.json();
          if (data.data?.clientSecret) {
            setClientSecret(data.data.clientSecret);
            setError('');
          } else {
            setError('Kh√¥ng nh·∫≠n ƒë∆∞·ª£c client secret t·ª´ server');
          }
        } else {
          const errorData = await response.json();
          setError(errorData.message || 'Kh√¥ng th·ªÉ t·∫°o payment intent');
        }
      } catch (error) {
        console.error('L·ªói t·∫°o payment intent:', error);
        setError('L·ªói k·∫øt n·ªëi server');
      }
    };

    if (ticketId && amount && ticketId !== 'undefined' && amount > 0) {
      createPaymentIntent();
    } else {
      setError('Thi·∫øu th√¥ng tin v√© ho·∫∑c s·ªë ti·ªÅn kh√¥ng h·ª£p l·ªá');
    }
  }, [ticketId, amount, currency]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      setError('Stripe ch∆∞a ƒë∆∞·ª£c kh·ªüi t·∫°o');
      return;
    }

    if (!clientSecret) {
      setError('Ch∆∞a nh·∫≠n ƒë∆∞·ª£c client secret t·ª´ server');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const cardNumberElement = elements.getElement(CardNumberElement);
      const cardExpiryElement = elements.getElement(CardExpiryElement);
      const cardCvcElement = elements.getElement(CardCvcElement);
      const postalCodeElement = elements.getElement(PostalCodeElement);

      if (!cardNumberElement || !cardExpiryElement || !cardCvcElement) {
        setError('Kh√¥ng t√¨m th·∫•y form th·∫ª t√≠n d·ª•ng');
        setLoading(false);
        return;
      }

      // X√°c nh·∫≠n thanh to√°n v·ªõi Stripe
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: {
            number: cardNumberElement,
            exp_month: cardExpiryElement,
            exp_year: cardExpiryElement,
            cvc: cardCvcElement,
          },
          billing_details: {
            name: event.target.name?.value || 'Kh√°ch h√†ng',
            address: {
              postal_code: postalCodeElement ? event.target.postalCode?.value : undefined,
            },
          },
        }
      });

      if (error) {
        console.error('L·ªói thanh to√°n:', error);
        setError(error.message || 'Thanh to√°n th·∫•t b·∫°i');
      } else if (paymentIntent.status === 'succeeded') {
        // G·ªçi API x√°c nh·∫≠n thanh to√°n v·ªõi backend
        const token = localStorage.getItem('accessToken');
        const confirmResponse = await fetch('http://localhost:8080/api/v1/payments/confirm-payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({
            paymentIntentId: paymentIntent.id
          }),
        });

        if (confirmResponse.ok) {
          onPaymentSuccess(paymentIntent.id);
        } else {
          const errorData = await confirmResponse.json();
          setError(errorData.message || 'Kh√¥ng th·ªÉ x√°c nh·∫≠n thanh to√°n');
        }
      } else {
        setError('Thanh to√°n kh√¥ng th√†nh c√¥ng');
      }
    } catch (error) {
      console.error('L·ªói x·ª≠ l√Ω thanh to√°n:', error);
      setError('L·ªói x·ª≠ l√Ω thanh to√°n');
    }

    setLoading(false);
  };

  const cardStyle = {
    style: {
      base: {
        color: '#32325d',
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: 'antialiased',
        fontSize: '16px',
        '::placeholder': {
          color: '#aab7c4'
        }
      },
      invalid: {
        color: '#fa755a',
        iconColor: '#fa755a'
      }
    }
  };

  return (
